INSERT INTO `news` (`id`, `menu_id`, `title`, `caption`, `detail`, `title_eng`, `caption_eng`, `detail_eng`, `thumb_image_path`, `list_file_attach`, `create_user_id`, `create_date`, `feature_flag`, `update_date`, `is_public`, `del_flg`) VALUES
(6, 25, 'GIỚI THIỆU CÔNG TY KIỂM TOÁN VACO', 'Công ty TNHH Hãng Kiểm toán AASC (gọi tắt là AASC), đổi tên từ Công ty TNHH Dịch vụ Tư vấn Tài chính Kế toán và Kiểm toán (AASC) - Thành viên HLB Quốc tế tại Việt Nam – Mạng lưới Quốc tế các Hãng Kiểm toán và Tư vấn Quản trị chuyên nghiệp. Hãng Kiểm toán AASC là một trong hai tổ chức hợp pháp đầu tiên và lớn nhất của Việt Nam hoạt động trong lĩnh vực Kiểm toán, Kế toán, Tư vấn Tài chính, Tư vấn thuế và xác định giá trị doanh nghiệp. Hiện nay, AASC có trụ sở chính đặt tại Hà Nội, chi nhánh tại TP Hồ Chí Minh và chi nhánh tại Quảng Ninh.', '&lt;p&gt;\r\n	&lt;img alt=&quot;Chuyến bay MH370 của Malaysia Airlines chở 239 người mất tích khỏi radar từ ngày 8/3, sau khi cất cánh từ Kuala Lumpur. Nó được cho là đã rơi ở phía nam Ấn Độ Dương, dù giới chức chưa thu hồi được bất cứ mảnh vỡ nào. Ảnh:&amp;nbsp;Auckland Photo News&quot; src=&quot;http://m.f29.img.vnecdn.net/2014/03/28/boeing-777-9947-1395972381.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; line-height: 18.479999542236328px; text-align: justify;&quot;&gt;C&amp;ocirc;ng ty TNHH H&amp;atilde;ng Kiểm to&amp;aacute;n AASC (gọi tắt l&amp;agrave; AASC), đổi t&amp;ecirc;n từ C&amp;ocirc;ng ty TNHH Dịch vụ Tư vấn T&amp;agrave;i ch&amp;iacute;nh Kế to&amp;aacute;n v&amp;agrave; Kiểm to&amp;aacute;n (AASC) - Th&amp;agrave;nh vi&amp;ecirc;n HLB Quốc tế tại Việt Nam &amp;ndash; Mạng lưới Quốc tế c&amp;aacute;c H&amp;atilde;ng Kiểm to&amp;aacute;n v&amp;agrave; Tư vấn Quản trị chuy&amp;ecirc;n nghiệp. H&amp;atilde;ng Kiểm to&amp;aacute;n AASC l&amp;agrave; một trong hai tổ chức hợp ph&amp;aacute;p đầu ti&amp;ecirc;n v&amp;agrave; lớn nhất của Việt Nam hoạt động trong lĩnh vực Kiểm to&amp;aacute;n, Kế to&amp;aacute;n, Tư vấn T&amp;agrave;i ch&amp;iacute;nh, Tư vấn thuế v&amp;agrave; x&amp;aacute;c định gi&amp;aacute; trị doanh nghiệp. Hiện nay, AASC c&amp;oacute; trụ sở ch&amp;iacute;nh đặt tại H&amp;agrave; Nội, chi nh&amp;aacute;nh tại TP Hồ Ch&amp;iacute; Minh v&amp;agrave; chi nh&amp;aacute;nh tại Quảng Ninh.&lt;/span&gt;&lt;br style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; line-height: 18.479999542236328px; text-align: justify;&quot; /&gt;\r\n	&lt;br style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; line-height: 18.479999542236328px; text-align: justify;&quot; /&gt;\r\n	&lt;span style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; line-height: 18.479999542236328px; text-align: justify;&quot;&gt;Theo nhận x&amp;eacute;t của Trưởng ban Kinh tế T.Ư, Nguy&amp;ecirc;n Tổng Kiểm to&amp;aacute;n Nh&amp;agrave; nước, nguy&amp;ecirc;n Bộ trưởng Bộ T&amp;agrave;i ch&amp;iacute;nh Gi&amp;aacute;o sư - Tiến sỹ Vương Đ&amp;igrave;nh Huệ v&amp;agrave; xếp hạng của Bộ T&amp;agrave;i ch&amp;iacute;nh v&amp;agrave; Hội VACPA, AASC hiện nay được xếp hạng Nhất v&amp;agrave; l&amp;agrave; Nh&amp;agrave; cung cấp dịch vụ lớn Đầu đ&amp;agrave;n của Hệ thống c&amp;aacute;c C&amp;ocirc;ng ty Kiểm to&amp;aacute;n Việt Nam, với lượng kh&amp;aacute;ch h&amp;agrave;ng đa dạng v&amp;agrave; c&amp;oacute; dịch vụ kiểm to&amp;aacute;n B&amp;aacute;o c&amp;aacute;o quyết to&amp;aacute;n vốn đầu tư chất lượng nhất.&lt;/span&gt;&lt;br style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; line-height: 18.479999542236328px; text-align: justify;&quot; /&gt;\r\n	&lt;br style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; line-height: 18.479999542236328px; text-align: justify;&quot; /&gt;\r\n	&lt;span style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; line-height: 18.479999542236328px; text-align: justify;&quot;&gt;Thực hiện c&amp;aacute;c cam kết với Tổ chức thương mại thế giới (WTO), năm 2007, AASC đ&amp;atilde; chuyển đổi th&amp;agrave;nh c&amp;ocirc;ng từ Doanh nghiệp nh&amp;agrave; nước thuộc Bộ T&amp;agrave;i ch&amp;iacute;nh th&amp;agrave;nh C&amp;ocirc;ng ty TNHH c&amp;oacute; 2 th&amp;agrave;nh vi&amp;ecirc;n trở l&amp;ecirc;n v&amp;agrave; l&amp;agrave; một trong 5 Đơn vị kiểm to&amp;aacute;n hoạt động tại Việt Nam c&amp;oacute; doanh thu h&amp;agrave;ng năm, c&amp;oacute; hệ thống kh&amp;aacute;ch h&amp;agrave;ng v&amp;agrave; c&amp;oacute; số lượng Kiểm to&amp;aacute;n vi&amp;ecirc;n v&amp;agrave; nh&amp;acirc;n vi&amp;ecirc;n lớn nhất hiện nay (Gần 80 Kiểm to&amp;aacute;n vi&amp;ecirc;n Nh&amp;agrave; nước, 03 Kiểm to&amp;aacute;n vi&amp;ecirc;n c&amp;oacute; chứng chỉ ACCA của Vương quốc Anh, 01 Kiểm to&amp;aacute;n vi&amp;ecirc;n c&amp;oacute; Chứng chỉ CPA Mỹ, 01 chuy&amp;ecirc;n gia người Nhật Bản, 19 Thẩm định vi&amp;ecirc;n về gi&amp;aacute;, 54 Chứng chỉ Tư vấn Thủ tục về Thuế v&amp;agrave; gần 400 nh&amp;acirc;n vi&amp;ecirc;n). Trong suốt 23 năm hoạt động, d&amp;ugrave; dưới h&amp;igrave;nh thức DNNN hay c&amp;ocirc;ng ty TNHH, t&amp;ocirc;n chỉ hoạt động của AASC vẫn lu&amp;ocirc;n l&amp;agrave; cung cấp những dịch vụ c&amp;oacute; chất lượng cao nhất v&amp;igrave; lợi &amp;iacute;ch hợp ph&amp;aacute;p của kh&amp;aacute;ch h&amp;agrave;ng. Kh&amp;aacute;ch h&amp;agrave;ng của ch&amp;uacute;ng t&amp;ocirc;i rất đa dạng, bao gồm tất cả c&amp;aacute;c lĩnh vực kinh tế, th&amp;agrave;nh phần kinh tế: Tập đo&amp;agrave;n kinh tế, Tổng c&amp;ocirc;ng ty nh&amp;agrave; nước, C&amp;ocirc;ng ty c&amp;oacute; vốn đầu tư nước ngo&amp;agrave;i, C&amp;ocirc;ng ty ni&amp;ecirc;m yết, C&amp;ocirc;ng ty cổ phần, Ng&amp;acirc;n h&amp;agrave;ng thương mại, c&amp;aacute;c Dự &amp;aacute;n c&amp;oacute; sử dụng vốn vay, vốn viện trợ của Ng&amp;acirc;n h&amp;agrave;ng Thế giới (WB), Ng&amp;acirc;n h&amp;agrave;ng Ph&amp;aacute;t triển Ch&amp;acirc;u &amp;aacute; (ADB) cũng như c&amp;aacute;c tổ chức t&amp;iacute;n dụng quốc tế kh&amp;aacute;c v&amp;agrave; c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh đầu tư x&amp;acirc;y dựng cơ bản.&lt;/span&gt;&lt;/p&gt;\r\n', 'Infomation VACO', 'AASC Auditing Firm Company Limited (abbreviation: AASC), rebranded from Auditing and Accounting Financial Consultancy Services Limited Company (AASC) – A member of HLB International in Vietnam – A world-wide network of professional auditing firms and business management advisers. AASC Auditing Firm is one of the first two and largest Vietnamese legal organizations operating in the field of auditing, accounting, financial and tax consultancy and business evaluation. Currently, AASC has Head office based in Hanoi and two branches in Ho Chi Minh City and Quang Ninh.', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;img alt=&quot;Chuyến bay MH370 của Malaysia Airlines chở 239 người mất tích khỏi radar từ ngày 8/3, sau khi cất cánh từ Kuala Lumpur. Nó được cho là đã rơi ở phía nam Ấn Độ Dương, dù giới chức chưa thu hồi được bất cứ mảnh vỡ nào. Ảnh:&amp;nbsp;Auckland Photo News&quot; src=&quot;http://m.f29.img.vnecdn.net/2014/03/28/boeing-777-9947-1395972381.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	AASC Auditing Firm Company Limited (abbreviation: AASC), rebranded from Auditing and Accounting Financial Consultancy Services Limited Company (AASC) &amp;ndash; A member of HLB International in Vietnam &amp;ndash; A world-wide network of professional auditing firms and business management advisers. AASC Auditing Firm is one of the first two and largest Vietnamese legal organizations operating in the field of auditing, accounting, financial and tax consultancy and business evaluation. Currently, AASC has Head office based in Hanoi and two branches in Ho Chi Minh City and Quang Ninh.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Currently, AASC Auditing Firm is regarded as the first and the leading provider of professional services among Vietnam auditing firms with numerous clients operating in diversified sectors and deliveries the best quality services in construction audit as said by the Head of the Central Department of Economics, former Vietnam Audit General of the State Audit Office, former Minister of Ministry of Finance, Prof. Dr. Vuong Dinh Hue and according to the Vietnamese auditing firms ranking issued by the Ministry of Finance and VACPA.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	In fulfilling commitment with the World Trade Organization (WTO), in 2007, AASC has transformed successfully from a State-owned enterprise into a Limited company with more than two members and is one of five auditing companies operating in Vietnam which has the biggest annual revenue, systems of clients and number of employees. (Currently, AASC has nearly 80 Vietnamese Certified Public Accountants, 03 auditors qualified ACCA certification by the United Kingdom,&amp;nbsp;01 U.S. Certified Public Accountant, 01 Japanese expert, 18 appraisers, 54 members certified tax procedure consultants and approximate 400 officers). During 23 years of operation, whether, in SOE or Limited company model, AASC&amp;rsquo;s supreme principle is always to provide the first-class services for the legal interests of our clients. Our clients are diversified in all economic fields: Economic Group, State-owned Corporation, Foreign-invested companies, listed companies, joint stock companies, commercial banks, Projects funded by World Bank (WB), Asian Development Bank (ADB) as well as other International Credit Organizations and Construction projects.&lt;/p&gt;\r\n', NULL, NULL, 1, '2014-03-28 14:51:08', 1, '2014-03-28 15:01:10', 1, 0),
(7, 26, 'LIÊN ĐOÀN BÓNG ĐÁ VIỆT NAM TIẾP TỤC TÍN NHIỆM', 'Ngày 25/03/2014, tại Hà Nội, Chủ tịch HĐTV, Tổng Giám đốc Hãng Kiểm toán AASC Ông Ngô Đức Đoàn đã tham dự Đại hội Khóa VII Liên đoàn Bóng đá…', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Ng&amp;agrave;y 25/03/2014, tại H&amp;agrave; Nội, Chủ tịch HĐTV, Tổng Gi&amp;aacute;m đốc H&amp;atilde;ng Kiểm to&amp;aacute;n AASC &amp;Ocirc;ng Ng&amp;ocirc; Đức Đo&amp;agrave;n đ&amp;atilde; tham dự Đại hội Kh&amp;oacute;a VII Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Việt Nam (VFF) nhiệm kỳ 2014-2018 v&amp;agrave; c&amp;ocirc;ng bố kết quả kiểm to&amp;aacute;n b&amp;aacute;o c&amp;aacute;o t&amp;agrave;i ch&amp;iacute;nh c&amp;aacute;c năm 2009, 2010, 2011, 2012 v&amp;agrave; B&amp;aacute;o c&amp;aacute;o chương tr&amp;igrave;nh hỗ trợ t&amp;agrave;i ch&amp;iacute;nh của FIFA h&amp;agrave;ng năm nhiệm kỳ Kh&amp;oacute;a VI của VFF.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Trong c&amp;aacute;c năm nhiệm kỳ Kh&amp;oacute;a VI của VFF, với vị thế l&amp;agrave; H&amp;atilde;ng Kiểm to&amp;aacute;n đầu đ&amp;agrave;n trong Hệ thống c&amp;aacute;c C&amp;ocirc;ng ty Kiểm to&amp;aacute;n Việt Nam, AASC đ&amp;atilde; cung cấp dịch vụ kiểm to&amp;aacute;n b&amp;aacute;o c&amp;aacute;o t&amp;agrave;i ch&amp;iacute;nh với chất lượng vượt trội v&amp;agrave; tư vấn t&amp;iacute;ch cực, hiệu quả cho VFF trong c&amp;ocirc;ng t&amp;aacute;c quản l&amp;yacute; T&amp;agrave;i ch&amp;iacute;nh, Kế to&amp;aacute;n v&amp;agrave; Thuế ph&amp;ugrave; hợp với hoạt động thực tiễn của Việt Nam hiện nay v&amp;agrave; c&amp;aacute;c y&amp;ecirc;u cầu của Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Thế giới (FIFA). L&amp;atilde;nh đạo VFF cũng đ&amp;aacute;nh gi&amp;aacute; rất cao c&amp;aacute;c dịch vụ kiểm to&amp;aacute;n, tư vấn t&amp;agrave;i ch&amp;iacute;nh, kế to&amp;aacute;n, thuế của AASC v&amp;agrave; b&amp;agrave;y tỏ mong muốn tiếp tục mở rộng, tăng cường mối quan hệ hợp t&amp;aacute;c l&amp;acirc;u d&amp;agrave;i, chặt chẽ giữa hai b&amp;ecirc;n trong c&amp;aacute;c năm nhiệm kỳ Kh&amp;oacute;a VII (2014-2018) của Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Việt Nam. Đ&amp;aacute;p lại sự tin tưởng của L&amp;atilde;nh đạo Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Việt Nam, Tổng Gi&amp;aacute;m đốc AASC Ng&amp;ocirc; Đức Đo&amp;agrave;n cam kết với uy t&amp;iacute;n v&amp;agrave; danh tiếng của H&amp;atilde;ng Kiểm to&amp;aacute;n AASC, kinh nghiệm s&amp;acirc;u rộng c&amp;ugrave;ng bản lĩnh vững v&amp;agrave;ng của c&amp;aacute;c Kiểm to&amp;aacute;n vi&amp;ecirc;n, sự đồng thuận của to&amp;agrave;n thể c&amp;aacute;n bộ, nh&amp;acirc;n vi&amp;ecirc;n C&amp;ocirc;ng ty, AASC sẽ thực hiện v&amp;agrave; ho&amp;agrave;n th&amp;agrave;nh xuất sắc c&amp;aacute;c cuộc kiểm to&amp;aacute;n, cung cấp c&amp;aacute;c dịch vụ chuy&amp;ecirc;n ng&amp;agrave;nh với chất lượng cao nhất tu&amp;acirc;n thủ c&amp;aacute;c chuẩn mực kiểm to&amp;aacute;n v&amp;agrave; đ&amp;aacute;p ứng tốt nhất c&amp;aacute;c ti&amp;ecirc;u chuẩn khắt khe của Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Thế giới.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Đ&amp;uacute;ng 16 giờ 15 ph&amp;uacute;t c&amp;ugrave;ng ng&amp;agrave;y, Đại hội biểu quyết 100% Đại biểu dự Đại hội t&amp;iacute;n nhiệm lựa chọn AASC l&amp;agrave; đơn vị Kiểm to&amp;aacute;n c&amp;aacute;c năm nhiệm kỳ Kh&amp;oacute;a VII (2014 - 2018) của Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Việt Nam.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em&gt;H&amp;igrave;nh ảnh tại Đại hội&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em&gt;&lt;img alt=&quot;Dai hoi khoa 7 VFF&quot; height=&quot;280&quot; src=&quot;http://aasc.com.vn/web/images/tinnoibo/Thang_03.2014/Dai_hoi_khoa_7_VFF.jpg&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;500&quot; /&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em style=&quot;line-height: 1.3em;&quot;&gt;Chủ tịch VFF L&amp;ecirc; H&amp;ugrave;ng Dũng c&amp;ocirc;ng bố kết quả lựa chọn AASC l&amp;agrave; đơn vị Kiểm to&amp;aacute;n cho nhiệm kỳ 2014-2018 của VFF&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em style=&quot;line-height: 1.3em;&quot;&gt;&lt;img alt=&quot;Bieu quyet VFF&quot; height=&quot;314&quot; src=&quot;http://aasc.com.vn/web/images/tinnoibo/Thang_03.2014/Bieu_quyet_VFF.jpg&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;501&quot; /&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em&gt;&lt;span style=&quot;line-height: 1.3em;&quot;&gt;100% Đại biểu dự Đại hội biểu quyết t&amp;iacute;n nhiệm lựa chọn AASC l&amp;agrave; đơn vị Kiểm to&amp;aacute;n c&amp;aacute;c năm nhiệm kỳ kh&amp;oacute;a VII của Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Việt Nam&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: right;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;line-height: 1.3em;&quot;&gt;BBT&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n', 'LIÊN ĐOÀN BÓNG ĐÁ VIỆT NAM TIẾP TỤC TÍN NHIỆM', 'Ngày 25/03/2014, tại Hà Nội, Chủ tịch HĐTV, Tổng Giám đốc Hãng Kiểm toán AASC Ông Ngô Đức Đoàn đã tham dự Đại hội Khóa VII Liên đoàn Bóng đá…', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Ng&amp;agrave;y 25/03/2014, tại H&amp;agrave; Nội, Chủ tịch HĐTV, Tổng Gi&amp;aacute;m đốc H&amp;atilde;ng Kiểm to&amp;aacute;n AASC &amp;Ocirc;ng Ng&amp;ocirc; Đức Đo&amp;agrave;n đ&amp;atilde; tham dự Đại hội Kh&amp;oacute;a VII Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Việt Nam (VFF) nhiệm kỳ 2014-2018 v&amp;agrave; c&amp;ocirc;ng bố kết quả kiểm to&amp;aacute;n b&amp;aacute;o c&amp;aacute;o t&amp;agrave;i ch&amp;iacute;nh c&amp;aacute;c năm 2009, 2010, 2011, 2012 v&amp;agrave; B&amp;aacute;o c&amp;aacute;o chương tr&amp;igrave;nh hỗ trợ t&amp;agrave;i ch&amp;iacute;nh của FIFA h&amp;agrave;ng năm nhiệm kỳ Kh&amp;oacute;a VI của VFF.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Trong c&amp;aacute;c năm nhiệm kỳ Kh&amp;oacute;a VI của VFF, với vị thế l&amp;agrave; H&amp;atilde;ng Kiểm to&amp;aacute;n đầu đ&amp;agrave;n trong Hệ thống c&amp;aacute;c C&amp;ocirc;ng ty Kiểm to&amp;aacute;n Việt Nam, AASC đ&amp;atilde; cung cấp dịch vụ kiểm to&amp;aacute;n b&amp;aacute;o c&amp;aacute;o t&amp;agrave;i ch&amp;iacute;nh với chất lượng vượt trội v&amp;agrave; tư vấn t&amp;iacute;ch cực, hiệu quả cho VFF trong c&amp;ocirc;ng t&amp;aacute;c quản l&amp;yacute; T&amp;agrave;i ch&amp;iacute;nh, Kế to&amp;aacute;n v&amp;agrave; Thuế ph&amp;ugrave; hợp với hoạt động thực tiễn của Việt Nam hiện nay v&amp;agrave; c&amp;aacute;c y&amp;ecirc;u cầu của Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Thế giới (FIFA). L&amp;atilde;nh đạo VFF cũng đ&amp;aacute;nh gi&amp;aacute; rất cao c&amp;aacute;c dịch vụ kiểm to&amp;aacute;n, tư vấn t&amp;agrave;i ch&amp;iacute;nh, kế to&amp;aacute;n, thuế của AASC v&amp;agrave; b&amp;agrave;y tỏ mong muốn tiếp tục mở rộng, tăng cường mối quan hệ hợp t&amp;aacute;c l&amp;acirc;u d&amp;agrave;i, chặt chẽ giữa hai b&amp;ecirc;n trong c&amp;aacute;c năm nhiệm kỳ Kh&amp;oacute;a VII (2014-2018) của Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Việt Nam. Đ&amp;aacute;p lại sự tin tưởng của L&amp;atilde;nh đạo Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Việt Nam, Tổng Gi&amp;aacute;m đốc AASC Ng&amp;ocirc; Đức Đo&amp;agrave;n cam kết với uy t&amp;iacute;n v&amp;agrave; danh tiếng của H&amp;atilde;ng Kiểm to&amp;aacute;n AASC, kinh nghiệm s&amp;acirc;u rộng c&amp;ugrave;ng bản lĩnh vững v&amp;agrave;ng của c&amp;aacute;c Kiểm to&amp;aacute;n vi&amp;ecirc;n, sự đồng thuận của to&amp;agrave;n thể c&amp;aacute;n bộ, nh&amp;acirc;n vi&amp;ecirc;n C&amp;ocirc;ng ty, AASC sẽ thực hiện v&amp;agrave; ho&amp;agrave;n th&amp;agrave;nh xuất sắc c&amp;aacute;c cuộc kiểm to&amp;aacute;n, cung cấp c&amp;aacute;c dịch vụ chuy&amp;ecirc;n ng&amp;agrave;nh với chất lượng cao nhất tu&amp;acirc;n thủ c&amp;aacute;c chuẩn mực kiểm to&amp;aacute;n v&amp;agrave; đ&amp;aacute;p ứng tốt nhất c&amp;aacute;c ti&amp;ecirc;u chuẩn khắt khe của Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Thế giới.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Đ&amp;uacute;ng 16 giờ 15 ph&amp;uacute;t c&amp;ugrave;ng ng&amp;agrave;y, Đại hội biểu quyết 100% Đại biểu dự Đại hội t&amp;iacute;n nhiệm lựa chọn AASC l&amp;agrave; đơn vị Kiểm to&amp;aacute;n c&amp;aacute;c năm nhiệm kỳ Kh&amp;oacute;a VII (2014 - 2018) của Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Việt Nam.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em&gt;H&amp;igrave;nh ảnh tại Đại hội&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em&gt;&lt;img alt=&quot;Dai hoi khoa 7 VFF&quot; height=&quot;280&quot; src=&quot;http://aasc.com.vn/web/images/tinnoibo/Thang_03.2014/Dai_hoi_khoa_7_VFF.jpg&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;500&quot; /&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em style=&quot;line-height: 1.3em;&quot;&gt;Chủ tịch VFF L&amp;ecirc; H&amp;ugrave;ng Dũng c&amp;ocirc;ng bố kết quả lựa chọn AASC l&amp;agrave; đơn vị Kiểm to&amp;aacute;n cho nhiệm kỳ 2014-2018 của VFF&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em style=&quot;line-height: 1.3em;&quot;&gt;&lt;img alt=&quot;Bieu quyet VFF&quot; height=&quot;314&quot; src=&quot;http://aasc.com.vn/web/images/tinnoibo/Thang_03.2014/Bieu_quyet_VFF.jpg&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;501&quot; /&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em&gt;&lt;span style=&quot;line-height: 1.3em;&quot;&gt;100% Đại biểu dự Đại hội biểu quyết t&amp;iacute;n nhiệm lựa chọn AASC l&amp;agrave; đơn vị Kiểm to&amp;aacute;n c&amp;aacute;c năm nhiệm kỳ kh&amp;oacute;a VII của Li&amp;ecirc;n đo&amp;agrave;n B&amp;oacute;ng đ&amp;aacute; Việt Nam&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: right;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;line-height: 1.3em;&quot;&gt;BBT&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n', NULL, NULL, 1, '2014-03-28 15:41:58', 1, NULL, 0, 0),
(8, 26, 'Tháng 3, tháng của Phụ nữ, tháng của Thanh ni', 'Tháng 3, tháng của Phụ nữ, tháng của Thanh niên và đặc biệt là tháng nở rộ Hoa Báo cáo Kiểm toán - loài hoa nói hộ tình yêu của…', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;line-height: 1.3em;&quot;&gt;&lt;a href=&quot;http://aasc.com.vn/web/images/tinnoibo/Thang_03.2014/Anh_hoa_bao_cao_kiem_toan_1.png&quot; rel=&quot;lightbox-other&quot; style=&quot;color: rgb(242, 100, 34);&quot; title=&quot;Tháng 3 nở rộ hoa Báo cáo Kiểm toán&quot;&gt;&lt;img alt=&quot;Anh hoa bao cao kiem toan 1&quot; class=&quot;thumb thumb-plg&quot; src=&quot;http://aasc.com.vn/web/images/plg_jdvthumbs/thumb-comp514-Anh_hoa_bao_cao_kiem_toan_1-ab06ca3c4fc43ef876012a6d355dc6f6.png&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: none; padding: 2px; margin: 3px 7px 0px 0px; float: left;&quot; /&gt;&lt;/a&gt;&lt;/span&gt;Th&amp;aacute;ng 3, th&amp;aacute;ng của Phụ nữ, th&amp;aacute;ng của Thanh ni&amp;ecirc;n v&amp;agrave; đặc biệt l&amp;agrave; th&amp;aacute;ng nở rộ Hoa B&amp;aacute;o c&amp;aacute;o Kiểm to&amp;aacute;n - lo&amp;agrave;i hoa n&amp;oacute;i hộ t&amp;igrave;nh y&amp;ecirc;u của ch&amp;uacute;ng ta với AASC. Hướng tới kỷ niệm 1 năm ng&amp;agrave;y C&amp;ocirc;ng bố Thương hiệu C&amp;ocirc;ng ty TNHH H&amp;Atilde;NG KIỂM TO&amp;Aacute;N AASC (17/05/2013-17/05/2014), trong th&amp;aacute;ng 3, C&amp;aacute;n bộ, Kiểm to&amp;aacute;n vi&amp;ecirc;n v&amp;agrave; nh&amp;acirc;n vi&amp;ecirc;n to&amp;agrave;n C&amp;ocirc;ng ty đ&amp;atilde; s&amp;ocirc;i nổi, h&amp;acirc;n hoan thi đua lập th&amp;agrave;nh t&amp;iacute;ch ch&amp;agrave;o mừng ng&amp;agrave;y th&amp;agrave;nh lập Đo&amp;agrave;n TNCS Hồ Ch&amp;iacute; Minh v&amp;agrave; sự kiện ng&amp;agrave;y 17/05/2013 đ&amp;aacute;nh dấu mốc son ph&amp;aacute;t triển vẻ vang của C&amp;ocirc;ng ty Dịch vụ Tư vấn T&amp;agrave;i ch&amp;iacute;nh Kế to&amp;aacute;n v&amp;agrave; Kiểm to&amp;aacute;n trước đ&amp;acirc;y v&amp;agrave; l&amp;agrave; H&amp;Atilde;NG KIỂM TO&amp;Aacute;N AASC ng&amp;agrave;y nay. Theo số liệu của c&amp;aacute;c Ph&amp;ograve;ng, Ban, Chi nh&amp;aacute;nh C&amp;ocirc;ng ty, th&amp;aacute;ng 3 năm nay C&amp;ocirc;ng ty ph&amp;aacute;t h&amp;agrave;nh tr&amp;ecirc;n 1000 B&amp;ocirc;ng hoa B&amp;aacute;o c&amp;aacute;o Kiểm to&amp;aacute;n trong cả nước - Hương thơm của Hoa l&amp;agrave; t&amp;igrave;nh y&amp;ecirc;u của ch&amp;uacute;ng t&amp;ocirc;i với AASC; sắc m&amp;agrave;u của Hoa ch&amp;iacute;nh l&amp;agrave; sự thủy chung của nam nữ Thanh ni&amp;ecirc;n AASC với ng&amp;ocirc;i nh&amp;agrave; chung th&amp;acirc;n thương v&amp;agrave; qu&amp;yacute; trọng số 01 L&amp;ecirc; Phụng Hiểu - Quận ho&amp;agrave;n Kiếm - H&amp;agrave; Nội. Nhờ nơi đ&amp;acirc;y m&amp;agrave; c&amp;aacute;c bạn Thanh ni&amp;ecirc;n được tu dưỡng, r&amp;egrave;n luyện, thỏa sức tỏa s&amp;aacute;ng, cống hiến cho nghề nghiệp Kiểm to&amp;aacute;n v&amp;agrave; Tư vấn.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Nh&amp;acirc;n kỷ niệm 83 năm ng&amp;agrave;y th&amp;agrave;nh lập Đo&amp;agrave;n TNCS Hồ Ch&amp;iacute; Minh (26/03/1931-26/03/2014) v&amp;agrave; kỷ niệm 1 năm ng&amp;agrave;y C&amp;ocirc;ng bố Thương hiệu H&amp;Atilde;NG KIỂM TO&amp;Aacute;N AASC (17/05/2013-17/05/2014), Ch&amp;uacute;c Nam, nữ Thanh ni&amp;ecirc;n Kiểm to&amp;aacute;n vi&amp;ecirc;n, nh&amp;acirc;n vi&amp;ecirc;n AASC tr&amp;agrave;n đầy sức thanh xu&amp;acirc;n, lập c&amp;ocirc;ng, tỏa s&amp;aacute;ng rực rỡ, tất cả v&amp;igrave; C&amp;ocirc;ng ty TNHH H&amp;atilde;ng Kiểm to&amp;aacute;n AASC ph&amp;aacute;t triển vững mạnh, thịnh vượng.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;img alt=&quot;Anh hoa bao cao kiem toan 2&quot; height=&quot;452&quot; src=&quot;http://aasc.com.vn/web/images/tinnoibo/Thang_03.2014/Anh_hoa_bao_cao_kiem_toan_2.jpg&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;671&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: right;&quot;&gt;\r\n	&lt;em&gt;&lt;strong&gt;BBT Website&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n', 'Tháng 3, tháng của Phụ nữ, tháng của Thanh ni', 'Tháng 3, tháng của Phụ nữ, tháng của Thanh niên và đặc biệt là tháng nở rộ Hoa Báo cáo Kiểm toán - loài hoa nói hộ tình yêu của…', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;line-height: 1.3em;&quot;&gt;&lt;a href=&quot;http://aasc.com.vn/web/images/tinnoibo/Thang_03.2014/Anh_hoa_bao_cao_kiem_toan_1.png&quot; rel=&quot;lightbox-other&quot; style=&quot;color: rgb(242, 100, 34);&quot; title=&quot;Tháng 3 nở rộ hoa Báo cáo Kiểm toán&quot;&gt;&lt;img alt=&quot;Anh hoa bao cao kiem toan 1&quot; class=&quot;thumb thumb-plg&quot; src=&quot;http://aasc.com.vn/web/images/plg_jdvthumbs/thumb-comp514-Anh_hoa_bao_cao_kiem_toan_1-ab06ca3c4fc43ef876012a6d355dc6f6.png&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: none; padding: 2px; margin: 3px 7px 0px 0px; float: left;&quot; /&gt;&lt;/a&gt;&lt;/span&gt;Th&amp;aacute;ng 3, th&amp;aacute;ng của Phụ nữ, th&amp;aacute;ng của Thanh ni&amp;ecirc;n v&amp;agrave; đặc biệt l&amp;agrave; th&amp;aacute;ng nở rộ Hoa B&amp;aacute;o c&amp;aacute;o Kiểm to&amp;aacute;n - lo&amp;agrave;i hoa n&amp;oacute;i hộ t&amp;igrave;nh y&amp;ecirc;u của ch&amp;uacute;ng ta với AASC. Hướng tới kỷ niệm 1 năm ng&amp;agrave;y C&amp;ocirc;ng bố Thương hiệu C&amp;ocirc;ng ty TNHH H&amp;Atilde;NG KIỂM TO&amp;Aacute;N AASC (17/05/2013-17/05/2014), trong th&amp;aacute;ng 3, C&amp;aacute;n bộ, Kiểm to&amp;aacute;n vi&amp;ecirc;n v&amp;agrave; nh&amp;acirc;n vi&amp;ecirc;n to&amp;agrave;n C&amp;ocirc;ng ty đ&amp;atilde; s&amp;ocirc;i nổi, h&amp;acirc;n hoan thi đua lập th&amp;agrave;nh t&amp;iacute;ch ch&amp;agrave;o mừng ng&amp;agrave;y th&amp;agrave;nh lập Đo&amp;agrave;n TNCS Hồ Ch&amp;iacute; Minh v&amp;agrave; sự kiện ng&amp;agrave;y 17/05/2013 đ&amp;aacute;nh dấu mốc son ph&amp;aacute;t triển vẻ vang của C&amp;ocirc;ng ty Dịch vụ Tư vấn T&amp;agrave;i ch&amp;iacute;nh Kế to&amp;aacute;n v&amp;agrave; Kiểm to&amp;aacute;n trước đ&amp;acirc;y v&amp;agrave; l&amp;agrave; H&amp;Atilde;NG KIỂM TO&amp;Aacute;N AASC ng&amp;agrave;y nay. Theo số liệu của c&amp;aacute;c Ph&amp;ograve;ng, Ban, Chi nh&amp;aacute;nh C&amp;ocirc;ng ty, th&amp;aacute;ng 3 năm nay C&amp;ocirc;ng ty ph&amp;aacute;t h&amp;agrave;nh tr&amp;ecirc;n 1000 B&amp;ocirc;ng hoa B&amp;aacute;o c&amp;aacute;o Kiểm to&amp;aacute;n trong cả nước - Hương thơm của Hoa l&amp;agrave; t&amp;igrave;nh y&amp;ecirc;u của ch&amp;uacute;ng t&amp;ocirc;i với AASC; sắc m&amp;agrave;u của Hoa ch&amp;iacute;nh l&amp;agrave; sự thủy chung của nam nữ Thanh ni&amp;ecirc;n AASC với ng&amp;ocirc;i nh&amp;agrave; chung th&amp;acirc;n thương v&amp;agrave; qu&amp;yacute; trọng số 01 L&amp;ecirc; Phụng Hiểu - Quận ho&amp;agrave;n Kiếm - H&amp;agrave; Nội. Nhờ nơi đ&amp;acirc;y m&amp;agrave; c&amp;aacute;c bạn Thanh ni&amp;ecirc;n được tu dưỡng, r&amp;egrave;n luyện, thỏa sức tỏa s&amp;aacute;ng, cống hiến cho nghề nghiệp Kiểm to&amp;aacute;n v&amp;agrave; Tư vấn.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Nh&amp;acirc;n kỷ niệm 83 năm ng&amp;agrave;y th&amp;agrave;nh lập Đo&amp;agrave;n TNCS Hồ Ch&amp;iacute; Minh (26/03/1931-26/03/2014) v&amp;agrave; kỷ niệm 1 năm ng&amp;agrave;y C&amp;ocirc;ng bố Thương hiệu H&amp;Atilde;NG KIỂM TO&amp;Aacute;N AASC (17/05/2013-17/05/2014), Ch&amp;uacute;c Nam, nữ Thanh ni&amp;ecirc;n Kiểm to&amp;aacute;n vi&amp;ecirc;n, nh&amp;acirc;n vi&amp;ecirc;n AASC tr&amp;agrave;n đầy sức thanh xu&amp;acirc;n, lập c&amp;ocirc;ng, tỏa s&amp;aacute;ng rực rỡ, tất cả v&amp;igrave; C&amp;ocirc;ng ty TNHH H&amp;atilde;ng Kiểm to&amp;aacute;n AASC ph&amp;aacute;t triển vững mạnh, thịnh vượng.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;img alt=&quot;Anh hoa bao cao kiem toan 2&quot; height=&quot;452&quot; src=&quot;http://aasc.com.vn/web/images/tinnoibo/Thang_03.2014/Anh_hoa_bao_cao_kiem_toan_2.jpg&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;671&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: right;&quot;&gt;\r\n	&lt;em&gt;&lt;strong&gt;BBT Website&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n', NULL, NULL, 1, '2014-03-28 15:43:41', 1, NULL, 1, 0),
(9, 26, 'HAPPY INTERNATIONAL WOMEN''S DAY', '............', '&lt;p&gt;\r\n	&lt;img alt=&quot;Thiep 8-3 gui CBNV&quot; src=&quot;http://aasc.com.vn/web/images/ecard/Thiep%208-3%20gui%20CBNV.jpg&quot; /&gt;&lt;/p&gt;\r\n', 'HAPPY INTERNATIONAL WOMEN''S DAY', '.........', '&lt;p&gt;\r\n	&lt;img alt=&quot;Thiep 8-3 gui CBNV&quot; height=&quot;1263&quot; src=&quot;http://aasc.com.vn/web/images/ecard/Thiep%208-3%20gui%20CBNV.jpg&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 1px solid rgb(255, 102, 51); color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot; width=&quot;2191&quot; /&gt;&lt;/p&gt;\r\n', NULL, NULL, 1, '2014-03-28 15:44:48', 1, NULL, 1, 0),
(10, 27, 'BỘ TÀI CHÍNH QUY ĐỊNH VỀ THẨM TRA QUYẾT TOÁN ', 'Ngày 02 tháng 01 năm 2014, Bộ Tài chính đã ban hành Thông tư số 04/2014/TT-BTC quy định quy trình thẩm tra quyết toán dự án hoàn thành đối với các dự án sử dụng vốn ngân sách Nhà nước.\r\n ', '&lt;div class=&quot;itemBody&quot; style=&quot;padding: 8px 0px 4px; margin: 0px 0px 12px; text-align: justify; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px;&quot;&gt;\r\n	&lt;div class=&quot;itemFullText&quot;&gt;\r\n		&lt;div class=&quot;dm-list&quot; style=&quot;margin: 0px 0px 15px; padding: 0px; font-family: Arial; font-size: 12px; width: 623px; float: left; color: rgb(42, 43, 43); line-height: normal;&quot;&gt;\r\n			&lt;div id=&quot;ctl00_SPWebPartManager1_g_275cd07f_7dd8_4f62_97b3_19577a1ec443_ctl00_pnHide&quot; style=&quot;margin: 0px; padding: 0px;&quot;&gt;\r\n				&lt;div&gt;\r\n					&lt;strong style=&quot;color: rgb(59, 59, 59);&quot;&gt;Ng&amp;agrave;y 02 th&amp;aacute;ng 01 năm 2014, Bộ T&amp;agrave;i ch&amp;iacute;nh đ&amp;atilde; ban h&amp;agrave;nh Th&amp;ocirc;ng tư số 04/2014/TT-BTC quy định quy tr&amp;igrave;nh thẩm tra quyết to&amp;aacute;n dự &amp;aacute;n ho&amp;agrave;n th&amp;agrave;nh đối với c&amp;aacute;c dự &amp;aacute;n sử dụng vốn ng&amp;acirc;n s&amp;aacute;ch Nh&amp;agrave; nước.&lt;/strong&gt;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&lt;strong style=&quot;color: rgb(59, 59, 59);&quot;&gt;&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&lt;strong style=&quot;color: rgb(59, 59, 59);&quot;&gt;&lt;img alt=&quot;m66c9738c-38f6-47ff-a5f3-042167cc797e.jpeg&quot; class=&quot;thumb&quot; height=&quot;84&quot; src=&quot;http://aasc.com.vn/web/images/tintuc/m66c9738c-38f6-47ff-a5f3-042167cc797e.jpeg.png&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px none; color: rgb(42, 43, 43); margin: 0px; padding: 0px;&quot; width=&quot;135&quot; /&gt;&lt;/strong&gt;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&amp;nbsp;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&lt;em&gt;Ảnh minh họa&lt;/em&gt;&lt;/div&gt;\r\n			&lt;/div&gt;\r\n			&lt;div class=&quot;list-chitiet&quot; style=&quot;margin: 0px; padding: 0px; width: 623px; color: rgb(59, 59, 59); text-align: right;&quot;&gt;\r\n				&lt;p align=&quot;justify&quot; style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n					Cụ thể, Th&amp;ocirc;ng tư số 04/2014/TT-BTC quy định về quy tr&amp;igrave;nh thẩm tra quyết to&amp;aacute;n với c&amp;aacute;c nội dung như: Xem x&amp;eacute;t t&amp;iacute;nh ph&amp;aacute;p l&amp;yacute; của hợp đồng kiểm to&amp;aacute;n; đối chiếu nội dung b&amp;aacute;o c&amp;aacute;o kết quả kiểm to&amp;aacute;n của dự &amp;aacute;n đối với nội dung kiểm tra b&amp;aacute;o c&amp;aacute;o quyết to&amp;aacute;n vốn đầu tư ho&amp;agrave;n th&amp;agrave;nh quy định tại Chuẩn mực Kiểm to&amp;aacute;n b&amp;aacute;o c&amp;aacute;o quyết to&amp;aacute;n vốn đầu tư ho&amp;agrave;n th&amp;agrave;nh số 1000; xem x&amp;eacute;t những &amp;yacute; kiến m&amp;agrave; chủ đầu tư kh&amp;ocirc;ng thống nhất với b&amp;aacute;o c&amp;aacute;o kiểm to&amp;aacute;n của nh&amp;agrave; thầu kiểm to&amp;aacute;n; kiểm tra việc chấp h&amp;agrave;nh c&amp;aacute;c văn bản quy phạm ph&amp;aacute;p luật, việc chấp h&amp;agrave;nh kết luận của c&amp;aacute;c cơ quan thanh tra, kiểm tra v&amp;agrave; nhận x&amp;eacute;t, kiến nghị về qu&amp;aacute; tr&amp;igrave;nh quản l&amp;yacute; đầu tư, thực hiện dự &amp;aacute;n.&lt;/p&gt;\r\n				&lt;p align=&quot;justify&quot; style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n					Ri&amp;ecirc;ng đối với những dự &amp;aacute;n kh&amp;ocirc;ng thực hiện kiểm to&amp;aacute;n b&amp;aacute;o c&amp;aacute;o quyết to&amp;aacute;n, c&amp;aacute;n bộ thẩm tra c&amp;oacute; tr&amp;aacute;ch nhiệm thẩm tra hồ sơ ph&amp;aacute;p l&amp;yacute;, nguồn vốn đầu tư, chi ph&amp;iacute; đầu tư của dự &amp;aacute;n; thẩm tra chi ph&amp;iacute; đầu tư kh&amp;ocirc;ng t&amp;iacute;nh v&amp;agrave;o gi&amp;aacute; trị t&amp;agrave;i sản (nếu c&amp;oacute;); thẩm tra x&amp;aacute;c định gi&amp;aacute; trị t&amp;agrave;i sản; c&amp;ocirc;ng nợ, vật tư thiết bị tồn đọng, v. v... Trường hợp cần thiết, thủ trưởng cơ quan chủ tr&amp;igrave; thẩm tra quyết to&amp;aacute;n quyết định việc kiểm tra thực tế tại Ban quản l&amp;yacute; dự &amp;aacute;n v&amp;agrave; hiện trường x&amp;acirc;y dựng c&amp;ocirc;ng tr&amp;igrave;nh trong qu&amp;aacute; tr&amp;igrave;nh thẩm tra b&amp;aacute;o c&amp;aacute;o quyết to&amp;aacute;n dự &amp;aacute;n ho&amp;agrave;n th&amp;agrave;nh.&lt;/p&gt;\r\n				&lt;p align=&quot;justify&quot; style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n					Sau khi thẩm tra quyết to&amp;aacute;n, cơ quan thẩm tra quyết to&amp;aacute;n lập hồ sơ tr&amp;igrave;nh duyệt quyết to&amp;aacute;n dự &amp;aacute;n ho&amp;agrave;n th&amp;agrave;nh để tr&amp;igrave;nh người c&amp;oacute; thẩm quyền ph&amp;ecirc; duyệt. Hồ sơ tr&amp;igrave;nh ph&amp;ecirc; duyệt quyết to&amp;aacute;n gồm c&amp;oacute;: B&amp;aacute;o c&amp;aacute;o kết quả thẩm tra quyết to&amp;aacute;n, dự thảo quyết định ph&amp;ecirc; duyệt quyết to&amp;aacute;n dự &amp;aacute;n ho&amp;agrave;n th&amp;agrave;nh v&amp;agrave; c&amp;aacute;c t&amp;agrave;i liệu do chủ đầu tư tr&amp;igrave;nh (k&amp;egrave;m theo).&lt;/p&gt;\r\n				&lt;p align=&quot;justify&quot; style=&quot;margin: 10px 0px; padding: 0px;&quot;&gt;\r\n					Th&amp;ocirc;ng tư n&amp;agrave;y c&amp;oacute; hiệu lực thi h&amp;agrave;nh kể từ ng&amp;agrave;y 16 th&amp;aacute;ng 02 năm 2014 v&amp;agrave; thay thế Quyết định số 56/2008/QĐ-BTC ng&amp;agrave;y 17/7/2008 của Bộ trưởng Bộ T&amp;agrave;i ch&amp;iacute;nh về việc ban h&amp;agrave;nh Quy tr&amp;igrave;nh thẩm tra quyết to&amp;aacute;n dự &amp;aacute;n ho&amp;agrave;n th&amp;agrave;nh đối với c&amp;aacute;c dự &amp;aacute;n sử dụng vốn ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước.&lt;/p&gt;\r\n				Nguồn:&amp;nbsp;Cổng Th&amp;ocirc;ng tin điện tử Bộ T&amp;agrave;i ch&amp;iacute;nh&amp;nbsp;&lt;/div&gt;\r\n		&lt;/div&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;clr&quot; style=&quot;clear: both; height: 0px; line-height: 0; float: none; padding: 0px; margin: 0px; border: none;&quot;&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;itemSocialSharing&quot; style=&quot;padding: 15px 0px 0px; border-top-width: 6px; border-top-style: solid; border-top-color: rgb(246, 246, 246); margin-bottom: 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n', 'MINISTRY OF FINANCE GUIDES PROJECT COMPLETION', 'Circular No. 04/2014/TT-BTC dated 02 January 2014 of the Ministry of Finance provides a verification process for project completion finalization of projects using capital from the state budget as follows:', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; line-height: 15.333333015441895px; font-family: Arial, sans-serif;&quot;&gt;Circular No. 04/2014/TT-BTC dated 02 January 2014 of the Ministry of Finance provides a verification process for project completion finalization of projects using capital from the state budget as follows:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; line-height: 15.333333015441895px; font-family: Arial, sans-serif;&quot;&gt;&lt;img alt=&quot;BTC&quot; height=&quot;84&quot; src=&quot;http://aasc.com.vn/web/images/tintuc/BTC.jpeg&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;135&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; line-height: 15.333333015441895px; font-family: Arial, sans-serif;&quot;&gt;Verification of the legality of auditing contract; Comparison of the work contents of report on results of audit of the settlement with the report on settlement of investment capital pursuant to Vietnamese Standards on Auditing No. 1000; Examination of the adverse opinion in audit reports between employer entity and audit firm; Examination of the compliance with the provisions of law on construction investment, inspection conclusions, examination written record, comments, proposals on investment management and project implementation.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 6pt; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; line-height: 15.333333015441895px; font-family: Arial, sans-serif;&quot;&gt;For projects with settlements not yet audited, examiner should verify legal dossiers, projects investment capital sources, investment expenses, lost investment expenses not included in the asset value (if any); the value of assets formed through investment; outstanding debts, unused supplies and equipment&amp;hellip; The leader could decide to examine settlements in the project management unit and construction field in the process of verifying settlement reports of completed projects, if necessary.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 6pt; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; line-height: 15.333333015441895px; font-family: Arial, sans-serif;&quot;&gt;After the verification, dossiers submitted for settlement approval should be prepared and sent to authorities including verification reports on settlement, draft decision on settlement, and documents supplied by investors.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 6pt; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; line-height: 15.333333015441895px; font-family: Arial, sans-serif;&quot;&gt;This Circular takes effective from February 16, 2014 and replaces Decision No. 56/2008/QĐ-BTC dated 17/7/2008 issued by Ministry of Finance promulgating on project completion finalization of projects using capital from the State budget&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: right;&quot;&gt;\r\n	From Ministry of Finance&lt;/p&gt;\r\n', NULL, NULL, 1, '2014-03-28 15:46:44', 1, NULL, 1, 0);
INSERT INTO `news` (`id`, `menu_id`, `title`, `caption`, `detail`, `title_eng`, `caption_eng`, `detail_eng`, `thumb_image_path`, `list_file_attach`, `create_user_id`, `create_date`, `feature_flag`, `update_date`, `is_public`, `del_flg`) VALUES
(11, 27, 'NĂM 2014: TĂNG TỐC KIỂM TOÁN HOẠT ĐỘNG TÁI CƠ', 'Tái cơ cấu nền kinh tế được khẳng định trong Nghị quyết Hội nghị lần thứ ba Ban Chấp hành Trung ương Đảng khóa XI, sau hơn 3 năm thực hiện đã đạt được những kết quả tích cực. Tuy nhiên, tại hội thảo “Tổ chức kiểm toán quá trình tái cơ cấu nền kinh tế” diễn ra vừa qua tại Hà Nội, TS. Nguyễn Hữu Vạn-Tổng Kiểm toán Nhà nước cho biết, năm 2014, kiểm toán hoạt động tái cơ cấu nền kinh tế sẽ được Kiểm toán nhà nước (KTNN) triển khai mạnh mẽ hơn.', '&lt;div style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;T&amp;aacute;i cơ cấu nền kinh tế được khẳng định trong Nghị quyết Hội nghị lần thứ ba Ban Chấp h&amp;agrave;nh Trung ương Đảng kh&amp;oacute;a XI, sau hơn 3 năm thực hiện đ&amp;atilde; đạt được những kết quả t&amp;iacute;ch cực. Tuy nhi&amp;ecirc;n, tại hội thảo &amp;ldquo;Tổ chức kiểm to&amp;aacute;n qu&amp;aacute; tr&amp;igrave;nh t&amp;aacute;i cơ cấu nền kinh tế&amp;rdquo; diễn ra vừa qua tại H&amp;agrave; Nội, TS. Nguyễn Hữu Vạn-Tổng Kiểm to&amp;aacute;n Nh&amp;agrave; nước cho biết, năm 2014, kiểm to&amp;aacute;n hoạt động t&amp;aacute;i cơ cấu nền kinh tế sẽ được Kiểm to&amp;aacute;n nh&amp;agrave; nước (KTNN) triển khai mạnh mẽ hơn.&lt;/strong&gt;&lt;/div&gt;\r\n&lt;div style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n&lt;div style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;img alt=&quot;1593039&quot; height=&quot;250&quot; src=&quot;http://aasc.com.vn/web/images/tintuc/1593039.PNG&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;376&quot; /&gt;&lt;br /&gt;\r\n	Ảnh minh họa. Nguồn Internet&lt;br /&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Quan t&amp;acirc;m t&amp;aacute;i cơ cấu khu vực DNNN&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Việc t&amp;aacute;i cơ cấu n&amp;ecirc;n kinh tế&amp;nbsp;được Ch&amp;iacute;nh phủ chỉ&amp;nbsp; đạo tập trung v&amp;agrave;o ba lĩnh vực (đầu tư c&amp;ocirc;ng; khu vực t&amp;agrave;i ch&amp;iacute;nh, tiền tệ, ng&amp;acirc;n h&amp;agrave;ng; khu vực doanh nghiệp Nh&amp;agrave; nước, trọng t&amp;acirc;m l&amp;agrave; tập đo&amp;agrave;n kinh tế, c&amp;aacute;c Tổng c&amp;ocirc;ng ty Nh&amp;agrave; nước).&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		TS. Nguyễn Hữu Vạn-Tổng Kiểm to&amp;aacute;n Nh&amp;agrave; nước&amp;nbsp; chia sẻ, t&amp;aacute;i cơ cấu nền kinh tế đạt mục ti&amp;ecirc;u chuyển đổi m&amp;ocirc; h&amp;igrave;nh tăng trưởng (ổn định kinh tế vĩ m&amp;ocirc;, chỉ số gi&amp;aacute; ti&amp;ecirc;u d&amp;ugrave;ng giảm, c&amp;aacute;c yếu tố nền tảng của kinh tế vĩ m&amp;ocirc; dần được tăng cường, c&amp;ugrave;ng cố), tuy nhi&amp;ecirc;n c&amp;ograve;n nhiều hạn chế, nhất l&amp;agrave; việc t&amp;aacute;i cơ cấu hoạt động doanh nghiệp Nh&amp;agrave; nước (DNNN).&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Chuy&amp;ecirc;n gia Phạm Đức Trung-Viện Nghi&amp;ecirc;n cứu quản l&amp;yacute; kinh tế Trung ương cho biết, kết quả thực hiện t&amp;aacute;i cơ cấu DNNN c&amp;ograve;n hạn chế. C&amp;ocirc;ng t&amp;aacute;c x&amp;acirc;y dựng ho&amp;agrave;n thiện khung khổ ph&amp;aacute;p luật chưa kịp thời. Phần lớn văn bản ph&amp;aacute;p l&amp;yacute; c&amp;oacute; li&amp;ecirc;n quan trực tiếp đến t&amp;aacute;i cấu tr&amp;uacute;c DNNN vẫn đang trong giai đoạn chuẩn bị, đặc biệt về cổ phần h&amp;oacute;a, đa dạng h&amp;oacute;a sở hữu. B&amp;ecirc;n cạnh đ&amp;oacute;, nhiều Tổng c&amp;ocirc;ng ty Nh&amp;agrave; nước c&amp;ograve;n l&amp;uacute;ng t&amp;uacute;ng trong x&amp;acirc;y dựng nội dung, lộ tr&amp;igrave;nh t&amp;aacute;i cơ cấu, nhất l&amp;agrave; khi vướng phải c&amp;aacute;c vấn đề xử l&amp;yacute; t&amp;agrave;i ch&amp;iacute;nh, tho&amp;aacute;i vốn&amp;hellip;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		&amp;Ocirc;ng L&amp;ecirc; Ho&amp;agrave;ng Hải - Ph&amp;oacute; Cục trưởng Cục T&amp;agrave;i ch&amp;iacute;nh doanh nghiệp (Bộ T&amp;agrave;i ch&amp;iacute;nh) cũng nhận x&amp;eacute;t, việc xử l&amp;yacute; t&amp;aacute;i cơ cấu khu vực DNNN diễn ra cũng rất kh&amp;oacute; khăn v&amp;agrave; phức tạp.&amp;nbsp; Trong số 62 tập đo&amp;agrave;n, Tổng c&amp;ocirc;ng ty được Ch&amp;iacute;nh phủ ph&amp;ecirc; duyệt đề &amp;aacute;n t&amp;aacute;i cơ cấu đều trong qu&amp;aacute; tr&amp;igrave;nh đang tiến h&amp;agrave;nh, chia l&amp;agrave;m bốn nh&amp;oacute;m (t&amp;aacute;i cơ cấu tổ chức, sắp xếp lại DN; t&amp;aacute;i cơ cấu về t&amp;agrave;i ch&amp;iacute;nh; tho&amp;aacute;i vốn đầu tư ra ngo&amp;agrave;i DN, t&amp;aacute;i cơ cấu về quản trị, lao động). V&amp;igrave; vậy việc kiểm tra qu&amp;aacute; tr&amp;igrave;nh thực hiện t&amp;aacute;i cơ cấu của từng tổ chức, DN li&amp;ecirc;n quan đến t&amp;aacute;i cơ cấu t&amp;agrave;i ch&amp;iacute;nh, tho&amp;aacute;i vốn đầu tư&amp;hellip;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Ts L&amp;ecirc; Xu&amp;acirc;n Nghĩa - Viện trưởng Viện Nghi&amp;ecirc;n cứu Ph&amp;aacute;t triển kinh doanh, đặc biệt quan t&amp;acirc;m đến t&amp;aacute;i cơ cấu khu vực ng&amp;acirc;n h&amp;agrave;ng, tiền tệ chia sẻ,&amp;nbsp;việc t&amp;aacute;i cơ cấu khu vực ng&amp;acirc;n h&amp;agrave;ng c&amp;ograve;n gặp trở lực từ một số nguy&amp;ecirc;n nh&amp;acirc;n, như: thiếu đồng bộ về ch&amp;iacute;nh s&amp;aacute;ch, c&amp;aacute;c ng&amp;acirc;n h&amp;agrave;ng xử l&amp;yacute; nợ xấu chậm, thị trường mua b&amp;aacute;n nợ c&amp;ograve;n k&amp;eacute;m ph&amp;aacute;t triển, nợ xấu tiềm t&amp;agrave;ng lớn từ ph&amp;iacute;a DNNN&amp;hellip; Th&amp;aacute;o gỡ kh&amp;uacute;c mắc n&amp;agrave;y, cần sự đồng thuận giữa ng&amp;acirc;n h&amp;agrave;ng v&amp;agrave; c&amp;aacute;c Bộ, ng&amp;agrave;nh li&amp;ecirc;n quan, đặc biệt l&amp;agrave; Bộ T&amp;agrave;i ch&amp;iacute;nh, yếu tố n&amp;agrave;y sẽ đem lại nguồn lực v&amp;agrave; cơ chế th&amp;uacute;c đẩy hoạt động t&amp;aacute;i cơ cấu khu vực DNNN, hệ thống ng&amp;acirc;n h&amp;agrave;ng trở n&amp;ecirc;n minh bạch-hiệu quả&amp;hellip;&amp;rdquo;- TS L&amp;ecirc; Xu&amp;acirc;n Nghĩa cũng b&amp;agrave;y tỏ.&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Tập trung v&amp;agrave;o 3 điểm &amp;ldquo;n&amp;oacute;ng&amp;rdquo;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Trước thực tế hoạt động t&amp;aacute;i cơ cấu nền kinh tế c&amp;ograve;n nhiều th&amp;aacute;ch thức, diễn biến phức tạp, TS. Nguyễn Hữu Vạn&amp;nbsp;cho hay, x&amp;aacute;c định c&amp;ocirc;ng t&amp;aacute;c Kiểm to&amp;aacute;n Nh&amp;agrave; nước cần được đổi mới v&amp;agrave; hoạt động c&amp;oacute; trọng t&amp;acirc;m. Trong năm 2014, để phục vụ Quốc hội thực hiện gi&amp;aacute;m s&amp;aacute;t việc thực hiện t&amp;aacute;i cơ cấu nền kinh tế, Kiểm to&amp;aacute;n Nh&amp;agrave; nước sẽ tập trung v&amp;agrave;o ba điểm &amp;ldquo;n&amp;oacute;ng&amp;rdquo;: kiểm to&amp;aacute;n c&amp;aacute;c dự &amp;aacute;n, c&amp;ocirc;ng tr&amp;igrave;nh đầu tư x&amp;acirc;y dựng, mua sắm, sửa chữa t&amp;agrave;i sản&amp;hellip;, ch&amp;uacute; trọng đ&amp;aacute;nh gi&amp;aacute; t&amp;iacute;nh kinh tế, hiệu quả sử dụng vốn tr&amp;aacute;i phiếu Ch&amp;iacute;nh phủ; kiểm to&amp;aacute;n c&amp;aacute;c tập đo&amp;agrave;n, Tổng c&amp;ocirc;ng ty Nh&amp;agrave; nước, ng&amp;acirc;n h&amp;agrave;ng thương mại Nh&amp;agrave; nước; kiểm to&amp;aacute;n tổ chức t&amp;agrave;i ch&amp;iacute;nh Nh&amp;agrave; nước.&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Về&amp;nbsp;đổi mới v&amp;agrave; n&amp;acirc;ng cao hiệu quả&amp;nbsp;kiểm to&amp;aacute;n hoạt động t&amp;aacute;i cơ cấu nền kinh tế, Ths L&amp;ecirc; Thị Hồng Hạnh-Ph&amp;oacute; Kiểm to&amp;aacute;n trưởng Kiểm to&amp;aacute;n Nh&amp;agrave; nước khu vực I cho rằng, năm 2014, cần n&amp;acirc;ng cao hiệu quả kiểm to&amp;aacute;n ở tất cả c&amp;aacute;c kh&amp;acirc;u, l&amp;agrave;m chặt chẽ từ kh&amp;acirc;u lập kế hoạch kiểm to&amp;aacute;n đến chi tiết những hạng mục cần kiểm to&amp;aacute;n căn cứ tr&amp;ecirc;n b&amp;aacute;o c&amp;aacute;o t&amp;agrave;i ch&amp;iacute;nh của DN trong nhiều năm gần đ&amp;acirc;y. Tập trung nh&amp;acirc;n lực kiểm to&amp;aacute;n c&amp;oacute; lộ tr&amp;igrave;nh trung, d&amp;agrave;i hạn (3-5 năm) đối với từng DN cụ thể. H&amp;agrave;ng năm Kiểm to&amp;aacute;n Nh&amp;agrave; nước n&amp;ecirc;n c&amp;oacute; đ&amp;aacute;nh gi&amp;aacute; r&amp;uacute;t kinh nghiệm c&amp;ocirc;ng t&amp;aacute;c kiểm to&amp;aacute;n hoạt động t&amp;aacute;i cơ cấu nền kinh tế theo chuy&amp;ecirc;n đề, từng linh vực DNNN, đầu tư c&amp;ocirc;ng, t&amp;aacute;i cơ cấu hoạt động t&amp;agrave;i ch&amp;iacute;nh, ng&amp;acirc;n h&amp;agrave;ng&amp;hellip;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		&amp;ldquo;Đối với lĩnh vực kiểm to&amp;aacute;n hoạt động t&amp;aacute;i cơ cấu ng&amp;acirc;n h&amp;agrave;ng, , cơ quan kiểm to&amp;aacute;n cần quan t&amp;acirc;m đến những chỉ ti&amp;ecirc;u ph&amp;acirc;n t&amp;iacute;ch t&amp;agrave;i ch&amp;iacute;nh của DN ng&amp;acirc;n h&amp;agrave;ng. C&amp;oacute; thể &amp;aacute;p dụng c&amp;aacute;c chuẩn mực kế to&amp;aacute;n, chuẩn mực b&amp;aacute;o c&amp;aacute;o t&amp;agrave;i ch&amp;iacute;nh trong qu&amp;aacute; tr&amp;igrave;nh xem x&amp;eacute;t hoạt động t&amp;aacute;i cấu tr&amp;uacute;c của từng DN ng&amp;acirc;n h&amp;agrave;ng. Ch&amp;uacute; &amp;yacute; đến c&amp;aacute;c chỉ ti&amp;ecirc;u: tỷ lệ cho vay/ huy động, tỷ lệ cho vay trung d&amp;agrave;i hạn, tỷ lệ rủi ro của t&amp;agrave;i sản t&amp;agrave;i ch&amp;iacute;nh v&amp;agrave; bất động sản&amp;hellip;, tr&amp;ecirc;n cơ sở đ&amp;oacute; x&amp;aacute;c định năng lực t&amp;agrave;i ch&amp;iacute;nh của DN, đưa ra c&amp;aacute;c biện ph&amp;aacute;p hỗ trợ, từ đ&amp;oacute; th&amp;uacute;c đẩy nhanh hơn, hiệu quả hơn qu&amp;aacute; tr&amp;igrave;nh t&amp;aacute;i cấu tr&amp;uacute;c của DN ng&amp;acirc;n h&amp;agrave;ng, g&amp;oacute;p phần l&amp;agrave;m l&amp;agrave;nh mạnh hoạt động to&amp;agrave;n hệ thống ng&amp;acirc;n h&amp;agrave;ng v&amp;agrave; nền kinh tế&amp;hellip;&amp;rdquo;- TS L&amp;ecirc; Xu&amp;acirc;n Nghĩa đưa ra giải ph&amp;aacute;p./.&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px; text-align: right;&quot;&gt;\r\n		&lt;strong&gt;Nguồn: Cổng Th&amp;ocirc;ng tin Bộ T&amp;agrave;i ch&amp;iacute;nh&lt;/strong&gt;&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n', 'STRENGTHENING AUDITING IN ECONOMIC RESTRUCTUR', 'Tái cơ cấu nền kinh tế được khẳng định trong Nghị quyết Hội nghị lần thứ ba Ban Chấp hành Trung ương Đảng khóa XI, sau hơn 3 năm thực hiện đã đạt được những kết quả tích cực. Tuy nhiên, tại hội thảo “Tổ chức kiểm toán quá trình tái cơ cấu nền kinh tế” diễn ra vừa qua tại Hà Nội, TS. Nguyễn Hữu Vạn-Tổng Kiểm toán Nhà nước cho biết, năm 2014, kiểm toán hoạt động tái cơ cấu nền kinh tế sẽ được Kiểm toán nhà nước (KTNN) triển khai mạnh mẽ hơn.', '&lt;div style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;T&amp;aacute;i cơ cấu nền kinh tế được khẳng định trong Nghị quyết Hội nghị lần thứ ba Ban Chấp h&amp;agrave;nh Trung ương Đảng kh&amp;oacute;a XI, sau hơn 3 năm thực hiện đ&amp;atilde; đạt được những kết quả t&amp;iacute;ch cực. Tuy nhi&amp;ecirc;n, tại hội thảo &amp;ldquo;Tổ chức kiểm to&amp;aacute;n qu&amp;aacute; tr&amp;igrave;nh t&amp;aacute;i cơ cấu nền kinh tế&amp;rdquo; diễn ra vừa qua tại H&amp;agrave; Nội, TS. Nguyễn Hữu Vạn-Tổng Kiểm to&amp;aacute;n Nh&amp;agrave; nước cho biết, năm 2014, kiểm to&amp;aacute;n hoạt động t&amp;aacute;i cơ cấu nền kinh tế sẽ được Kiểm to&amp;aacute;n nh&amp;agrave; nước (KTNN) triển khai mạnh mẽ hơn.&lt;/strong&gt;&lt;/div&gt;\r\n&lt;div style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n&lt;div style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;img alt=&quot;1593039&quot; height=&quot;250&quot; src=&quot;http://aasc.com.vn/web/images/tintuc/1593039.PNG&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;376&quot; /&gt;&lt;br /&gt;\r\n	Ảnh minh họa. Nguồn Internet&lt;br /&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Quan t&amp;acirc;m t&amp;aacute;i cơ cấu khu vực DNNN&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Việc t&amp;aacute;i cơ cấu n&amp;ecirc;n kinh tế&amp;nbsp;được Ch&amp;iacute;nh phủ chỉ&amp;nbsp; đạo tập trung v&amp;agrave;o ba lĩnh vực (đầu tư c&amp;ocirc;ng; khu vực t&amp;agrave;i ch&amp;iacute;nh, tiền tệ, ng&amp;acirc;n h&amp;agrave;ng; khu vực doanh nghiệp Nh&amp;agrave; nước, trọng t&amp;acirc;m l&amp;agrave; tập đo&amp;agrave;n kinh tế, c&amp;aacute;c Tổng c&amp;ocirc;ng ty Nh&amp;agrave; nước).&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		TS. Nguyễn Hữu Vạn-Tổng Kiểm to&amp;aacute;n Nh&amp;agrave; nước&amp;nbsp; chia sẻ, t&amp;aacute;i cơ cấu nền kinh tế đạt mục ti&amp;ecirc;u chuyển đổi m&amp;ocirc; h&amp;igrave;nh tăng trưởng (ổn định kinh tế vĩ m&amp;ocirc;, chỉ số gi&amp;aacute; ti&amp;ecirc;u d&amp;ugrave;ng giảm, c&amp;aacute;c yếu tố nền tảng của kinh tế vĩ m&amp;ocirc; dần được tăng cường, c&amp;ugrave;ng cố), tuy nhi&amp;ecirc;n c&amp;ograve;n nhiều hạn chế, nhất l&amp;agrave; việc t&amp;aacute;i cơ cấu hoạt động doanh nghiệp Nh&amp;agrave; nước (DNNN).&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Chuy&amp;ecirc;n gia Phạm Đức Trung-Viện Nghi&amp;ecirc;n cứu quản l&amp;yacute; kinh tế Trung ương cho biết, kết quả thực hiện t&amp;aacute;i cơ cấu DNNN c&amp;ograve;n hạn chế. C&amp;ocirc;ng t&amp;aacute;c x&amp;acirc;y dựng ho&amp;agrave;n thiện khung khổ ph&amp;aacute;p luật chưa kịp thời. Phần lớn văn bản ph&amp;aacute;p l&amp;yacute; c&amp;oacute; li&amp;ecirc;n quan trực tiếp đến t&amp;aacute;i cấu tr&amp;uacute;c DNNN vẫn đang trong giai đoạn chuẩn bị, đặc biệt về cổ phần h&amp;oacute;a, đa dạng h&amp;oacute;a sở hữu. B&amp;ecirc;n cạnh đ&amp;oacute;, nhiều Tổng c&amp;ocirc;ng ty Nh&amp;agrave; nước c&amp;ograve;n l&amp;uacute;ng t&amp;uacute;ng trong x&amp;acirc;y dựng nội dung, lộ tr&amp;igrave;nh t&amp;aacute;i cơ cấu, nhất l&amp;agrave; khi vướng phải c&amp;aacute;c vấn đề xử l&amp;yacute; t&amp;agrave;i ch&amp;iacute;nh, tho&amp;aacute;i vốn&amp;hellip;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		&amp;Ocirc;ng L&amp;ecirc; Ho&amp;agrave;ng Hải - Ph&amp;oacute; Cục trưởng Cục T&amp;agrave;i ch&amp;iacute;nh doanh nghiệp (Bộ T&amp;agrave;i ch&amp;iacute;nh) cũng nhận x&amp;eacute;t, việc xử l&amp;yacute; t&amp;aacute;i cơ cấu khu vực DNNN diễn ra cũng rất kh&amp;oacute; khăn v&amp;agrave; phức tạp.&amp;nbsp; Trong số 62 tập đo&amp;agrave;n, Tổng c&amp;ocirc;ng ty được Ch&amp;iacute;nh phủ ph&amp;ecirc; duyệt đề &amp;aacute;n t&amp;aacute;i cơ cấu đều trong qu&amp;aacute; tr&amp;igrave;nh đang tiến h&amp;agrave;nh, chia l&amp;agrave;m bốn nh&amp;oacute;m (t&amp;aacute;i cơ cấu tổ chức, sắp xếp lại DN; t&amp;aacute;i cơ cấu về t&amp;agrave;i ch&amp;iacute;nh; tho&amp;aacute;i vốn đầu tư ra ngo&amp;agrave;i DN, t&amp;aacute;i cơ cấu về quản trị, lao động). V&amp;igrave; vậy việc kiểm tra qu&amp;aacute; tr&amp;igrave;nh thực hiện t&amp;aacute;i cơ cấu của từng tổ chức, DN li&amp;ecirc;n quan đến t&amp;aacute;i cơ cấu t&amp;agrave;i ch&amp;iacute;nh, tho&amp;aacute;i vốn đầu tư&amp;hellip;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Ts L&amp;ecirc; Xu&amp;acirc;n Nghĩa - Viện trưởng Viện Nghi&amp;ecirc;n cứu Ph&amp;aacute;t triển kinh doanh, đặc biệt quan t&amp;acirc;m đến t&amp;aacute;i cơ cấu khu vực ng&amp;acirc;n h&amp;agrave;ng, tiền tệ chia sẻ,&amp;nbsp;việc t&amp;aacute;i cơ cấu khu vực ng&amp;acirc;n h&amp;agrave;ng c&amp;ograve;n gặp trở lực từ một số nguy&amp;ecirc;n nh&amp;acirc;n, như: thiếu đồng bộ về ch&amp;iacute;nh s&amp;aacute;ch, c&amp;aacute;c ng&amp;acirc;n h&amp;agrave;ng xử l&amp;yacute; nợ xấu chậm, thị trường mua b&amp;aacute;n nợ c&amp;ograve;n k&amp;eacute;m ph&amp;aacute;t triển, nợ xấu tiềm t&amp;agrave;ng lớn từ ph&amp;iacute;a DNNN&amp;hellip; Th&amp;aacute;o gỡ kh&amp;uacute;c mắc n&amp;agrave;y, cần sự đồng thuận giữa ng&amp;acirc;n h&amp;agrave;ng v&amp;agrave; c&amp;aacute;c Bộ, ng&amp;agrave;nh li&amp;ecirc;n quan, đặc biệt l&amp;agrave; Bộ T&amp;agrave;i ch&amp;iacute;nh, yếu tố n&amp;agrave;y sẽ đem lại nguồn lực v&amp;agrave; cơ chế th&amp;uacute;c đẩy hoạt động t&amp;aacute;i cơ cấu khu vực DNNN, hệ thống ng&amp;acirc;n h&amp;agrave;ng trở n&amp;ecirc;n minh bạch-hiệu quả&amp;hellip;&amp;rdquo;- TS L&amp;ecirc; Xu&amp;acirc;n Nghĩa cũng b&amp;agrave;y tỏ.&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Tập trung v&amp;agrave;o 3 điểm &amp;ldquo;n&amp;oacute;ng&amp;rdquo;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Trước thực tế hoạt động t&amp;aacute;i cơ cấu nền kinh tế c&amp;ograve;n nhiều th&amp;aacute;ch thức, diễn biến phức tạp, TS. Nguyễn Hữu Vạn&amp;nbsp;cho hay, x&amp;aacute;c định c&amp;ocirc;ng t&amp;aacute;c Kiểm to&amp;aacute;n Nh&amp;agrave; nước cần được đổi mới v&amp;agrave; hoạt động c&amp;oacute; trọng t&amp;acirc;m. Trong năm 2014, để phục vụ Quốc hội thực hiện gi&amp;aacute;m s&amp;aacute;t việc thực hiện t&amp;aacute;i cơ cấu nền kinh tế, Kiểm to&amp;aacute;n Nh&amp;agrave; nước sẽ tập trung v&amp;agrave;o ba điểm &amp;ldquo;n&amp;oacute;ng&amp;rdquo;: kiểm to&amp;aacute;n c&amp;aacute;c dự &amp;aacute;n, c&amp;ocirc;ng tr&amp;igrave;nh đầu tư x&amp;acirc;y dựng, mua sắm, sửa chữa t&amp;agrave;i sản&amp;hellip;, ch&amp;uacute; trọng đ&amp;aacute;nh gi&amp;aacute; t&amp;iacute;nh kinh tế, hiệu quả sử dụng vốn tr&amp;aacute;i phiếu Ch&amp;iacute;nh phủ; kiểm to&amp;aacute;n c&amp;aacute;c tập đo&amp;agrave;n, Tổng c&amp;ocirc;ng ty Nh&amp;agrave; nước, ng&amp;acirc;n h&amp;agrave;ng thương mại Nh&amp;agrave; nước; kiểm to&amp;aacute;n tổ chức t&amp;agrave;i ch&amp;iacute;nh Nh&amp;agrave; nước.&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Về&amp;nbsp;đổi mới v&amp;agrave; n&amp;acirc;ng cao hiệu quả&amp;nbsp;kiểm to&amp;aacute;n hoạt động t&amp;aacute;i cơ cấu nền kinh tế, Ths L&amp;ecirc; Thị Hồng Hạnh-Ph&amp;oacute; Kiểm to&amp;aacute;n trưởng Kiểm to&amp;aacute;n Nh&amp;agrave; nước khu vực I cho rằng, năm 2014, cần n&amp;acirc;ng cao hiệu quả kiểm to&amp;aacute;n ở tất cả c&amp;aacute;c kh&amp;acirc;u, l&amp;agrave;m chặt chẽ từ kh&amp;acirc;u lập kế hoạch kiểm to&amp;aacute;n đến chi tiết những hạng mục cần kiểm to&amp;aacute;n căn cứ tr&amp;ecirc;n b&amp;aacute;o c&amp;aacute;o t&amp;agrave;i ch&amp;iacute;nh của DN trong nhiều năm gần đ&amp;acirc;y. Tập trung nh&amp;acirc;n lực kiểm to&amp;aacute;n c&amp;oacute; lộ tr&amp;igrave;nh trung, d&amp;agrave;i hạn (3-5 năm) đối với từng DN cụ thể. H&amp;agrave;ng năm Kiểm to&amp;aacute;n Nh&amp;agrave; nước n&amp;ecirc;n c&amp;oacute; đ&amp;aacute;nh gi&amp;aacute; r&amp;uacute;t kinh nghiệm c&amp;ocirc;ng t&amp;aacute;c kiểm to&amp;aacute;n hoạt động t&amp;aacute;i cơ cấu nền kinh tế theo chuy&amp;ecirc;n đề, từng linh vực DNNN, đầu tư c&amp;ocirc;ng, t&amp;aacute;i cơ cấu hoạt động t&amp;agrave;i ch&amp;iacute;nh, ng&amp;acirc;n h&amp;agrave;ng&amp;hellip;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		&amp;ldquo;Đối với lĩnh vực kiểm to&amp;aacute;n hoạt động t&amp;aacute;i cơ cấu ng&amp;acirc;n h&amp;agrave;ng, , cơ quan kiểm to&amp;aacute;n cần quan t&amp;acirc;m đến những chỉ ti&amp;ecirc;u ph&amp;acirc;n t&amp;iacute;ch t&amp;agrave;i ch&amp;iacute;nh của DN ng&amp;acirc;n h&amp;agrave;ng. C&amp;oacute; thể &amp;aacute;p dụng c&amp;aacute;c chuẩn mực kế to&amp;aacute;n, chuẩn mực b&amp;aacute;o c&amp;aacute;o t&amp;agrave;i ch&amp;iacute;nh trong qu&amp;aacute; tr&amp;igrave;nh xem x&amp;eacute;t hoạt động t&amp;aacute;i cấu tr&amp;uacute;c của từng DN ng&amp;acirc;n h&amp;agrave;ng. Ch&amp;uacute; &amp;yacute; đến c&amp;aacute;c chỉ ti&amp;ecirc;u: tỷ lệ cho vay/ huy động, tỷ lệ cho vay trung d&amp;agrave;i hạn, tỷ lệ rủi ro của t&amp;agrave;i sản t&amp;agrave;i ch&amp;iacute;nh v&amp;agrave; bất động sản&amp;hellip;, tr&amp;ecirc;n cơ sở đ&amp;oacute; x&amp;aacute;c định năng lực t&amp;agrave;i ch&amp;iacute;nh của DN, đưa ra c&amp;aacute;c biện ph&amp;aacute;p hỗ trợ, từ đ&amp;oacute; th&amp;uacute;c đẩy nhanh hơn, hiệu quả hơn qu&amp;aacute; tr&amp;igrave;nh t&amp;aacute;i cấu tr&amp;uacute;c của DN ng&amp;acirc;n h&amp;agrave;ng, g&amp;oacute;p phần l&amp;agrave;m l&amp;agrave;nh mạnh hoạt động to&amp;agrave;n hệ thống ng&amp;acirc;n h&amp;agrave;ng v&amp;agrave; nền kinh tế&amp;hellip;&amp;rdquo;- TS L&amp;ecirc; Xu&amp;acirc;n Nghĩa đưa ra giải ph&amp;aacute;p./.&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px; text-align: right;&quot;&gt;\r\n		&lt;strong&gt;Nguồn: Cổng Th&amp;ocirc;ng tin Bộ T&amp;agrave;i ch&amp;iacute;nh&lt;/strong&gt;&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n', NULL, NULL, 1, '2014-03-28 15:48:37', 1, NULL, 1, 0),
(12, 27, 'SIẾT CHẤT LƯỢNG KIỂM TOÁN TRONG LĨNH VỰC CHỨN', '(ĐTCK) Chất lượng kiểm toán trong lĩnh vực chứng khoán sẽ được siết chặt theo quy định mới tại Thông tư 183/2013/TT-BTC của Bộ Tài chính có hiệu lực từ ngày 18/1 tới. ', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;line-height: 1.3em;&quot;&gt;(ĐTCK)&amp;nbsp;Chất lượng kiểm to&amp;aacute;n trong lĩnh vực chứng kho&amp;aacute;n sẽ được siết chặt theo quy định mới tại Th&amp;ocirc;ng tư 183/2013/TT-BTC của Bộ T&amp;agrave;i ch&amp;iacute;nh c&amp;oacute; hiệu lực từ ng&amp;agrave;y 18/1 tới.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;line-height: 1.3em;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;img alt=&quot;hd&quot; height=&quot;200&quot; src=&quot;http://aasc.com.vn/web/images/hd.jpg&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;369&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Cụ thể, c&amp;aacute;c c&amp;ocirc;ng ty kiểm to&amp;aacute;n kh&amp;ocirc;ng được kiểm to&amp;aacute;n cho c&amp;aacute;c đơn vị c&amp;oacute; lợi &amp;iacute;ch c&amp;ocirc;ng ch&amp;uacute;ng như: c&amp;ocirc;ng ty đại ch&amp;uacute;ng quy m&amp;ocirc; lớn, tổ chức ni&amp;ecirc;m yết, tổ chức ph&amp;aacute;t h&amp;agrave;nh chứng kho&amp;aacute;n ra c&amp;ocirc;ng ch&amp;uacute;ng, CTCK..., trong c&amp;aacute;c trường hợp: tổ chức kiểm to&amp;aacute;n l&amp;agrave; kh&amp;aacute;ch h&amp;agrave;ng đang hưởng những điều kiện ưu đ&amp;atilde;i ngo&amp;agrave;i mức th&amp;ocirc;ng thường của đơn vị c&amp;oacute; lợi &amp;iacute;ch c&amp;ocirc;ng ch&amp;uacute;ng; người c&amp;oacute; tr&amp;aacute;ch nhiệm quản l&amp;yacute;, điều h&amp;agrave;nh, th&amp;agrave;nh vi&amp;ecirc;n ban kiểm so&amp;aacute;t, kế to&amp;aacute;n trưởng của tổ chức kiểm to&amp;aacute;n l&amp;agrave; người c&amp;oacute; tr&amp;aacute;ch nhiệm quản l&amp;yacute;, điều h&amp;agrave;nh của đơn vị c&amp;oacute; lợi &amp;iacute;ch c&amp;ocirc;ng ch&amp;uacute;ng được kiểm to&amp;aacute;n.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Cũng theo quy định mới, trong thời hạn 90 ng&amp;agrave;y, kể từ ng&amp;agrave;y kết th&amp;uacute;c năm t&amp;agrave;i ch&amp;iacute;nh, tổ chức kiểm to&amp;aacute;n được chấp thuận phải c&amp;ocirc;ng khai tr&amp;ecirc;n trang th&amp;ocirc;ng tin điện tử của m&amp;igrave;nh b&amp;aacute;o c&amp;aacute;o minh bạch h&amp;agrave;ng năm. B&amp;aacute;o c&amp;aacute;o minh bạch phải được cập nhật thường xuy&amp;ecirc;n c&amp;aacute;c th&amp;ocirc;ng tin trong v&amp;ograve;ng 30 ng&amp;agrave;y kể từ khi c&amp;oacute; thay đổi.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Ủy ban Chứng kho&amp;aacute;n Nh&amp;agrave; nước trực tiếp gi&amp;aacute;m s&amp;aacute;t, kiểm tra đối với tổ chức kiểm to&amp;aacute;n được chấp thuận thực hiện kiểm to&amp;aacute;n cho c&amp;aacute;c đơn vị c&amp;oacute; lợi &amp;iacute;ch c&amp;ocirc;ng ch&amp;uacute;ng thuộc lĩnh vực chứng kho&amp;aacute;n.&lt;/p&gt;\r\n&lt;p align=&quot;right&quot; style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px;&quot;&gt;\r\n	H.H&amp;ograve;e &amp;ndash; B&amp;aacute;o Đầu tư Chứng kho&amp;aacute;n.&amp;nbsp;&lt;/p&gt;\r\n', 'IMPROVING THE QUALITY CONTROL OF AUDITING ENG', 'Quality of audit in securities will be control tightly according to new provisions of Circular No.183/2013/TT-BTC of Ministry of Finance. This Circular takes effect from 18/1/2014.', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Quality of audit in securities will be control tightly according to new provisions of Circular No.183/2013/TT-BTC of Ministry of Finance. This Circular takes effect from 18/1/2014.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;font-family: &#039;Times New Roman&#039;, serif;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;img alt=&quot;DTCK&quot; height=&quot;204&quot; src=&quot;http://aasc.com.vn/web/images/DTCK.png&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;373&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	In detail, audit firms who are not allowed to audit public interest organizations such as: large public companies, listed companies, organizations issuing shares, etc. In case, the audit firm is a customer who are enjoying the preferential conditions provided by the public interest organizations to be audited; or person who have responsibility to manage, control, member of management board, chief accountants of audit firm is responsible for manage, control the public interest organization to be audited.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Also according to these new regulations, during 90 days since the last day of the fiscal year, approved audit firm must publish their transparency report annually on their website. This transparency report must be updated regularly all changes in their information during 30 days since there is any change.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	State Securities Commission of Vietnam directly reviews and inspects the audit engagement of approved audit firms for securities public interest organizations &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;, serif;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;em&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Translated from Vietnamese version&lt;/em&gt;&lt;/p&gt;\r\n', NULL, NULL, 1, '2014-03-28 15:50:23', 1, NULL, 1, 0);
INSERT INTO `news` (`id`, `menu_id`, `title`, `caption`, `detail`, `title_eng`, `caption_eng`, `detail_eng`, `thumb_image_path`, `list_file_attach`, `create_user_id`, `create_date`, `feature_flag`, `update_date`, `is_public`, `del_flg`) VALUES
(13, 27, 'ĐẨY MẠNH CỔ PHẦN HÓA DNNN VÀ THOÁI VỐN NN - N', 'Đó là một trong những nội dung chỉ đạo của Thứ trưởng Bộ Tài chính Trần Xuân Hà tại hội nghị của HNX tổ chức ngày 19/12/2013 về tổng kết công tác chỉ đạo điều hành năm 2013 và triển khai chương trình công tác năm 2014 nhằm tổng kết, đánh giá những kết quả đã đạt được trong năm qua và đề ra phương hướng, nhiệm vụ công tác cho năm tới.', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Đ&amp;oacute; l&amp;agrave; một trong những nội dung chỉ đạo của Thứ trưởng Bộ T&amp;agrave;i ch&amp;iacute;nh Trần Xu&amp;acirc;n H&amp;agrave; tại hội nghị của HNX tổ chức ng&amp;agrave;y 19/12/2013 về tổng kết c&amp;ocirc;ng t&amp;aacute;c chỉ đạo điều h&amp;agrave;nh năm 2013 v&amp;agrave; triển khai chương tr&amp;igrave;nh c&amp;ocirc;ng t&amp;aacute;c năm 2014 nhằm tổng kết, đ&amp;aacute;nh gi&amp;aacute; những kết quả đ&amp;atilde; đạt được trong năm qua v&amp;agrave; đề ra phương hướng, nhiệm vụ c&amp;ocirc;ng t&amp;aacute;c cho năm tới.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 6pt 0cm; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; text-align: justify; line-height: 12pt;&quot;&gt;\r\n	&lt;span style=&quot;font-family: Arial, sans-serif; font-size: 8pt; line-height: normal; text-align: center;&quot;&gt;&lt;img alt=&quot;1590934&quot; height=&quot;300&quot; src=&quot;http://aasc.com.vn/web/images/tintuc/1590934.JPG&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px; display: block; margin-left: auto; margin-right: auto;&quot; width=&quot;397&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: center;&quot;&gt;\r\n	&lt;em&gt;Thứ trưởng Bộ T&amp;agrave;i ch&amp;iacute;nh Trần Xu&amp;acirc;n H&amp;agrave; ph&amp;aacute;t biểu chỉ đạo&amp;nbsp;&amp;nbsp;tại hội nghị&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;Tăng trưởng đột ph&amp;aacute; về quy m&amp;ocirc; huy động vốn cho NSNN&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Tham dự hội nghị c&amp;oacute; sự hiện diện của Thứ trưởng Trần Xu&amp;acirc;n H&amp;agrave;, Chủ tịch UBCKNN Vũ Bằng, c&amp;ugrave;ng đại diện L&amp;atilde;nh đạo c&amp;aacute;c đơn vị thuộc Bộ T&amp;agrave;i ch&amp;iacute;nh, Kho bạc Nh&amp;agrave; nước, UBCKNN, Ng&amp;acirc;n h&amp;agrave;ng Nh&amp;agrave; nước, UBND Tp.H&amp;agrave; Nội, Trung t&amp;acirc;m Lưu k&amp;yacute; Chứng kho&amp;aacute;n Việt Nam, Sở GDCK Hồ Ch&amp;iacute; Minh, Ng&amp;acirc;n h&amp;agrave;ng Ph&amp;aacute;t triển Việt Nam, Ng&amp;acirc;n h&amp;agrave;ng Ch&amp;iacute;nh s&amp;aacute;ch x&amp;atilde; hội, Hiệp hội Kinh doanh Chứng kho&amp;aacute;n, Hiệp Hội Tr&amp;aacute;i phiếu Việt Nam, c&amp;ugrave;ng to&amp;agrave;n thể c&amp;aacute;n bộ nh&amp;acirc;n vi&amp;ecirc;n Sở GDCK H&amp;agrave; Nội.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Theo b&amp;aacute;o c&amp;aacute;o của Sở GDCK H&amp;agrave; Nội đ&amp;atilde; n&amp;ecirc;u bật kết quả đ&amp;aacute;ng ghi nhận tr&amp;ecirc;n cả 03 thị trường tr&amp;aacute;i phiếu, cổ phiếu ni&amp;ecirc;m yết v&amp;agrave; UPCoM c&amp;ugrave;ng với những đổi mới trong c&amp;ocirc;ng t&amp;aacute;c tổ chức điều h&amp;agrave;nh của Sở GDCK H&amp;agrave; Nội trong năm 2013.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Trong đ&amp;oacute;, điểm s&amp;aacute;ng tr&amp;ecirc;n thị trường tr&amp;aacute;i phiếu l&amp;agrave; sự tăng trưởng đột ph&amp;aacute; về quy m&amp;ocirc; huy động vốn cho ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước, quy m&amp;ocirc; giao dịch v&amp;agrave; tỷ lệ thanh khoản. Điểm s&amp;aacute;ng tr&amp;ecirc;n thị trường cổ phiếu ni&amp;ecirc;m yết l&amp;agrave; việc n&amp;acirc;ng cấp, ph&amp;aacute;t triển hệ thống c&amp;ocirc;ng nghệ l&amp;ecirc;n phi&amp;ecirc;n bản 5 với năng lực xử l&amp;yacute; gấp 20-30 lần so với phi&amp;ecirc;n bản cũ; n&amp;acirc;ng cao chất lượng DNNY th&amp;ocirc;ng qua việc cải thiện chất lượng quản trị c&amp;ocirc;ng ty; r&amp;uacute;t ngắn thời gian c&amp;ocirc;ng bố th&amp;ocirc;ng tin của doanh nghiệp.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Đặc biệt trong năm qua, b&amp;ecirc;n cạnh vận h&amp;agrave;nh v&amp;agrave; ph&amp;aacute;t triển c&amp;aacute;c thị trường hiện c&amp;oacute;, Sở cũng tập trung ph&amp;aacute;t triển c&amp;aacute;c sản phẩm mới như c&amp;aacute;c bộ chỉ số mới, sản phẩm ETF, c&amp;aacute;c sản phẩm ph&amp;aacute;i sinh...&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Ph&amp;aacute;t biểu tại hội nghị, Chủ tịch UBCKNN Vũ Bằng nhấn mạnh trong năm tới Sở phải tiếp tục nghi&amp;ecirc;n cứu v&amp;agrave; t&amp;iacute;ch cực triển khai c&amp;aacute;c c&amp;ocirc;ng t&amp;aacute;c chuẩn bị để c&amp;oacute; thể sớm đưa sản phẩm ETF v&amp;agrave; c&amp;aacute;c sản phẩm ph&amp;aacute;i sinh v&amp;agrave;o vận h&amp;agrave;nh.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;Nỗ lực đổi mới tổ chức điều h&amp;agrave;nh&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Ph&amp;aacute;t biểu chỉ đạo tại Hội nghị, Thứ trưởng Bộ T&amp;agrave;i ch&amp;iacute;nh Trần Xu&amp;acirc;n H&amp;agrave; đ&amp;atilde; biểu dương c&amp;aacute;c th&amp;agrave;nh t&amp;iacute;ch của Sở GDCK H&amp;agrave; Nội đạt được trong năm 2013, được thể hiện tr&amp;ecirc;n c&amp;aacute;c mặt như: Kết quả huy động vốn cho ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước vượt mức kế hoạch, chương tr&amp;igrave;nh n&amp;acirc;ng cao chất lượng c&amp;ocirc;ng ty ni&amp;ecirc;m yết, c&amp;aacute;c kết quả nghi&amp;ecirc;n cứu v&amp;agrave; triển khai sản phẩm mới &amp;hellip; v&amp;agrave; đặc biệt l&amp;agrave; c&amp;aacute;c nỗ lực đổi mới c&amp;ocirc;ng t&amp;aacute;c tổ chức điều h&amp;agrave;nh của Sở.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Thứ trưởng chỉ đạo, trong năm 2014, Sở&amp;nbsp; GDCK H&amp;agrave; Nội cần ph&amp;aacute;t huy c&amp;aacute;c th&amp;agrave;nh t&amp;iacute;ch đ&amp;atilde; đạt được đồng thời phải coi việc đẩy mạnh cổ phần h&amp;oacute;a DNNN v&amp;agrave; tho&amp;aacute;i vốn nh&amp;agrave; nước l&amp;agrave; nhiệm vụ trọng t&amp;acirc;m; tiến h&amp;agrave;nh r&amp;agrave; so&amp;aacute;t danh s&amp;aacute;ch c&amp;aacute;c c&amp;ocirc;ng ty đ&amp;atilde; ph&amp;aacute;t h&amp;agrave;nh ra c&amp;ocirc;ng ch&amp;uacute;ng sau 01 năm để đưa v&amp;agrave;o giao dịch tập trung nhằm tăng h&amp;agrave;ng h&amp;oacute;a cho thị trường v&amp;agrave; t&amp;iacute;ch cực tham gia t&amp;aacute;i cơ cấu TTCK.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Kết th&amp;uacute;c hội nghị, Chủ tịch HĐQT ki&amp;ecirc;m Tổng Gi&amp;aacute;m đốc Trần Văn Dũng đ&amp;atilde; ph&amp;aacute;t biểu tiếp thu c&amp;aacute;c &amp;yacute; kiến chỉ đạo của L&amp;atilde;nh đạo BTC, UBCKNN v&amp;agrave; mong muốn Sở GDCK H&amp;agrave; Nội tiếp tục nhận được sự ủng hộ của c&amp;aacute;c cấp quản l&amp;yacute;, c&amp;aacute;c đơn vị li&amp;ecirc;n quan v&amp;agrave; th&amp;agrave;nh vi&amp;ecirc;n thị trường để c&amp;oacute; thể tạo được nhiều bước đột ph&amp;aacute;t trong hoạt động của Sở nhằm g&amp;oacute;p phần th&amp;uacute;c đẩy sự ph&amp;aacute;t triển của thị trường chứng kho&amp;aacute;n Việt Nam.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: right;&quot;&gt;\r\n	&lt;em&gt;&lt;strong&gt;Nguồn: Cổng Th&amp;ocirc;ng tin điện tử Bộ T&amp;agrave;i ch&amp;iacute;nh&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n', 'ĐẨY MẠNH CỔ PHẦN HÓA DNNN VÀ THOÁI VỐN NN - N', 'Đó là một trong những nội dung chỉ đạo của Thứ trưởng Bộ Tài chính Trần Xuân Hà tại hội nghị của HNX tổ chức ngày 19/12/2013 về tổng kết công tác chỉ đạo điều hành năm 2013 và triển khai chương trình công tác năm 2014 nhằm tổng kết, đánh giá những kết quả đã đạt được trong năm qua và đề ra phương hướng, nhiệm vụ công tác cho năm tới.', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Đ&amp;oacute; l&amp;agrave; một trong những nội dung chỉ đạo của Thứ trưởng Bộ T&amp;agrave;i ch&amp;iacute;nh Trần Xu&amp;acirc;n H&amp;agrave; tại hội nghị của HNX tổ chức ng&amp;agrave;y 19/12/2013 về tổng kết c&amp;ocirc;ng t&amp;aacute;c chỉ đạo điều h&amp;agrave;nh năm 2013 v&amp;agrave; triển khai chương tr&amp;igrave;nh c&amp;ocirc;ng t&amp;aacute;c năm 2014 nhằm tổng kết, đ&amp;aacute;nh gi&amp;aacute; những kết quả đ&amp;atilde; đạt được trong năm qua v&amp;agrave; đề ra phương hướng, nhiệm vụ c&amp;ocirc;ng t&amp;aacute;c cho năm tới.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 6pt 0cm; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; text-align: justify; line-height: 12pt;&quot;&gt;\r\n	&lt;span style=&quot;font-family: Arial, sans-serif; font-size: 8pt; line-height: normal; text-align: center;&quot;&gt;&lt;img alt=&quot;1590934&quot; height=&quot;300&quot; src=&quot;http://aasc.com.vn/web/images/tintuc/1590934.JPG&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px; display: block; margin-left: auto; margin-right: auto;&quot; width=&quot;397&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: center;&quot;&gt;\r\n	&lt;em&gt;Thứ trưởng Bộ T&amp;agrave;i ch&amp;iacute;nh Trần Xu&amp;acirc;n H&amp;agrave; ph&amp;aacute;t biểu chỉ đạo&amp;nbsp;&amp;nbsp;tại hội nghị&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;Tăng trưởng đột ph&amp;aacute; về quy m&amp;ocirc; huy động vốn cho NSNN&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Tham dự hội nghị c&amp;oacute; sự hiện diện của Thứ trưởng Trần Xu&amp;acirc;n H&amp;agrave;, Chủ tịch UBCKNN Vũ Bằng, c&amp;ugrave;ng đại diện L&amp;atilde;nh đạo c&amp;aacute;c đơn vị thuộc Bộ T&amp;agrave;i ch&amp;iacute;nh, Kho bạc Nh&amp;agrave; nước, UBCKNN, Ng&amp;acirc;n h&amp;agrave;ng Nh&amp;agrave; nước, UBND Tp.H&amp;agrave; Nội, Trung t&amp;acirc;m Lưu k&amp;yacute; Chứng kho&amp;aacute;n Việt Nam, Sở GDCK Hồ Ch&amp;iacute; Minh, Ng&amp;acirc;n h&amp;agrave;ng Ph&amp;aacute;t triển Việt Nam, Ng&amp;acirc;n h&amp;agrave;ng Ch&amp;iacute;nh s&amp;aacute;ch x&amp;atilde; hội, Hiệp hội Kinh doanh Chứng kho&amp;aacute;n, Hiệp Hội Tr&amp;aacute;i phiếu Việt Nam, c&amp;ugrave;ng to&amp;agrave;n thể c&amp;aacute;n bộ nh&amp;acirc;n vi&amp;ecirc;n Sở GDCK H&amp;agrave; Nội.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Theo b&amp;aacute;o c&amp;aacute;o của Sở GDCK H&amp;agrave; Nội đ&amp;atilde; n&amp;ecirc;u bật kết quả đ&amp;aacute;ng ghi nhận tr&amp;ecirc;n cả 03 thị trường tr&amp;aacute;i phiếu, cổ phiếu ni&amp;ecirc;m yết v&amp;agrave; UPCoM c&amp;ugrave;ng với những đổi mới trong c&amp;ocirc;ng t&amp;aacute;c tổ chức điều h&amp;agrave;nh của Sở GDCK H&amp;agrave; Nội trong năm 2013.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Trong đ&amp;oacute;, điểm s&amp;aacute;ng tr&amp;ecirc;n thị trường tr&amp;aacute;i phiếu l&amp;agrave; sự tăng trưởng đột ph&amp;aacute; về quy m&amp;ocirc; huy động vốn cho ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước, quy m&amp;ocirc; giao dịch v&amp;agrave; tỷ lệ thanh khoản. Điểm s&amp;aacute;ng tr&amp;ecirc;n thị trường cổ phiếu ni&amp;ecirc;m yết l&amp;agrave; việc n&amp;acirc;ng cấp, ph&amp;aacute;t triển hệ thống c&amp;ocirc;ng nghệ l&amp;ecirc;n phi&amp;ecirc;n bản 5 với năng lực xử l&amp;yacute; gấp 20-30 lần so với phi&amp;ecirc;n bản cũ; n&amp;acirc;ng cao chất lượng DNNY th&amp;ocirc;ng qua việc cải thiện chất lượng quản trị c&amp;ocirc;ng ty; r&amp;uacute;t ngắn thời gian c&amp;ocirc;ng bố th&amp;ocirc;ng tin của doanh nghiệp.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Đặc biệt trong năm qua, b&amp;ecirc;n cạnh vận h&amp;agrave;nh v&amp;agrave; ph&amp;aacute;t triển c&amp;aacute;c thị trường hiện c&amp;oacute;, Sở cũng tập trung ph&amp;aacute;t triển c&amp;aacute;c sản phẩm mới như c&amp;aacute;c bộ chỉ số mới, sản phẩm ETF, c&amp;aacute;c sản phẩm ph&amp;aacute;i sinh...&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Ph&amp;aacute;t biểu tại hội nghị, Chủ tịch UBCKNN Vũ Bằng nhấn mạnh trong năm tới Sở phải tiếp tục nghi&amp;ecirc;n cứu v&amp;agrave; t&amp;iacute;ch cực triển khai c&amp;aacute;c c&amp;ocirc;ng t&amp;aacute;c chuẩn bị để c&amp;oacute; thể sớm đưa sản phẩm ETF v&amp;agrave; c&amp;aacute;c sản phẩm ph&amp;aacute;i sinh v&amp;agrave;o vận h&amp;agrave;nh.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;Nỗ lực đổi mới tổ chức điều h&amp;agrave;nh&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Ph&amp;aacute;t biểu chỉ đạo tại Hội nghị, Thứ trưởng Bộ T&amp;agrave;i ch&amp;iacute;nh Trần Xu&amp;acirc;n H&amp;agrave; đ&amp;atilde; biểu dương c&amp;aacute;c th&amp;agrave;nh t&amp;iacute;ch của Sở GDCK H&amp;agrave; Nội đạt được trong năm 2013, được thể hiện tr&amp;ecirc;n c&amp;aacute;c mặt như: Kết quả huy động vốn cho ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước vượt mức kế hoạch, chương tr&amp;igrave;nh n&amp;acirc;ng cao chất lượng c&amp;ocirc;ng ty ni&amp;ecirc;m yết, c&amp;aacute;c kết quả nghi&amp;ecirc;n cứu v&amp;agrave; triển khai sản phẩm mới &amp;hellip; v&amp;agrave; đặc biệt l&amp;agrave; c&amp;aacute;c nỗ lực đổi mới c&amp;ocirc;ng t&amp;aacute;c tổ chức điều h&amp;agrave;nh của Sở.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Thứ trưởng chỉ đạo, trong năm 2014, Sở&amp;nbsp; GDCK H&amp;agrave; Nội cần ph&amp;aacute;t huy c&amp;aacute;c th&amp;agrave;nh t&amp;iacute;ch đ&amp;atilde; đạt được đồng thời phải coi việc đẩy mạnh cổ phần h&amp;oacute;a DNNN v&amp;agrave; tho&amp;aacute;i vốn nh&amp;agrave; nước l&amp;agrave; nhiệm vụ trọng t&amp;acirc;m; tiến h&amp;agrave;nh r&amp;agrave; so&amp;aacute;t danh s&amp;aacute;ch c&amp;aacute;c c&amp;ocirc;ng ty đ&amp;atilde; ph&amp;aacute;t h&amp;agrave;nh ra c&amp;ocirc;ng ch&amp;uacute;ng sau 01 năm để đưa v&amp;agrave;o giao dịch tập trung nhằm tăng h&amp;agrave;ng h&amp;oacute;a cho thị trường v&amp;agrave; t&amp;iacute;ch cực tham gia t&amp;aacute;i cơ cấu TTCK.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Kết th&amp;uacute;c hội nghị, Chủ tịch HĐQT ki&amp;ecirc;m Tổng Gi&amp;aacute;m đốc Trần Văn Dũng đ&amp;atilde; ph&amp;aacute;t biểu tiếp thu c&amp;aacute;c &amp;yacute; kiến chỉ đạo của L&amp;atilde;nh đạo BTC, UBCKNN v&amp;agrave; mong muốn Sở GDCK H&amp;agrave; Nội tiếp tục nhận được sự ủng hộ của c&amp;aacute;c cấp quản l&amp;yacute;, c&amp;aacute;c đơn vị li&amp;ecirc;n quan v&amp;agrave; th&amp;agrave;nh vi&amp;ecirc;n thị trường để c&amp;oacute; thể tạo được nhiều bước đột ph&amp;aacute;t trong hoạt động của Sở nhằm g&amp;oacute;p phần th&amp;uacute;c đẩy sự ph&amp;aacute;t triển của thị trường chứng kho&amp;aacute;n Việt Nam.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: right;&quot;&gt;\r\n	&lt;em&gt;&lt;strong&gt;Nguồn: Cổng Th&amp;ocirc;ng tin điện tử Bộ T&amp;agrave;i ch&amp;iacute;nh&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n', NULL, NULL, 1, '2014-03-28 15:52:49', 1, NULL, 1, 0);
INSERT INTO `news` (`id`, `menu_id`, `title`, `caption`, `detail`, `title_eng`, `caption_eng`, `detail_eng`, `thumb_image_path`, `list_file_attach`, `create_user_id`, `create_date`, `feature_flag`, `update_date`, `is_public`, `del_flg`) VALUES
(14, 28, 'CÔNG BỐ THÔNG TIN CHỨNG NHẬN ĐỦ ĐIỀU KIỆN KIN', '........', '&lt;div class=&quot;itemIntroText&quot; style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;p style=&quot;margin: 12pt 0cm 6pt;&quot;&gt;\r\n		&amp;nbsp;&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;itemFullText&quot; style=&quot;color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		&amp;nbsp;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		&lt;img alt=&quot;logo&quot; height=&quot;66&quot; src=&quot;http://aasc.com.vn/web/images/logo.png&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px; float: left;&quot; width=&quot;141&quot; /&gt;&lt;span style=&quot;line-height: 1.3em;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		&lt;strong style=&quot;line-height: 1.3em;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;C&amp;Ocirc;NG BỐ TH&amp;Ocirc;NG TIN CHỨNG NHẬN ĐỦ ĐIỀU KIỆN&amp;nbsp;&lt;/strong&gt;&lt;span style=&quot;line-height: 1.3em;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		&lt;span style=&quot;line-height: 1.3em;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		&amp;nbsp;&lt;img alt=&quot;Hinh anh bo tro-04&quot; height=&quot;22&quot; src=&quot;http://aasc.com.vn/web/images/Hinh_anh_bo_tro-04.png&quot; style=&quot;max-width: 100%; height: auto; vertical-align: middle; border: 0px;&quot; width=&quot;143&quot; /&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;strong&gt;ĐĂNG K&amp;Yacute; KINH DOANH&amp;nbsp;DỊCH VỤ KIỂM TO&amp;Aacute;N&lt;/strong&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 12pt 0cm 6pt;&quot;&gt;\r\n		&lt;span style=&quot;line-height: 12.649999618530273px; font-family: &#039;UTM Facebook K&amp;amp;&#039;;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		&lt;strong&gt;C&amp;Ocirc;NG TY TNHH H&amp;Atilde;NG KIỂM TO&amp;Aacute;N AASC&lt;/strong&gt;&amp;nbsp;tiền th&amp;acirc;n l&amp;agrave; C&amp;ocirc;ng ty Dịch vụ Tư vấn T&amp;agrave;i ch&amp;iacute;nh Kế to&amp;aacute;n v&amp;agrave; Kiểm to&amp;aacute;n (AASC) - Bộ T&amp;agrave;i ch&amp;iacute;nh l&amp;agrave; một trong hai C&amp;ocirc;ng ty đầu ti&amp;ecirc;n v&amp;agrave; lớn nhất của ng&amp;agrave;nh Kiểm to&amp;aacute;n độc lập Việt Nam được th&amp;agrave;nh lập năm 1991. Theo số liệu của Hội Kiểm to&amp;aacute;n vi&amp;ecirc;n H&amp;agrave;nh nghề Việt Nam (VACPA), AASC hiện nay được xếp hạng thứ 5 về doanh thu, thứ Nh&amp;igrave; về kh&amp;aacute;ch h&amp;agrave;ng v&amp;agrave; thứ Nhất về doanh thu dịch vụ kiểm to&amp;aacute;n quyết to&amp;aacute;n Dự &amp;aacute;n đầu tư x&amp;acirc;y dựng.&lt;/p&gt;\r\n	&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n		Thực hiện Th&amp;ocirc;ng tư số 203/2012/TT-BTC ng&amp;agrave;y 19/11/2012 về tr&amp;igrave;nh tự, thủ tục cấp, quản l&amp;yacute;, sử dụng Giấy chứng nhận đủ điều kiện kinh doanh dịch vụ kiểm to&amp;aacute;n, ng&amp;agrave;y 30/10/2013, Bộ T&amp;agrave;i ch&amp;iacute;nh đ&amp;atilde; cấp Giấy Chứng nhận&amp;nbsp;&lt;strong&gt;H&amp;atilde;ng Kiểm to&amp;aacute;n AASC&lt;/strong&gt;&amp;nbsp;đủ điều kiện kinh doanh dịch vụ kiểm to&amp;aacute;n với th&amp;ocirc;ng tin c&amp;ocirc;ng bố như sau:&lt;/p&gt;\r\n	&lt;table border=&quot;1&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; style=&quot;max-width: 100%; background-color: transparent; border-collapse: collapse; border-spacing: 0px; width: 637px; border: none; height: 264px;&quot;&gt;\r\n		&lt;tbody&gt;\r\n			&lt;tr&gt;\r\n				&lt;td style=&quot;line-height: 24px; padding: 0cm 5.4pt; text-align: left; vertical-align: top; width: 182.6pt; border: none;&quot; valign=&quot;top&quot; width=&quot;243&quot;&gt;\r\n					&lt;p style=&quot;margin: 2pt 0cm;&quot;&gt;\r\n						&lt;span style=&quot;position: relative; font-size: 8px; line-height: 0; vertical-align: baseline; bottom: -0.25em;&quot;&gt;&lt;span style=&quot;font-size: 12pt; font-family: &#039;Times New Roman&#039;, serif;&quot;&gt;&lt;span style=&quot;font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 11px; line-height: normal;&quot;&gt;T&amp;ecirc;n doanh nghiệp&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n				&lt;/td&gt;\r\n				&lt;td style=&quot;line-height: 24px; padding: 0cm 5.4pt; text-align: left; vertical-align: top; width: 297.7pt; border: none;&quot; valign=&quot;top&quot; width=&quot;397&quot;&gt;\r\n					&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n						&lt;strong&gt;C&amp;Ocirc;NG TY TNHH H&amp;Atilde;NG KIỂM TO&amp;Aacute;N AASC&lt;/strong&gt;&lt;/p&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td style=&quot;line-height: 24px; padding: 0cm 5.4pt; text-align: left; vertical-align: top; width: 182.6pt; border: none;&quot; valign=&quot;top&quot; width=&quot;243&quot;&gt;\r\n					&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n						&lt;span style=&quot;line-height: 1.3em;&quot;&gt;T&amp;ecirc;n bằng tiếng nước ngo&amp;agrave;i&lt;/span&gt;&lt;/p&gt;\r\n				&lt;/td&gt;\r\n				&lt;td style=&quot;line-height: 24px; padding: 0cm 5.4pt; text-align: left; vertical-align: top; width: 297.7pt; border: none;&quot; valign=&quot;top&quot; width=&quot;397&quot;&gt;\r\n					&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n						&lt;strong style=&quot;line-height: 1.3em;&quot;&gt;AASC Auditing Firm Company Limited&lt;/strong&gt;&lt;/p&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td style=&quot