INSERT INTO `news` (`id`, `menu_id`, `title`, `caption`, `detail`, `title_eng`, `caption_eng`, `detail_eng`, `thumb_image_path`, `list_file_attach`, `create_user_id`, `create_date`, `feature_flag`, `update_date`, `is_public`, `del_flg`) VALUES
(17, 29, 'Quy định mới', 'THÔNG TƯ 07/2014/TT-BTC\r\n\r\nQuy định chi tiết và hướng dẫn thực hiện việc xử phạt vi phạm hành chính trong lĩnh vực quản lý, sử dụng tài sản nhà nước theo quy định tại Nghị định số 192/2013/NĐ-CP ngày 21/11/2013 của Chính phủ', '&lt;h2 style=&quot;margin: 8px 0px 16px; font-family: Arial, Verdana, Tahoma, Helvetica, sans-serif; font-weight: normal; line-height: 1; color: rgb(238, 92, 36); text-rendering: optimizelegibility; font-size: 20px;&quot;&gt;\r\n	&lt;a href=&quot;http://aasc.com.vn/web/index.php/tin-tuc/quy-dinh-moi/157-thong-tu-07-2014-tt-btc&quot; style=&quot;color: rgb(34, 34, 34); text-decoration: none; font-size: 12px; text-transform: uppercase;&quot;&gt;TH&amp;Ocirc;NG TƯ 07/2014/TT-BTC&lt;/a&gt;&lt;/h2&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span style=&quot;line-height: 1.3em;&quot;&gt;Quy định chi tiết v&amp;agrave; hướng dẫn thực hiện việc xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước theo quy định tại Nghị định số 192/2013/NĐ-CP ng&amp;agrave;y 21/11/2013 của Ch&amp;iacute;nh phủ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Căn cứ Nghị định số 81/2013/NĐ-CP ng&amp;agrave;y 19 th&amp;aacute;ng 7 năm 2013 của Ch&amp;iacute;nh phủ quy định ch&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tiết một số điều v&amp;agrave; biện ph&amp;aacute;p th&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;h&amp;agrave;nh Luật xử l&amp;yacute; vi phạm h&amp;agrave;nh ch&amp;iacute;nh;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Căn cứ Nghị định số 192/20&lt;/span&gt;1&lt;span lang=&quot;VI&quot;&gt;3/NĐ-CP ng&amp;agrave;y 21 th&amp;aacute;ng 11 năm 20&lt;/span&gt;1&lt;span lang=&quot;VI&quot;&gt;3 của Ch&amp;iacute;nh phủ quy định việc xử phạt vi phạm h&amp;agrave;nh ch&lt;/span&gt;&amp;iacute;&lt;span lang=&quot;VI&quot;&gt;nh trong&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;ĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước; thực h&amp;agrave;nh tiết kiệm chống&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;&amp;atilde;ng ph&amp;iacute;; dự trữ quốc gia; kho bạc nh&amp;agrave; nước;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Căn cứ Nghị định s&lt;/span&gt;ố 11&lt;span lang=&quot;VI&quot;&gt;8/2008/NĐ-CP ng&amp;agrave;y 27 th&amp;aacute;ng 11 năm 2008 của Ch&amp;iacute;nh phủ quy định chức n&lt;/span&gt;ă&lt;span lang=&quot;VI&quot;&gt;ng, nhiệm vụ, quyền hạn v&amp;agrave;&amp;nbsp;&lt;/span&gt;cơ cấu tổ chức&lt;span lang=&quot;VI&quot;&gt;của Bộ&amp;nbsp;&lt;/span&gt;T&amp;agrave;i ch&amp;iacute;nh&lt;span lang=&quot;VI&quot;&gt;;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Theo đề nghị của Cục trưởng Cục Quản&amp;nbsp;&lt;/span&gt;l&amp;yacute;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;c&amp;ocirc;ng sản,&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Bộ trưởng Bộ T&amp;agrave;i ch&lt;/span&gt;&amp;iacute;&lt;span lang=&quot;VI&quot;&gt;nh ban h&amp;agrave;nh Th&amp;ocirc;ng tư quy định chi tiết v&amp;agrave; hướng dẫn thực hiện việc xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;ĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước theo quy định tại Nghị định s&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;192/2013/NĐ-CP ng&amp;agrave;y 21 th&amp;aacute;ng 11 năm 2013 của Ch&amp;iacute;nh phủ quy định việc xử phạt vi phạm h&amp;agrave;nh ch&lt;/span&gt;&amp;iacute;&lt;span lang=&quot;VI&quot;&gt;nh trong lĩnh vực quản l&lt;/span&gt;&amp;yacute;&lt;span lang=&quot;VI&quot;&gt;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước; thực h&amp;agrave;nh tiết kiệm chống l&amp;atilde;ng ph&lt;/span&gt;&amp;iacute;&lt;span lang=&quot;VI&quot;&gt;; dự trữ quốc gia; kho bạc nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Chương&amp;nbsp;&lt;/span&gt;1.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;QUY ĐỊNH CHUNG&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 1. Phạm vi điều chỉnh&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Th&amp;ocirc;ng tư n&amp;agrave;y quy định chi tiết v&amp;agrave; hướng dẫn thực hiện việc x&lt;/span&gt;ử&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước theo quy định tại Nghị định số 192/2013/NĐ-CP ng&amp;agrave;y 21 th&amp;aacute;ng 11 năm 2013 của Ch&amp;iacute;nh phủ quy định việc xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;ĩnh vực quản l&amp;yacute;, s&lt;/span&gt;ử&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;dụng t&amp;agrave;i sản nh&amp;agrave; nước; thực h&amp;agrave;nh tiết kiệm chống l&amp;atilde;ng ph&amp;iacute;; dự trữ quốc gia; kho bạc nh&amp;agrave; nước (sau đ&amp;acirc;y gọi tắt l&amp;agrave; Nghị định số 192/2013/NĐ-CP).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 2. Đố&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tượng &amp;aacute;p dụng&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Cơ quan, tổ chức, đơn vị (sau đ&amp;acirc;y gọi chung l&amp;agrave; tổ chức), c&amp;aacute; nh&amp;acirc;n c&amp;oacute; h&amp;agrave;nh vi vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Người c&amp;oacute; thẩm quyền xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;C&amp;aacute;c đối tượng kh&amp;aacute;c c&amp;oacute; li&amp;ecirc;n quan đến xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Đ&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ều 3. Nguy&amp;ecirc;n tắc x&amp;aacute;c định h&amp;igrave;nh thức xử phạt v&amp;agrave; mức phạt tiền&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;igrave;nh thức xử phạt cảnh c&amp;aacute;o được &amp;aacute;p dụng trong c&amp;aacute;c trường hợp sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Xử phạt h&amp;agrave;nh vi quy định tại khoản 1 Điều 16 Nghị định số 192/2013/NĐ-CP;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với c&amp;aacute;c h&amp;agrave;nh vi được &amp;aacute;p dụng một trong hai h&amp;igrave;nh thức xử phạt ch&amp;iacute;nh (phạt cảnh c&amp;aacute;o hoặc phạt ti&lt;/span&gt;ề&lt;span lang=&quot;VI&quot;&gt;n) quy định khoản 1 Đi&lt;/span&gt;ề&lt;span lang=&quot;VI&quot;&gt;u 13, đi&lt;/span&gt;ể&lt;span lang=&quot;VI&quot;&gt;m a khoản 4 Đi&lt;/span&gt;ề&lt;span lang=&quot;VI&quot;&gt;u 14, khoản 1 Điều 15, khoản 2 Điều 16, khoản 7 Điều 18, khoản 1 Điều 19, khoản 4 Điều 19, khoản 5 Điều 19, khoản 1 Điều 20, khoản 1 v&amp;agrave; khoản 2 Điều 21 Nghị định số 192/2013/NĐ-CP th&lt;/span&gt;&amp;igrave;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;h&amp;igrave;nh thức xử phạt cảnh c&amp;aacute;o được &amp;aacute;p dụng đối với vi phạm lần đầu v&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; t&amp;igrave;nh tiết t&lt;/span&gt;ă&lt;span lang=&quot;VI&quot;&gt;ng nặng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với những h&amp;agrave;nh vi chỉ quy định h&amp;igrave;nh thức phạt tiền hoặc kh&amp;ocirc;ng đủ điều kiện &amp;aacute;p dụng h&amp;igrave;nh thức xử phạt cảnh c&amp;aacute;o, mức phạt tiền cụ thể được x&amp;aacute;c định như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Mức tiền phạt cụ thể đối với một h&amp;agrave;nh vi vi phạm h&amp;agrave;nh ch&amp;iacute;nh kh&amp;ocirc;ng c&amp;oacute; t&amp;igrave;nh tiết tăng nặng hoặc giảm nhẹ l&amp;agrave; mức trung b&amp;igrave;nh của khung tiền phạt được quy định đối với h&amp;agrave;nh vi đ&amp;oacute;. Mức trung b&amp;igrave;nh của khung tiền phạt được x&amp;aacute;c định b&lt;/span&gt;ằ&lt;span lang=&quot;VI&quot;&gt;ng trung b&amp;igrave;nh giữa mức tối đa của khung h&amp;igrave;nh phạt v&amp;agrave; mức tối thiểu của khung h&amp;igrave;nh phạt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Cứ một t&amp;igrave;nh tiết giảm nhẹ hoặc tăng nặng th&amp;igrave; mức tiền phạt được giảm hoặc tăng 20% so với mức trung b&amp;igrave;nh của khung tiền phạt được quy định đ&lt;/span&gt;ố&lt;span lang=&quot;VI&quot;&gt;i với h&amp;agrave;nh vi đ&amp;oacute;, nhưng kh&amp;ocirc;ng được thấp h&lt;/span&gt;ơn&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;mức tối thiểu v&amp;agrave; kh&amp;ocirc;ng được cao hơn mức tối đa của khung h&amp;igrave;nh phạt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Khi x&amp;aacute;c định mức phạt tiền đối với h&amp;agrave;nh vi vi phạm vừa c&amp;oacute; t&amp;igrave;nh tiết giảm nhẹ, vừa c&amp;oacute; t&amp;igrave;nh tiết tăng nặng th&amp;igrave; xem x&amp;eacute;t giảm trừ t&amp;igrave;nh ti&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;t tăng nặng theo nguy&amp;ecirc;n tắc c&amp;oacute; một t&amp;igrave;nh tiết giảm nhẹ được giảm trừ một t&amp;igrave;nh&amp;nbsp;&lt;/span&gt;tiết&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tăng nặng. Sau khi&amp;nbsp;&lt;/span&gt;g&lt;span lang=&quot;VI&quot;&gt;iảm trừ, nếu c&amp;ograve;n t&amp;igrave;nh tiết giảm nhẹ hoặc tăng nặng th&amp;igrave; &amp;aacute;p dụng theo quy định tại&amp;nbsp;&lt;/span&gt;điểm&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;b khoản n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Nguồn kinh ph&amp;iacute; nộp phạt của tổ chức, c&amp;aacute; nh&amp;acirc;n bị x&lt;/span&gt;ử&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;phạt vi phạm kh&amp;ocirc;ng được sử dụng tiền ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước hoặc tiền c&amp;oacute; nguồn gốc từ ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước để nộp phạt v&amp;agrave; khắc phục hậu quả do h&amp;agrave;nh vi vi phạm của m&amp;igrave;nh g&amp;acirc;y ra. Trường hợp xử phạt tổ chức th&amp;igrave; sau khi chấp h&amp;agrave;nh quyết định xử phạt, t&lt;/span&gt;ổ&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;chức bị xử phạt x&amp;aacute;c định c&amp;aacute; nh&amp;acirc;n c&amp;oacute;&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;ỗi g&amp;acirc;y ra vi phạm h&amp;agrave;nh ch&amp;iacute;nh đ&lt;/span&gt;ể&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;x&amp;aacute;c định tr&amp;aacute;ch nhiệm ph&amp;aacute;p l&amp;yacute;, bao gồm cả việc nộp lại khoản tiền phạt v&amp;agrave; khắc phục hậu quả do h&amp;agrave;nh vi vi phạm của m&amp;igrave;nh g&amp;acirc;y ra tương ứng với mức độ vi phạm của c&amp;aacute; nh&amp;acirc;n đ&amp;oacute; theo quy định của ph&amp;aacute;p luật v&amp;agrave; Quy ch&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;qu&lt;/span&gt;ả&lt;span lang=&quot;VI&quot;&gt;n l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước của t&lt;/span&gt;ổ&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;chức.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 4. Lập b&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;&amp;ecirc;n bản xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Khi ph&amp;aacute;t hiện h&amp;agrave;nh vi vi phạm, người c&amp;oacute; thẩm quyền xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh&amp;nbsp;&lt;/span&gt;tr&lt;span lang=&quot;VI&quot;&gt;ong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước, c&amp;ocirc;ng chức được giao nhiệm vụ kiểm tra việc chấp h&amp;agrave;nh ph&amp;aacute;p luật về quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước quy định tại khoản 1 Điều 22 Nghị định số 192/2013/NĐ-CP phải lập bi&amp;ecirc;n bản ngay tại thời điểm ph&amp;aacute;t hiện h&amp;agrave;nh vi vi phạm.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Bi&amp;ecirc;n bản phải lập theo đ&amp;uacute;ng quy định tại M&lt;/span&gt;ẫ&lt;span lang=&quot;VI&quot;&gt;u Bi&amp;ecirc;n bản số 01 k&amp;egrave;m theo Nghị định số 81/2013/NĐ-CP ng&amp;agrave;y 19 th&amp;aacute;ng 7 năm 2013 của Ch&amp;iacute;nh phủ quy định chi tiết một số điều v&amp;agrave; biện ph&amp;aacute;p thi h&amp;agrave;nh Luật xử l&amp;yacute; vi phạm h&amp;agrave;nh ch&amp;iacute;nh (sau đ&amp;acirc;y gọi tắt l&amp;agrave; Nghị định số 81/2013/NĐ-CP).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Chương&amp;nbsp;&lt;/span&gt;2.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;XỬ PHẠT VI PHẠM H&amp;Agrave;NH CH&amp;Iacute;NH TRONG QUẢN L&amp;Yacute;, SỬ DỤNG T&amp;Agrave;I SẢN NH&amp;Agrave; NƯỚC TẠI CƠ QUAN, TỔ CHỨC, ĐƠN VỊ&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Đ&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ều 5. H&amp;agrave;nh vi vi phạm quy định về mua sắm t&amp;agrave;i sản nh&amp;agrave; nước&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về mua sắm t&amp;agrave;i sản nh&amp;agrave; nước được thực hiện theo quy định tại Điều 5 Nghị định số 192/2013/NĐ-CP. Một số nội dung được quy định cụ thể nh&lt;/span&gt;ư&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi thực hiện mua sắm t&amp;agrave;i sản khi chưa c&amp;oacute; quyết định của cấp c&amp;oacute; thẩm quyền quy định tại khoản 1 Điều 5 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi tại thời điểm thực hiện mua sắm chưa c&amp;oacute; quyết định về mua sắm t&amp;agrave;i sản nh&amp;agrave; nước của cấp c&amp;oacute; thẩm quyền. Thẩm quyền quyết định mua sắm t&amp;agrave;i sản nh&amp;agrave; nước thực hiện theo quy định của Luật quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave; c&amp;aacute;c văn bản quy định chi ti&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;t, hướng d&lt;/span&gt;ẫ&lt;span lang=&quot;VI&quot;&gt;n thi h&amp;agrave;nh;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi kh&amp;ocirc;ng thực hiện mua sắm tập trung đối với c&amp;aacute;c loại t&amp;agrave;i sản thuộc danh mục mua sắm tập trung quy định tại khoản 2 Điều 5 Nghị định số 192/2013/NĐ-CP v&amp;agrave; được x&amp;aacute;c định theo từng lần mua sắm;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi mua sắm t&amp;agrave;i sản vượt ti&amp;ecirc;u chuẩn, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; th&lt;/span&gt;ẩ&lt;span lang=&quot;VI&quot;&gt;m quyền quy định tại khoản 3 Điều 5 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi mua sắm t&amp;agrave;i sản vượt về diện t&amp;iacute;ch (đối với trụ sở l&amp;agrave;m việc), vượt về số lượng, vượt về mức gi&amp;aacute; (đối với phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc) so với ti&amp;ecirc;u chuẩn, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền quy định;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	4.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc x&amp;aacute;c định phần gi&amp;aacute; trị vượt so với ti&amp;ecirc;u chuẩn, định mức để l&amp;agrave;m căn cứ xử phạt được quy định như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường h&lt;/span&gt;ợ&lt;span lang=&quot;VI&quot;&gt;p mua sắm t&amp;agrave;i sản l&amp;agrave; phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc vượt về s&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;lượng so với ti&amp;ecirc;u chuẩn, định mức: Gi&amp;aacute; trị vượt/01 đơn vị t&amp;agrave;i sản được x&amp;aacute;c định bằng đơn gi&amp;aacute; của t&amp;agrave;i sản đ&amp;oacute; theo gi&amp;aacute; mua ghi tr&amp;ecirc;n h&amp;oacute;a đơn hoặc hợp đồng mua sắm t&amp;agrave;i sản;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp mua sắm t&amp;agrave;i sản l&amp;agrave; phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc đ&amp;uacute;ng về số lượng nhưng vượt về mức gi&amp;aacute; so với ti&amp;ecirc;u chuẩn, định mức: Gi&amp;aacute; trị vượt được x&amp;aacute;c định bằng gi&amp;aacute; trị của t&amp;agrave;i sản theo gi&amp;aacute; mua ghi tr&amp;ecirc;n h&amp;oacute;a đơn hoặc hợp đ&lt;/span&gt;ồ&lt;span lang=&quot;VI&quot;&gt;ng mua s&lt;/span&gt;ắ&lt;span lang=&quot;VI&quot;&gt;m t&amp;agrave;i sản trừ đi (-) gi&amp;aacute; trị t&amp;agrave;i sản theo ti&amp;ecirc;u chu&lt;/span&gt;ẩ&lt;span lang=&quot;VI&quot;&gt;n, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền quy định;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp mua sắm trụ sở l&amp;agrave;m việc vượt ti&amp;ecirc;u chuẩn, định mức: Gi&amp;aacute; trị vượt được x&amp;aacute;c định bằng ch&amp;ecirc;nh lệch giữa diện t&amp;iacute;ch thực tế trừ đi (-)&lt;/span&gt;d&lt;span lang=&quot;VI&quot;&gt;iện t&amp;iacute;ch được sử dụng theo ti&amp;ecirc;u chuẩn, định mức nh&amp;acirc;n với (x) đơn gi&amp;aacute; mua b&amp;igrave;nh qu&amp;acirc;n theo h&amp;oacute;a đ&lt;/span&gt;ơn&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;(hoặc theo h&lt;/span&gt;ợ&lt;span lang=&quot;VI&quot;&gt;p đồng mua sắm t&amp;agrave;i sản).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Đ&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ều 6. H&amp;agrave;nh vi vi phạm quy định về thu&amp;ecirc; t&amp;agrave;i sản&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về thu&amp;ecirc; t&amp;agrave;i sản được thực hiện theo quy định tại Điều 6 Nghị định số 192/2013/NĐ-CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi thực hiện thu&amp;ecirc; t&amp;agrave;i sản khi chưa c&amp;oacute; quyết định của cấp c&amp;oacute; thẩm quyền quy định tại khoản 1 Điều 6 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi k&amp;yacute; kết hợp đồng thu&amp;ecirc; t&amp;agrave;i sản khi chưa c&amp;oacute; quyết định về thu&amp;ecirc; t&amp;agrave;i sản của cấp c&amp;oacute; thẩm quyền.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Thẩm quyền quyết định thu&amp;ecirc; t&amp;agrave;i sản thực hiện theo quy định của Luật quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave; c&amp;aacute;c văn bản quy định chi tiết,&lt;/span&gt;hướng dẫn&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;thi h&amp;agrave;nh;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi thu&amp;ecirc; t&amp;agrave;i sản vượt ti&amp;ecirc;u chuẩn, định mức quy định tại khoản 1 Điều 6 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi thu&amp;ecirc; t&amp;agrave;i sản để phục vụ hoạt động của tổ chức vượt về diện t&amp;iacute;ch (đối với trụ sở l&amp;agrave;m việc), vượt về số lượng (đối với phương ti&lt;/span&gt;ệ&lt;span lang=&quot;VI&quot;&gt;n đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc) so với ti&amp;ecirc;u chu&lt;/span&gt;ẩ&lt;span lang=&quot;VI&quot;&gt;n, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền quy định;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi lựa chọn nh&amp;agrave; cung cấp dịch vụ cho thu&amp;ecirc; t&amp;agrave;i sản kh&amp;ocirc;ng đ&amp;uacute;ng h&amp;igrave;nh thức, tr&amp;igrave;nh tự quy định tại khoản 1 Điều 6 Nghị định số 192/2013/NĐ-CP bao gồm:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi kh&amp;ocirc;ng th&amp;ocirc;ng b&amp;aacute;o c&amp;ocirc;ng khai nhu cầu thu&amp;ecirc; t&amp;agrave;i sản tr&amp;ecirc;n c&amp;aacute;c phương tiện th&amp;ocirc;ng tin đại ch&amp;uacute;ng theo quy định của ph&amp;aacute;p luật về quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi kh&amp;ocirc;ng thực hiện đấu thầu khi lựa ch&lt;/span&gt;ọ&lt;span lang=&quot;VI&quot;&gt;n nh&amp;agrave; cung cấp dịch vụ cho thu&amp;ecirc; t&lt;/span&gt;&amp;agrave;&lt;span lang=&quot;VI&quot;&gt;i sản đối với c&amp;aacute;c trường hợp theo quy định của ph&amp;aacute;p luật phải thực hiện đấu thầu.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	4.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Gi&amp;aacute;&amp;nbsp;&lt;/span&gt;tr&lt;span lang=&quot;VI&quot;&gt;ị hợp đồng thu&amp;ecirc; t&amp;agrave;i sản để l&amp;agrave;m căn cứ xử phạt được x&amp;aacute;c định như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp trong hợp đồng thu&amp;ecirc; t&amp;agrave;i sản ghi cụ thể tổng gi&amp;aacute; trị hợp đồng th&amp;igrave; x&amp;aacute;c định theo t&lt;/span&gt;ổ&lt;span lang=&quot;VI&quot;&gt;ng gi&amp;aacute;&amp;nbsp;&lt;/span&gt;tr&lt;span lang=&quot;VI&quot;&gt;ị ghi trong hợp đ&lt;/span&gt;ồ&lt;span lang=&quot;VI&quot;&gt;ng;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp trong hợp đồng thu&amp;ecirc; t&amp;agrave;i sản ghi số lượng (khối lượng), đơn gi&amp;aacute; v&amp;agrave; thời hạn thu&amp;ecirc; th&lt;/span&gt;&amp;igrave;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tổng gi&amp;aacute; trị hợp đồng được x&amp;aacute;c định bằng (=) số lượng (khối lượng) nh&amp;acirc;n với (x) đơn gi&amp;aacute; thu&amp;ecirc; nh&amp;acirc;n với (x) thời hạn thu&amp;ecirc; ghi trong hợp đồng;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp trong hợp đồng kh&amp;ocirc;ng ghi r&amp;otilde; thời hạn thu&amp;ecirc; th&amp;igrave;&amp;nbsp;&lt;/span&gt;gi&amp;aacute;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;trị hợp đồng thu&amp;ecirc; được t&amp;iacute;nh theo số tiền thu&amp;ecirc; t&amp;agrave;i sản thực tế đ&amp;atilde; trả kể cả ph&lt;/span&gt;ầ&lt;span lang=&quot;VI&quot;&gt;n tiền thu&amp;ecirc; trả trước nếu c&amp;oacute; t&amp;iacute;nh đến thời điểm ra quyết định xử phạt h&amp;agrave;nh ch&amp;iacute;nh&lt;/span&gt;.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 7. H&amp;agrave;nh vi b&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tr&amp;iacute;, sử dụng t&amp;agrave;&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;sản nh&amp;agrave; n&lt;/span&gt;ướ&lt;span lang=&quot;VI&quot;&gt;c vượt ti&amp;ecirc;u chuẩn, định mức&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Vi&lt;/span&gt;ệ&lt;span lang=&quot;VI&quot;&gt;c xử phạt h&amp;agrave;nh vi bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước vượt t&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;&amp;ecirc;u chuẩn, định mức được thực hiện theo quy định tại khoản 1 Điều 7 Nghị định số 192/2013/NĐ- CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi b&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tr&amp;iacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước vượt ti&amp;ecirc;u chuẩn, định mức quy định tại khoản 1 Điều 7 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước cho người sử dụng, bộ phận sử dụng đ&amp;uacute;ng đối tượng nhưng vượt về diện t&amp;iacute;ch (đối với trụ sở l&amp;agrave;m việc), vượt về số lượng hoặc mức gi&amp;aacute; (đối với phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c,&amp;nbsp;&lt;/span&gt;tr&lt;span lang=&quot;VI&quot;&gt;ang thi&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;t bị l&amp;agrave;m việc);&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc x&amp;aacute;c định gi&amp;aacute; trị vượt so với ti&amp;ecirc;u chuẩn, định mức được quy định như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp b&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tr&amp;iacute;, sử dụng t&amp;agrave;i sản l&amp;agrave; phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc vượt về số lượng so với ti&amp;ecirc;u chu&lt;/span&gt;ẩ&lt;span lang=&quot;VI&quot;&gt;n, định mức: Gi&amp;aacute; trị vượt được x&amp;aacute;c định bằng số lượng t&amp;agrave;i sản vượt nh&amp;acirc;n với (x) nguy&amp;ecirc;n gi&amp;aacute; của t&amp;agrave;i sản&amp;nbsp;&lt;/span&gt;tr&lt;span lang=&quot;VI&quot;&gt;&amp;ecirc;n sổ kế to&amp;aacute;n; trường hợp tr&amp;ecirc;n sổ kế to&amp;aacute;n kh&amp;ocirc;ng theo d&amp;otilde;i nguy&amp;ecirc;n gi&amp;aacute; t&amp;agrave;i sản th&amp;igrave; l&lt;/span&gt;ấ&lt;span lang=&quot;VI&quot;&gt;y theo gi&amp;aacute; trị mua sắm mới t&amp;agrave;i sản c&amp;ugrave;ng&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;oại hoặc c&amp;oacute; ti&amp;ecirc;u chuẩn kỹ thuật, c&amp;ocirc;ng năng sử dụng tương đương tr&amp;ecirc;n thị trường tại thời điểm x&lt;/span&gt;ả&lt;span lang=&quot;VI&quot;&gt;y ra vi phạm;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản l&amp;agrave; phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc đ&amp;uacute;ng về số lượng nhưng vượt về mức gi&amp;aacute; so với ti&amp;ecirc;u chuẩn, định mức: Gi&amp;aacute; trị vượt được x&amp;aacute;c định bằng số lượng vượt nh&amp;acirc;n với (x) (ch&amp;ecirc;nh lệch giữa nguy&amp;ecirc;n gi&amp;aacute; của t&amp;agrave;i sản tr&amp;ecirc;n sổ kế to&amp;aacute;n trừ đi (-) gi&amp;aacute; trị t&amp;agrave;i sản theo ti&amp;ecirc;u chuẩn, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền quy định);&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp t&amp;agrave;i sản l&amp;agrave; trụ sở l&amp;agrave;m việc: Gi&amp;aacute; trị vượt được x&amp;aacute;c định bằng ch&amp;ecirc;nh lệch giữa diện t&amp;iacute;ch thực tế trừ đi (-) diện t&amp;iacute;ch được&amp;nbsp;&lt;/span&gt;sử dụng&lt;span lang=&quot;VI&quot;&gt;theo ti&amp;ecirc;u chuẩn, định mức nh&amp;acirc;n với (x) đơn gi&amp;aacute; b&amp;igrave;nh qu&amp;acirc;n theo sổ kế to&amp;aacute;n; trường hợp tr&amp;ecirc;n sổ kế to&amp;aacute;n kh&amp;ocirc;ng theo d&amp;otilde;i th&amp;igrave; đơn gi&amp;aacute; t&amp;iacute;nh theo suất đầu tư của nh&amp;agrave;, c&amp;ocirc;ng tr&amp;igrave;nh c&amp;oacute; ti&amp;ecirc;u chuẩn kỹ thuật tương đương do Bộ X&amp;acirc;y dựng ban h&amp;agrave;nh &amp;aacute;p dụng tại thời điểm h&amp;agrave;nh vi vi phạm xảy ra.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 8. H&amp;agrave;nh&amp;nbsp;&lt;/span&gt;vi&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;bố tr&amp;iacute;, sử dụng t&amp;agrave;&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng mục đ&amp;iacute;ch&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng mục đ&amp;iacute;ch quy định tại khoản 2 Điều 7 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng với mục đ&amp;iacute;ch, c&amp;ocirc;ng năng sử dụng của t&amp;agrave;i sản được đầu tư, trang bị, mua sắm. Một số trường hợp cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Bố tr&amp;iacute;, sử dụng trụ s&lt;/span&gt;ở l&lt;span lang=&quot;VI&quot;&gt;&amp;agrave;m việc, cơ sở hoạt động sự nghiệp l&amp;agrave;m nh&amp;agrave; ở hoặc sử dụng v&amp;agrave;o mục đ&amp;iacute;ch c&amp;aacute; nh&amp;acirc;n;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Bố tr&amp;iacute;, sử dụng xe &amp;ocirc; t&amp;ocirc; đưa đ&amp;oacute;n từ nơi ở đến nơi&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;&amp;agrave;m việc đối với chức danh kh&amp;ocirc;ng c&amp;oacute; ti&amp;ecirc;u chu&lt;/span&gt;ẩ&lt;span lang=&quot;VI&quot;&gt;n; bố tr&amp;iacute;, sử dụng xe &amp;ocirc; t&amp;ocirc; phục vụ c&amp;ocirc;ng t&amp;aacute;c cho c&amp;aacute;c chức danh kh&amp;ocirc;ng c&amp;oacute; ti&amp;ecirc;u chuẩn sử dụng xe m&amp;agrave; kh&amp;ocirc;ng được cấp c&amp;oacute; thẩm quyền ph&amp;ecirc; duyệt; bố tr&amp;iacute;, sử dụng xe &amp;ocirc; t&amp;ocirc; v&amp;agrave;o mục đ&amp;iacute;ch c&amp;aacute; nh&amp;acirc;n;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Bố tr&amp;iacute;, sử dụng m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc, t&amp;agrave;i sản kh&amp;aacute;c v&amp;agrave;o mục đ&amp;iacute;ch c&amp;aacute; nh&amp;acirc;n.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với cơ quan nh&amp;agrave; nước, đơn vị sự nghiệp c&amp;ocirc;ng lập chưa tự chủ t&amp;agrave;i ch&amp;iacute;nh sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết th&amp;igrave; xử phạt theo quy định tại khoản 2 Điều 7 Nghị định số 192/2013/NĐ-CP v&amp;agrave; khoản 1 Điều n&amp;agrave;y; kh&amp;ocirc;ng xử phạt theo quy định tại Điều 12 Nghị định số 192/2013/NĐ-CP v&amp;agrave; Điều 13 Th&amp;ocirc;ng tư n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với đơn vị sự nghiệp c&amp;ocirc;ng lập tự chủ t&amp;agrave;i ch&amp;iacute;nh sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết kh&amp;ocirc;n&lt;/span&gt;g&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;đ&amp;uacute;ng quy định th&amp;igrave; bị xử phạt theo quy định tại Điều 12 Nghị định số 192/2013/NĐ-CP v&amp;agrave; Điều 13 Th&lt;/span&gt;&amp;ocirc;&lt;span lang=&quot;VI&quot;&gt;ng tư n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 9. H&amp;agrave;nh v&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;cho mượn t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&lt;/span&gt;&amp;uacute;&lt;span lang=&quot;VI&quot;&gt;ng quy định&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi cho mượn t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng quy định được thực hiện theo quy định tại Điều 8 Nghị định số 192/2013/NĐ-CP.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi cho mượn t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng quy định l&amp;agrave; h&amp;agrave;nh vi cho tổ chức, c&amp;aacute; nh&amp;acirc;n kh&amp;aacute;c sử dụng t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng ph&amp;acirc;n biệt c&amp;oacute; hợp đồng cho mượn hay kh&amp;ocirc;ng c&amp;oacute; hợp đồng cho mượn, kh&amp;ocirc;ng ph&amp;acirc;n biệt thời hạn cho mượn.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Đ&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ều 10. H&amp;agrave;nh vi trao đ&lt;/span&gt;ổ&lt;span lang=&quot;VI&quot;&gt;i, biếu, tặng cho t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng quy định&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi trao đổi, biếu, tặng cho t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng quy định được thực hiện theo quy định tại Điều 9 Nghị định số 192/2013/NĐ-CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi trao đổi t&amp;agrave;i sản nh&amp;agrave; nước quy định tại khoản 1 Điều 9 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi d&amp;ugrave;ng t&amp;agrave;i sản&amp;nbsp;&lt;/span&gt;nh&amp;agrave;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;nước của tổ chức đ&lt;/span&gt;ể&lt;span lang=&quot;VI&quot;&gt;đổi lấy t&amp;agrave;i sản của tổ chức, c&amp;aacute; nh&amp;acirc;n kh&amp;aacute;c m&amp;agrave; kh&amp;ocirc;ng được cấp c&amp;oacute; thẩm quyền cho ph&amp;eacute;p;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi biếu, tặng cho t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng quy định l&amp;agrave; h&amp;agrave;nh vi sử dụng t&amp;agrave;i sản nh&amp;agrave; nước để l&amp;agrave;m qu&amp;agrave; tặng vi phạm Quy chế tặng qu&amp;agrave; do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền ban h&amp;agrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 11. H&amp;agrave;nh vi lấn chiếm trụ s&lt;/span&gt;ở&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;l&amp;agrave;m việc, cơ sở hoạt động sự nghiệp&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi lấn chiếm trụ sở l&amp;agrave;m việc, cơ sở hoạt động sự nghiệp thực hiện theo quy định tại Điều 10 Nghị định 192/2013/NĐ-CP.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi lấn chiếm trụ sở l&amp;agrave;m việc, cơ sở hoạt động sự nghiệp quy định tại Điều 10 Nghị định số 192/2013/NĐ-CP bao gồm:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Tổ chức, c&amp;aacute; nh&amp;acirc;n đang sử dụng đất liền kề tự chuyển dịch mốc g&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ới thửa đất để mở rộng diện t&amp;iacute;ch đất hoặc x&amp;acirc;y dựng c&amp;ocirc;ng tr&amp;igrave;nh lấn chiếm sang kh&amp;ocirc;ng gian phần diện t&amp;iacute;ch đất thuộc khu&amp;ocirc;n vi&amp;ecirc;n trụ sở l&amp;agrave;m việc, cơ sở hoạt động sự nghiệp của cơ quan, tổ chức, đơn vị;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Tổ chức, c&amp;aacute; nh&amp;acirc;n tự&amp;nbsp;&lt;/span&gt;&amp;yacute;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;sử dụng nh&amp;agrave;, đất thuộc trụ sở l&amp;agrave;m việc, cơ sở hoạt động sự nghiệp của cơ quan, tổ chức, đơn vị.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 12. H&amp;agrave;nh vi chiếm đoạt t&amp;agrave;i sản&amp;nbsp;&lt;/span&gt;nh&amp;agrave; nước&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi chiếm đoạt t&amp;agrave;i sản nh&amp;agrave; nước được thực hiện theo quy định tại Điều 11 Nghị định s&lt;/span&gt;ố&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;192/2013/NĐ-CP.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi chiếm đoạt t&amp;agrave;i sản nh&amp;agrave; nước bị xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh l&amp;agrave; việc nắm g&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ữ, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước m&amp;agrave; kh&amp;ocirc;ng được cơ quan nh&amp;agrave; nước c&amp;oacute;&amp;nbsp;&lt;/span&gt;thẩm quyền&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;cho ph&amp;eacute;p nhưng chưa đến mức bị truy cứu tr&amp;aacute;ch nhiệm h&amp;igrave;nh sự.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Đ&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ều 13. H&amp;agrave;nh vi vi phạm quy định về sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết được thực hiện theo quy định tại Điều 12 Nghị định số 192/2013/NĐ-CP.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết khi chưa c&amp;oacute; quyết định của cấp c&amp;oacute; thẩm quyền quy định tại khoản 1 Điều 12 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết m&amp;agrave; tại thời điểm thực hiện h&amp;agrave;nh vi kh&amp;ocirc;ng c&amp;oacute; quyết định của cấp c&amp;oacute; thẩm quyền.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Thẩm quyền quyết định sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết thực hiện theo quy định của Luật quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave; c&amp;aacute;c văn bản quy định chi tiết, hướng dẫn thi h&amp;agrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 14. H&amp;agrave;nh vi vi phạm quy định về xử l&amp;yacute; t&amp;agrave;i sản nh&amp;agrave; nước&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về xử l&amp;yacute; t&amp;agrave;i sản nh&amp;agrave; nước được thực hiện theo quy định tại Điều 14 Nghị định s&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;192/2013/NĐ-CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi để hư hỏng, thất tho&amp;aacute;t t&amp;agrave;i sản quy định tại khoản 2 Điều 14 Nghị đ&lt;/span&gt;ị&lt;span lang=&quot;VI&quot;&gt;nh số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi l&amp;agrave;m mất m&amp;aacute;t t&amp;agrave;i sản hoặc l&amp;agrave;m t&amp;agrave;i sản bị hư hỏng; l&amp;agrave;m mất m&amp;aacute;t, thay đổi hoặc biến dạng linh kiện, phụ t&amp;ugrave;ng của t&amp;agrave;i sản;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi k&amp;ecirc; khai kh&amp;ocirc;ng đ&amp;uacute;ng danh mục, hiện trạng t&amp;agrave;i sản đề nghị xử l&amp;yacute; quy định tại khoản 4 Điều 14 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi k&amp;ecirc; khai kh&amp;ocirc;ng đ&amp;uacute;ng danh mục, hiện trạng t&amp;agrave;i sản đề nghị xử l&amp;yacute; d&lt;/span&gt;ẫ&lt;span lang=&quot;VI&quot;&gt;n đ&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;n việc quy&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;t định xử l&amp;yacute; t&amp;agrave;i sản kh&amp;ocirc;ng đ&amp;uacute;ng quy định;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc x&amp;aacute;c định số tiền phải nộp lại tương ứng với gi&amp;aacute; trị t&amp;agrave;i sản bị thất tho&amp;aacute;t, hư hỏng quy định tại điểm b khoản 5 Điều 14 Nghị định số 192/2013/NĐ- CP được quy định như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với t&amp;agrave;i sản bị mất hoặc hư hỏng kh&amp;ocirc;ng thể khắc phục được, số tiền phải nộp lại được x&amp;aacute;c định tương ứng với gi&amp;aacute; mua mới t&amp;agrave;i sản c&amp;ugrave;ng loại hoặc c&amp;oacute; ti&amp;ecirc;u chuẩn, c&amp;ocirc;ng năng sử dụng tương đương tr&amp;ecirc;n thị trường;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với t&amp;agrave;i sản bị hư hỏng c&amp;oacute; thể khắc phục được, số tiền phải nộp lại l&amp;agrave; chi ph&amp;iacute; để sửa chữa t&amp;agrave;i sản đ&amp;oacute;.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 15. H&amp;agrave;nh vi vi phạm quy định về tổ chức xử l&amp;yacute; t&amp;agrave;i sản nh&amp;agrave; nước&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về tổ chức x&lt;/span&gt;ử&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;l&amp;yacute; t&amp;agrave;i sản nh&amp;agrave; nước được thực hiện theo quy định tại Điều 15 Nghị định số 192/2013/NĐ-CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi qu&amp;aacute; thời hạn quy định m&amp;agrave; vẫn chưa triển khai thực hiện phương &amp;aacute;n xử l&amp;yacute; đ&amp;atilde; được cấp c&amp;oacute; thẩm quyền ph&amp;ecirc; duyệt quy định tại điểm a khoản 1 Điều 15 Nghị định số 192/2013/NĐ-CP được x&amp;aacute;c định theo thời hạn xử l&amp;yacute; quy định tại quyết định xử l&amp;yacute; t&amp;agrave;i sản. Trường hợp tại quyết định xử l&amp;yacute; t&amp;agrave;i sản kh&amp;ocirc;ng quy định thời hạn cụ thể, th&amp;igrave; thực hiện theo quy định của ph&amp;aacute;p luật c&amp;oacute; li&amp;ecirc;n quan. Trường hợp được cấp c&amp;oacute; thẩm quyền ph&amp;ecirc; duyệt phương &amp;aacute;n xử l&amp;yacute; gia hạn việc xử l&amp;yacute; t&amp;agrave;i sản, th&amp;igrave; thời hạn quy định được x&amp;aacute;c định theo thời hạn sau khi đ&amp;atilde; được gia hạn;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi kh&amp;ocirc;ng thực hiện hoặc thực hiện kh&amp;ocirc;ng đầy đủ việc th&amp;ocirc;ng b&amp;aacute;o c&amp;ocirc;ng khai b&amp;aacute;n đấu gi&amp;aacute; t&amp;agrave;i sản nh&amp;agrave; nước theo quy định của ph&amp;aacute;p luật về quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước quy định tại&amp;nbsp;&lt;/span&gt;điểm&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;c khoản 2 Điều 15 Nghị định s&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;192/2013/NĐ-CP bao gồm:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Kh&amp;ocirc;ng thực hiện ni&amp;ecirc;m yết về việc b&amp;aacute;n đấu gi&amp;aacute; t&amp;agrave;i sản nh&amp;agrave; nước tại những địa điểm quy định;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Kh&amp;ocirc;ng th&amp;ocirc;ng b&amp;aacute;o c&amp;ocirc;ng khai tr&amp;ecirc;n c&amp;aacute;c phương tiện th&amp;ocirc;ng tin đại ch&amp;uacute;ng hoặc th&amp;ocirc;ng b&amp;aacute;o nhưng kh&amp;ocirc;ng đủ thời gian đối với việc b&amp;aacute;n đấu gi&amp;aacute; t&amp;agrave;i sản nh&amp;agrave; nước theo quy định của ph&amp;aacute;p luật về quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Nội dung ni&amp;ecirc;m yết, th&amp;ocirc;ng b&amp;aacute;o c&amp;ocirc;ng khai kh&amp;ocirc;ng đầy đủ th&amp;ocirc;ng tin theo quy định của ph&amp;aacute;p luật về b&amp;aacute;n đấu gi&amp;aacute; t&amp;agrave;i sản.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 16. H&amp;agrave;nh vi vi phạm quy định về đăng nhập v&amp;agrave; sử dụng số liệu về t&amp;agrave;i sản nh&amp;agrave; nước&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về việc đăng nhập v&amp;agrave; sử dụng số liệu về t&amp;agrave;i sản nh&amp;agrave; nước được thực hiện theo quy định tại Điều 16 Nghị định số 192/2013/NĐ-CP.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Sử dụng số li&lt;/span&gt;ệ&lt;span lang=&quot;VI&quot;&gt;u về t&amp;agrave;i sản nh&amp;agrave; nước trong Cơ sở dữ liệu quốc gia về t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch c&amp;aacute; nh&amp;acirc;n m&amp;agrave; kh&amp;ocirc;n&lt;/span&gt;g&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;được cơ quan c&amp;oacute; thẩm quyền quản l&amp;yacute; cơ sở dữ liệu đ&amp;oacute; cho ph&amp;eacute;p quy định tại điểm&amp;nbsp;&lt;/span&gt;d&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;khoản 3 Điều 16 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi sử dụng th&amp;ocirc;ng tin lưu giữ trong Cơ sở dữ liệu quốc gia về t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o c&amp;aacute;c mục đ&amp;iacute;ch kh&amp;aacute;c ngo&amp;agrave;i c&amp;aacute;c mục đ&amp;iacute;ch được quy định tại Th&amp;ocirc;ng tư số 123/2011/TT-BTC ng&amp;agrave;y 31/8/2011 của Bộ T&amp;agrave;i ch&amp;iacute;nh hướng dẫn việc quản l&amp;yacute;, khai th&amp;aacute;c Phần mềm Quản l&amp;yacute; đăng k&amp;yacute; t&amp;agrave;i sản nh&amp;agrave; nước m&amp;agrave; kh&amp;ocirc;ng được cơ quan c&amp;oacute; thẩm quyền quản l&amp;yacute; cơ sở dữ liệu đ&amp;oacute; cho ph&amp;eacute;p.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Cơ quan c&amp;oacute; thẩm quyền quản l&amp;yacute; Cơ sở dữ liệu quốc gia về t&amp;agrave;i sản nh&amp;agrave; nước được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Cục Quản l&amp;yacute; c&amp;ocirc;ng sản - Bộ T&amp;agrave;i ch&amp;iacute;nh đối với Cơ sở dữ liệu về t&amp;agrave;i sản nh&amp;agrave; nước của cả nước;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Cơ quan t&amp;agrave;i ch&amp;iacute;nh của Bộ, cơ quan trung ương, Sở T&amp;agrave;i ch&amp;iacute;nh c&amp;aacute;c t&lt;/span&gt;ỉ&lt;span lang=&quot;VI&quot;&gt;nh, th&amp;agrave;nh phố trực thuộc trung ương đối với Cơ sở dữ liệu về t&amp;agrave;i sản nh&amp;agrave; n&lt;/span&gt;ướ&lt;span lang=&quot;VI&quot;&gt;c của Bộ, cơ quan trung ương, tỉnh, th&amp;agrave;nh phố trực thuộc trung ương;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Thủ trưởng cơ quan, tổ chức, đơn vị đối với Cơ sở dữ liệu về t&amp;agrave;i sản nh&amp;agrave; nước của cơ quan, tổ chức, đơn vị.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Chương&amp;nbsp;&lt;/span&gt;3.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;XỬ PHẠT VI PHẠM H&amp;Agrave;NH CH&amp;Iacute;NH TRONG QUẢN L&amp;Yacute;, SỬ DỤNG T&amp;Agrave;I SẢN CỦA C&amp;Aacute;C DỰ &amp;Aacute;N SỬ DỤNG VỐN NH&amp;Agrave; NƯỚC&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 17. Phạm vi t&amp;agrave;i sản của c&amp;aacute;c dự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;T&amp;agrave;i sản của c&amp;aacute;c dự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước được quy định tại c&amp;aacute;c Điều 17, v&amp;agrave; 19 Nghị định số 192/2013/NĐ-CP bao gồm:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trụ sở l&amp;agrave;m việc v&amp;agrave; c&amp;aacute;c t&amp;agrave;i sản kh&amp;aacute;c gắn liền với đất; phương tiện vận tải; m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc v&amp;agrave; c&amp;aacute;c t&amp;agrave;i sản kh&amp;aacute;c phục vụ c&amp;ocirc;ng t&amp;aacute;c quản l&amp;yacute; của c&amp;aacute;c Ban quản l&amp;yacute; dự &amp;aacute;n, cơ quan, đơn vị thực hiện dự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;T&amp;agrave;i sản phục vụ hoạt động của c&amp;aacute;c chuy&amp;ecirc;n gia nước ngo&amp;agrave;i, c&amp;aacute;c nh&amp;agrave; thầu tư vấn, gi&amp;aacute;m s&amp;aacute;t, thi c&amp;ocirc;ng thực hiện c&amp;aacute;c dự &amp;aacute;n sử dụng vốn ODA v&amp;agrave; nguồn viện trợ phi ch&amp;iacute;nh phủ nước ngo&amp;agrave;i khi dự &amp;aacute;n kết th&amp;uacute;c chuyển giao cho ph&amp;iacute;a Việt Nam;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;T&amp;agrave;i sản thu&amp;ecirc; ngo&amp;agrave;i phục vụ c&amp;ocirc;ng t&amp;aacute;c quản l&amp;yacute; của Ban quản l&amp;yacute; dự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	4.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Vật tư thu hồi từ việc th&amp;aacute;o dỡ c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh kết cấu hạ tầng phục vụ lợi &amp;iacute;ch c&amp;ocirc;ng cộng, lợi &amp;iacute;ch quốc gia đ&amp;atilde; cũ khi x&amp;acirc;y dựng c&amp;ocirc;ng tr&amp;igrave;nh kết cấu hạ tầng mới.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 18. H&amp;agrave;nh vi vi phạm quy định về trang cấp t&amp;agrave;i sản của c&amp;aacute;c dự &amp;aacute;n sử dụng v&lt;/span&gt;ố&lt;span lang=&quot;VI&quot;&gt;n nh&amp;agrave; nước&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về trang cấp t&amp;agrave;i sản của c&amp;aacute;c dự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước được thực hiện theo quy định tại Điều 17 Nghị định s&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;192/2013/NĐ-CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi thực hiện mua sắm t&amp;agrave;i sản khi chưa c&amp;oacute; quyết định của cấp c&amp;oacute; thẩm quyền quy định tại khoản 1 Điều 17 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi mua sắm t&amp;agrave;i sản m&amp;agrave; khi thực hiện mua sắm kh&amp;ocirc;ng c&amp;oacute; quyết định về trang cấp t&amp;agrave;i sản của cấp c&amp;oacute; thẩm quyền theo quy định của ph&amp;aacute;p luật về quản l&amp;yacute;, sử dụng, xử l&amp;yacute; t&amp;agrave;i sản của c&amp;aacute;c dự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi kh&amp;ocirc;ng thực hiện mua sắm tập trung đối với c&amp;aacute;c loại t&amp;agrave;i sản thuộc danh mục mua sắm tập trung quy định tại khoản 3 Điều 17 Nghị định s&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;192/2013/NĐ-CP được hướng dẫn cụ thể như quy định tại khoản 2 Điều 5 Th&amp;ocirc;ng tư n&amp;agrave;y;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi mua sắm t&amp;agrave;i sản vượt ti&amp;ecirc;u chuẩn, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền quy định tại khoản 4 Điều 17 Nghị định số 192/2013/NĐ-CP được hướng dẫn cụ th&lt;/span&gt;ể&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;nh&lt;/span&gt;ư&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;quy định tại khoản 3 Điều 5 Th&amp;ocirc;ng tư n&amp;agrave;y;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc x&amp;aacute;c định gi&amp;aacute; trị t&amp;agrave;i sản vượt so với ti&amp;ecirc;u chuẩn, định mức thực hiện theo quy định tại khoản 4 Điều 5 Th&amp;ocirc;ng tư n&amp;agrave;y;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	4.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi vi phạm quy định về thu&amp;ecirc; t&amp;agrave;i sản để phục vụ c&amp;ocirc;ng t&amp;aacute;c quản l&amp;yacute; của c&amp;aacute;c dự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước quy định tại khoản 5 Điều 17 Nghị định số 192/2013/NĐ-CP được hướng dẫn cụ thể như quy định tại Điều 6 Th&amp;ocirc;ng tư n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 19. H&amp;agrave;nh vi vi phạm quy định về sử dụng t&amp;agrave;i sản của c&amp;aacute;c dự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về sử dụng t&lt;/span&gt;&amp;agrave;&lt;span lang=&quot;VI&quot;&gt;i sản của c&amp;aacute;c dự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước được thực hiện theo quy định tại Điều 18 Nghị định số 192/2013/NĐ-CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản của c&amp;aacute;c dự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước vượt ti&amp;ecirc;u chuẩn, định mức hoặc kh&amp;ocirc;ng đ&amp;uacute;ng mục đ&amp;iacute;ch quy định tại khoản 1 Điều 18 Nghị định số 192/2013/NĐ-CP được hướng dẫn cụ thể như quy định tại Điều 7, Điều 8 Th&amp;ocirc;ng tư n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc x&amp;aacute;c định gi&amp;aacute; trị vượt so với ti&amp;ecirc;u chuẩn, định mức thực hiện theo quy định tại khoản 2 Điều 7 Th&amp;ocirc;ng tư n&amp;agrave;y;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi cho mượn t&amp;agrave;i sản của&amp;nbsp;&lt;/span&gt;d&lt;span lang=&quot;VI&quot;&gt;ự &amp;aacute;n sử dụng vốn nh&amp;agrave; nước&amp;nbsp;&lt;/span&gt;quy định&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tại khoản 2 Điều 18 Nghị định số 192/2013/NĐ-CP được hướng dẫn cụ th&lt;/span&gt;ể&lt;span lang=&quot;VI&quot;&gt;như quy đị', 'New regulations', 'THÔNG TƯ 07/2014/TT-BTC\r\n\r\nQuy định chi tiết và hướng dẫn thực hiện việc xử phạt vi phạm hành chính trong lĩnh vực quản lý, sử dụng tài sản nhà nước theo quy định tại Nghị định số 192/2013/NĐ-CP ngày 21/11/2013 của Chính phủ', '&lt;h2 style=&quot;margin: 8px 0px 16px; font-family: Arial, Verdana, Tahoma, Helvetica, sans-serif; font-weight: normal; line-height: 1; color: rgb(238, 92, 36); text-rendering: optimizelegibility; font-size: 20px; text-align: justify;&quot;&gt;\r\n	&lt;a href=&quot;http://aasc.com.vn/web/index.php/en/tin-tuc/quy-dinh-moi/157-thong-tu-07-2014-tt-btc&quot; style=&quot;color: rgb(34, 34, 34); text-decoration: none; font-size: 12px; text-transform: uppercase;&quot;&gt;TH&amp;Ocirc;NG TƯ 07/2014/TT-BTC&lt;/a&gt;&lt;/h2&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span style=&quot;line-height: 1.3em;&quot;&gt;Quy định chi tiết v&amp;agrave; hướng dẫn thực hiện việc xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước theo quy định tại Nghị định số 192/2013/NĐ-CP ng&amp;agrave;y 21/11/2013 của Ch&amp;iacute;nh phủ&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Căn cứ Nghị định số 81/2013/NĐ-CP ng&amp;agrave;y 19 th&amp;aacute;ng 7 năm 2013 của Ch&amp;iacute;nh phủ quy định ch&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tiết một số điều v&amp;agrave; biện ph&amp;aacute;p th&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;h&amp;agrave;nh Luật xử l&amp;yacute; vi phạm h&amp;agrave;nh ch&amp;iacute;nh;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Căn cứ Nghị định số 192/20&lt;/span&gt;1&lt;span lang=&quot;VI&quot;&gt;3/NĐ-CP ng&amp;agrave;y 21 th&amp;aacute;ng 11 năm 20&lt;/span&gt;1&lt;span lang=&quot;VI&quot;&gt;3 của Ch&amp;iacute;nh phủ quy định việc xử phạt vi phạm h&amp;agrave;nh ch&lt;/span&gt;&amp;iacute;&lt;span lang=&quot;VI&quot;&gt;nh trong&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;ĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước; thực h&amp;agrave;nh tiết kiệm chống&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;&amp;atilde;ng ph&amp;iacute;; dự trữ quốc gia; kho bạc nh&amp;agrave; nước;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Căn cứ Nghị định s&lt;/span&gt;ố 11&lt;span lang=&quot;VI&quot;&gt;8/2008/NĐ-CP ng&amp;agrave;y 27 th&amp;aacute;ng 11 năm 2008 của Ch&amp;iacute;nh phủ quy định chức n&lt;/span&gt;ă&lt;span lang=&quot;VI&quot;&gt;ng, nhiệm vụ, quyền hạn v&amp;agrave;&amp;nbsp;&lt;/span&gt;cơ cấu tổ chức&lt;span lang=&quot;VI&quot;&gt;của Bộ&amp;nbsp;&lt;/span&gt;T&amp;agrave;i ch&amp;iacute;nh&lt;span lang=&quot;VI&quot;&gt;;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Theo đề nghị của Cục trưởng Cục Quản&amp;nbsp;&lt;/span&gt;l&amp;yacute;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;c&amp;ocirc;ng sản,&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Bộ trưởng Bộ T&amp;agrave;i ch&lt;/span&gt;&amp;iacute;&lt;span lang=&quot;VI&quot;&gt;nh ban h&amp;agrave;nh Th&amp;ocirc;ng tư quy định chi tiết v&amp;agrave; hướng dẫn thực hiện việc xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;ĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước theo quy định tại Nghị định s&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;192/2013/NĐ-CP ng&amp;agrave;y 21 th&amp;aacute;ng 11 năm 2013 của Ch&amp;iacute;nh phủ quy định việc xử phạt vi phạm h&amp;agrave;nh ch&lt;/span&gt;&amp;iacute;&lt;span lang=&quot;VI&quot;&gt;nh trong lĩnh vực quản l&lt;/span&gt;&amp;yacute;&lt;span lang=&quot;VI&quot;&gt;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước; thực h&amp;agrave;nh tiết kiệm chống l&amp;atilde;ng ph&lt;/span&gt;&amp;iacute;&lt;span lang=&quot;VI&quot;&gt;; dự trữ quốc gia; kho bạc nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Chương&amp;nbsp;&lt;/span&gt;1.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;QUY ĐỊNH CHUNG&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 1. Phạm vi điều chỉnh&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Th&amp;ocirc;ng tư n&amp;agrave;y quy định chi tiết v&amp;agrave; hướng dẫn thực hiện việc x&lt;/span&gt;ử&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước theo quy định tại Nghị định số 192/2013/NĐ-CP ng&amp;agrave;y 21 th&amp;aacute;ng 11 năm 2013 của Ch&amp;iacute;nh phủ quy định việc xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;ĩnh vực quản l&amp;yacute;, s&lt;/span&gt;ử&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;dụng t&amp;agrave;i sản nh&amp;agrave; nước; thực h&amp;agrave;nh tiết kiệm chống l&amp;atilde;ng ph&amp;iacute;; dự trữ quốc gia; kho bạc nh&amp;agrave; nước (sau đ&amp;acirc;y gọi tắt l&amp;agrave; Nghị định số 192/2013/NĐ-CP).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 2. Đố&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tượng &amp;aacute;p dụng&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Cơ quan, tổ chức, đơn vị (sau đ&amp;acirc;y gọi chung l&amp;agrave; tổ chức), c&amp;aacute; nh&amp;acirc;n c&amp;oacute; h&amp;agrave;nh vi vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Người c&amp;oacute; thẩm quyền xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;C&amp;aacute;c đối tượng kh&amp;aacute;c c&amp;oacute; li&amp;ecirc;n quan đến xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh trong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Đ&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ều 3. Nguy&amp;ecirc;n tắc x&amp;aacute;c định h&amp;igrave;nh thức xử phạt v&amp;agrave; mức phạt tiền&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;igrave;nh thức xử phạt cảnh c&amp;aacute;o được &amp;aacute;p dụng trong c&amp;aacute;c trường hợp sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Xử phạt h&amp;agrave;nh vi quy định tại khoản 1 Điều 16 Nghị định số 192/2013/NĐ-CP;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với c&amp;aacute;c h&amp;agrave;nh vi được &amp;aacute;p dụng một trong hai h&amp;igrave;nh thức xử phạt ch&amp;iacute;nh (phạt cảnh c&amp;aacute;o hoặc phạt ti&lt;/span&gt;ề&lt;span lang=&quot;VI&quot;&gt;n) quy định khoản 1 Đi&lt;/span&gt;ề&lt;span lang=&quot;VI&quot;&gt;u 13, đi&lt;/span&gt;ể&lt;span lang=&quot;VI&quot;&gt;m a khoản 4 Đi&lt;/span&gt;ề&lt;span lang=&quot;VI&quot;&gt;u 14, khoản 1 Điều 15, khoản 2 Điều 16, khoản 7 Điều 18, khoản 1 Điều 19, khoản 4 Điều 19, khoản 5 Điều 19, khoản 1 Điều 20, khoản 1 v&amp;agrave; khoản 2 Điều 21 Nghị định số 192/2013/NĐ-CP th&lt;/span&gt;&amp;igrave;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;h&amp;igrave;nh thức xử phạt cảnh c&amp;aacute;o được &amp;aacute;p dụng đối với vi phạm lần đầu v&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; t&amp;igrave;nh tiết t&lt;/span&gt;ă&lt;span lang=&quot;VI&quot;&gt;ng nặng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với những h&amp;agrave;nh vi chỉ quy định h&amp;igrave;nh thức phạt tiền hoặc kh&amp;ocirc;ng đủ điều kiện &amp;aacute;p dụng h&amp;igrave;nh thức xử phạt cảnh c&amp;aacute;o, mức phạt tiền cụ thể được x&amp;aacute;c định như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Mức tiền phạt cụ thể đối với một h&amp;agrave;nh vi vi phạm h&amp;agrave;nh ch&amp;iacute;nh kh&amp;ocirc;ng c&amp;oacute; t&amp;igrave;nh tiết tăng nặng hoặc giảm nhẹ l&amp;agrave; mức trung b&amp;igrave;nh của khung tiền phạt được quy định đối với h&amp;agrave;nh vi đ&amp;oacute;. Mức trung b&amp;igrave;nh của khung tiền phạt được x&amp;aacute;c định b&lt;/span&gt;ằ&lt;span lang=&quot;VI&quot;&gt;ng trung b&amp;igrave;nh giữa mức tối đa của khung h&amp;igrave;nh phạt v&amp;agrave; mức tối thiểu của khung h&amp;igrave;nh phạt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Cứ một t&amp;igrave;nh tiết giảm nhẹ hoặc tăng nặng th&amp;igrave; mức tiền phạt được giảm hoặc tăng 20% so với mức trung b&amp;igrave;nh của khung tiền phạt được quy định đ&lt;/span&gt;ố&lt;span lang=&quot;VI&quot;&gt;i với h&amp;agrave;nh vi đ&amp;oacute;, nhưng kh&amp;ocirc;ng được thấp h&lt;/span&gt;ơn&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;mức tối thiểu v&amp;agrave; kh&amp;ocirc;ng được cao hơn mức tối đa của khung h&amp;igrave;nh phạt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Khi x&amp;aacute;c định mức phạt tiền đối với h&amp;agrave;nh vi vi phạm vừa c&amp;oacute; t&amp;igrave;nh tiết giảm nhẹ, vừa c&amp;oacute; t&amp;igrave;nh tiết tăng nặng th&amp;igrave; xem x&amp;eacute;t giảm trừ t&amp;igrave;nh ti&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;t tăng nặng theo nguy&amp;ecirc;n tắc c&amp;oacute; một t&amp;igrave;nh tiết giảm nhẹ được giảm trừ một t&amp;igrave;nh&amp;nbsp;&lt;/span&gt;tiết&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tăng nặng. Sau khi&amp;nbsp;&lt;/span&gt;g&lt;span lang=&quot;VI&quot;&gt;iảm trừ, nếu c&amp;ograve;n t&amp;igrave;nh tiết giảm nhẹ hoặc tăng nặng th&amp;igrave; &amp;aacute;p dụng theo quy định tại&amp;nbsp;&lt;/span&gt;điểm&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;b khoản n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Nguồn kinh ph&amp;iacute; nộp phạt của tổ chức, c&amp;aacute; nh&amp;acirc;n bị x&lt;/span&gt;ử&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;phạt vi phạm kh&amp;ocirc;ng được sử dụng tiền ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước hoặc tiền c&amp;oacute; nguồn gốc từ ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước để nộp phạt v&amp;agrave; khắc phục hậu quả do h&amp;agrave;nh vi vi phạm của m&amp;igrave;nh g&amp;acirc;y ra. Trường hợp xử phạt tổ chức th&amp;igrave; sau khi chấp h&amp;agrave;nh quyết định xử phạt, t&lt;/span&gt;ổ&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;chức bị xử phạt x&amp;aacute;c định c&amp;aacute; nh&amp;acirc;n c&amp;oacute;&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;ỗi g&amp;acirc;y ra vi phạm h&amp;agrave;nh ch&amp;iacute;nh đ&lt;/span&gt;ể&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;x&amp;aacute;c định tr&amp;aacute;ch nhiệm ph&amp;aacute;p l&amp;yacute;, bao gồm cả việc nộp lại khoản tiền phạt v&amp;agrave; khắc phục hậu quả do h&amp;agrave;nh vi vi phạm của m&amp;igrave;nh g&amp;acirc;y ra tương ứng với mức độ vi phạm của c&amp;aacute; nh&amp;acirc;n đ&amp;oacute; theo quy định của ph&amp;aacute;p luật v&amp;agrave; Quy ch&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;qu&lt;/span&gt;ả&lt;span lang=&quot;VI&quot;&gt;n l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước của t&lt;/span&gt;ổ&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;chức.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 4. Lập b&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;&amp;ecirc;n bản xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Khi ph&amp;aacute;t hiện h&amp;agrave;nh vi vi phạm, người c&amp;oacute; thẩm quyền xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh&amp;nbsp;&lt;/span&gt;tr&lt;span lang=&quot;VI&quot;&gt;ong lĩnh vực quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước, c&amp;ocirc;ng chức được giao nhiệm vụ kiểm tra việc chấp h&amp;agrave;nh ph&amp;aacute;p luật về quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước quy định tại khoản 1 Điều 22 Nghị định số 192/2013/NĐ-CP phải lập bi&amp;ecirc;n bản ngay tại thời điểm ph&amp;aacute;t hiện h&amp;agrave;nh vi vi phạm.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Bi&amp;ecirc;n bản phải lập theo đ&amp;uacute;ng quy định tại M&lt;/span&gt;ẫ&lt;span lang=&quot;VI&quot;&gt;u Bi&amp;ecirc;n bản số 01 k&amp;egrave;m theo Nghị định số 81/2013/NĐ-CP ng&amp;agrave;y 19 th&amp;aacute;ng 7 năm 2013 của Ch&amp;iacute;nh phủ quy định chi tiết một số điều v&amp;agrave; biện ph&amp;aacute;p thi h&amp;agrave;nh Luật xử l&amp;yacute; vi phạm h&amp;agrave;nh ch&amp;iacute;nh (sau đ&amp;acirc;y gọi tắt l&amp;agrave; Nghị định số 81/2013/NĐ-CP).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Chương&amp;nbsp;&lt;/span&gt;2.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;XỬ PHẠT VI PHẠM H&amp;Agrave;NH CH&amp;Iacute;NH TRONG QUẢN L&amp;Yacute;, SỬ DỤNG T&amp;Agrave;I SẢN NH&amp;Agrave; NƯỚC TẠI CƠ QUAN, TỔ CHỨC, ĐƠN VỊ&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Đ&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ều 5. H&amp;agrave;nh vi vi phạm quy định về mua sắm t&amp;agrave;i sản nh&amp;agrave; nước&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về mua sắm t&amp;agrave;i sản nh&amp;agrave; nước được thực hiện theo quy định tại Điều 5 Nghị định số 192/2013/NĐ-CP. Một số nội dung được quy định cụ thể nh&lt;/span&gt;ư&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi thực hiện mua sắm t&amp;agrave;i sản khi chưa c&amp;oacute; quyết định của cấp c&amp;oacute; thẩm quyền quy định tại khoản 1 Điều 5 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi tại thời điểm thực hiện mua sắm chưa c&amp;oacute; quyết định về mua sắm t&amp;agrave;i sản nh&amp;agrave; nước của cấp c&amp;oacute; thẩm quyền. Thẩm quyền quyết định mua sắm t&amp;agrave;i sản nh&amp;agrave; nước thực hiện theo quy định của Luật quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave; c&amp;aacute;c văn bản quy định chi ti&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;t, hướng d&lt;/span&gt;ẫ&lt;span lang=&quot;VI&quot;&gt;n thi h&amp;agrave;nh;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi kh&amp;ocirc;ng thực hiện mua sắm tập trung đối với c&amp;aacute;c loại t&amp;agrave;i sản thuộc danh mục mua sắm tập trung quy định tại khoản 2 Điều 5 Nghị định số 192/2013/NĐ-CP v&amp;agrave; được x&amp;aacute;c định theo từng lần mua sắm;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi mua sắm t&amp;agrave;i sản vượt ti&amp;ecirc;u chuẩn, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; th&lt;/span&gt;ẩ&lt;span lang=&quot;VI&quot;&gt;m quyền quy định tại khoản 3 Điều 5 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi mua sắm t&amp;agrave;i sản vượt về diện t&amp;iacute;ch (đối với trụ sở l&amp;agrave;m việc), vượt về số lượng, vượt về mức gi&amp;aacute; (đối với phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc) so với ti&amp;ecirc;u chuẩn, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền quy định;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	4.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc x&amp;aacute;c định phần gi&amp;aacute; trị vượt so với ti&amp;ecirc;u chuẩn, định mức để l&amp;agrave;m căn cứ xử phạt được quy định như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường h&lt;/span&gt;ợ&lt;span lang=&quot;VI&quot;&gt;p mua sắm t&amp;agrave;i sản l&amp;agrave; phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc vượt về s&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;lượng so với ti&amp;ecirc;u chuẩn, định mức: Gi&amp;aacute; trị vượt/01 đơn vị t&amp;agrave;i sản được x&amp;aacute;c định bằng đơn gi&amp;aacute; của t&amp;agrave;i sản đ&amp;oacute; theo gi&amp;aacute; mua ghi tr&amp;ecirc;n h&amp;oacute;a đơn hoặc hợp đồng mua sắm t&amp;agrave;i sản;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp mua sắm t&amp;agrave;i sản l&amp;agrave; phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc đ&amp;uacute;ng về số lượng nhưng vượt về mức gi&amp;aacute; so với ti&amp;ecirc;u chuẩn, định mức: Gi&amp;aacute; trị vượt được x&amp;aacute;c định bằng gi&amp;aacute; trị của t&amp;agrave;i sản theo gi&amp;aacute; mua ghi tr&amp;ecirc;n h&amp;oacute;a đơn hoặc hợp đ&lt;/span&gt;ồ&lt;span lang=&quot;VI&quot;&gt;ng mua s&lt;/span&gt;ắ&lt;span lang=&quot;VI&quot;&gt;m t&amp;agrave;i sản trừ đi (-) gi&amp;aacute; trị t&amp;agrave;i sản theo ti&amp;ecirc;u chu&lt;/span&gt;ẩ&lt;span lang=&quot;VI&quot;&gt;n, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền quy định;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp mua sắm trụ sở l&amp;agrave;m việc vượt ti&amp;ecirc;u chuẩn, định mức: Gi&amp;aacute; trị vượt được x&amp;aacute;c định bằng ch&amp;ecirc;nh lệch giữa diện t&amp;iacute;ch thực tế trừ đi (-)&lt;/span&gt;d&lt;span lang=&quot;VI&quot;&gt;iện t&amp;iacute;ch được sử dụng theo ti&amp;ecirc;u chuẩn, định mức nh&amp;acirc;n với (x) đơn gi&amp;aacute; mua b&amp;igrave;nh qu&amp;acirc;n theo h&amp;oacute;a đ&lt;/span&gt;ơn&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;(hoặc theo h&lt;/span&gt;ợ&lt;span lang=&quot;VI&quot;&gt;p đồng mua sắm t&amp;agrave;i sản).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Đ&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ều 6. H&amp;agrave;nh vi vi phạm quy định về thu&amp;ecirc; t&amp;agrave;i sản&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về thu&amp;ecirc; t&amp;agrave;i sản được thực hiện theo quy định tại Điều 6 Nghị định số 192/2013/NĐ-CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi thực hiện thu&amp;ecirc; t&amp;agrave;i sản khi chưa c&amp;oacute; quyết định của cấp c&amp;oacute; thẩm quyền quy định tại khoản 1 Điều 6 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi k&amp;yacute; kết hợp đồng thu&amp;ecirc; t&amp;agrave;i sản khi chưa c&amp;oacute; quyết định về thu&amp;ecirc; t&amp;agrave;i sản của cấp c&amp;oacute; thẩm quyền.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Thẩm quyền quyết định thu&amp;ecirc; t&amp;agrave;i sản thực hiện theo quy định của Luật quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave; c&amp;aacute;c văn bản quy định chi tiết,&lt;/span&gt;hướng dẫn&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;thi h&amp;agrave;nh;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi thu&amp;ecirc; t&amp;agrave;i sản vượt ti&amp;ecirc;u chuẩn, định mức quy định tại khoản 1 Điều 6 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi thu&amp;ecirc; t&amp;agrave;i sản để phục vụ hoạt động của tổ chức vượt về diện t&amp;iacute;ch (đối với trụ sở l&amp;agrave;m việc), vượt về số lượng (đối với phương ti&lt;/span&gt;ệ&lt;span lang=&quot;VI&quot;&gt;n đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc) so với ti&amp;ecirc;u chu&lt;/span&gt;ẩ&lt;span lang=&quot;VI&quot;&gt;n, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền quy định;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi lựa chọn nh&amp;agrave; cung cấp dịch vụ cho thu&amp;ecirc; t&amp;agrave;i sản kh&amp;ocirc;ng đ&amp;uacute;ng h&amp;igrave;nh thức, tr&amp;igrave;nh tự quy định tại khoản 1 Điều 6 Nghị định số 192/2013/NĐ-CP bao gồm:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi kh&amp;ocirc;ng th&amp;ocirc;ng b&amp;aacute;o c&amp;ocirc;ng khai nhu cầu thu&amp;ecirc; t&amp;agrave;i sản tr&amp;ecirc;n c&amp;aacute;c phương tiện th&amp;ocirc;ng tin đại ch&amp;uacute;ng theo quy định của ph&amp;aacute;p luật về quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi kh&amp;ocirc;ng thực hiện đấu thầu khi lựa ch&lt;/span&gt;ọ&lt;span lang=&quot;VI&quot;&gt;n nh&amp;agrave; cung cấp dịch vụ cho thu&amp;ecirc; t&lt;/span&gt;&amp;agrave;&lt;span lang=&quot;VI&quot;&gt;i sản đối với c&amp;aacute;c trường hợp theo quy định của ph&amp;aacute;p luật phải thực hiện đấu thầu.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	4.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Gi&amp;aacute;&amp;nbsp;&lt;/span&gt;tr&lt;span lang=&quot;VI&quot;&gt;ị hợp đồng thu&amp;ecirc; t&amp;agrave;i sản để l&amp;agrave;m căn cứ xử phạt được x&amp;aacute;c định như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp trong hợp đồng thu&amp;ecirc; t&amp;agrave;i sản ghi cụ thể tổng gi&amp;aacute; trị hợp đồng th&amp;igrave; x&amp;aacute;c định theo t&lt;/span&gt;ổ&lt;span lang=&quot;VI&quot;&gt;ng gi&amp;aacute;&amp;nbsp;&lt;/span&gt;tr&lt;span lang=&quot;VI&quot;&gt;ị ghi trong hợp đ&lt;/span&gt;ồ&lt;span lang=&quot;VI&quot;&gt;ng;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp trong hợp đồng thu&amp;ecirc; t&amp;agrave;i sản ghi số lượng (khối lượng), đơn gi&amp;aacute; v&amp;agrave; thời hạn thu&amp;ecirc; th&lt;/span&gt;&amp;igrave;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tổng gi&amp;aacute; trị hợp đồng được x&amp;aacute;c định bằng (=) số lượng (khối lượng) nh&amp;acirc;n với (x) đơn gi&amp;aacute; thu&amp;ecirc; nh&amp;acirc;n với (x) thời hạn thu&amp;ecirc; ghi trong hợp đồng;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp trong hợp đồng kh&amp;ocirc;ng ghi r&amp;otilde; thời hạn thu&amp;ecirc; th&amp;igrave;&amp;nbsp;&lt;/span&gt;gi&amp;aacute;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;trị hợp đồng thu&amp;ecirc; được t&amp;iacute;nh theo số tiền thu&amp;ecirc; t&amp;agrave;i sản thực tế đ&amp;atilde; trả kể cả ph&lt;/span&gt;ầ&lt;span lang=&quot;VI&quot;&gt;n tiền thu&amp;ecirc; trả trước nếu c&amp;oacute; t&amp;iacute;nh đến thời điểm ra quyết định xử phạt h&amp;agrave;nh ch&amp;iacute;nh&lt;/span&gt;.&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 7. H&amp;agrave;nh vi b&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tr&amp;iacute;, sử dụng t&amp;agrave;&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;sản nh&amp;agrave; n&lt;/span&gt;ướ&lt;span lang=&quot;VI&quot;&gt;c vượt ti&amp;ecirc;u chuẩn, định mức&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Vi&lt;/span&gt;ệ&lt;span lang=&quot;VI&quot;&gt;c xử phạt h&amp;agrave;nh vi bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước vượt t&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;&amp;ecirc;u chuẩn, định mức được thực hiện theo quy định tại khoản 1 Điều 7 Nghị định số 192/2013/NĐ- CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi b&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tr&amp;iacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước vượt ti&amp;ecirc;u chuẩn, định mức quy định tại khoản 1 Điều 7 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước cho người sử dụng, bộ phận sử dụng đ&amp;uacute;ng đối tượng nhưng vượt về diện t&amp;iacute;ch (đối với trụ sở l&amp;agrave;m việc), vượt về số lượng hoặc mức gi&amp;aacute; (đối với phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c,&amp;nbsp;&lt;/span&gt;tr&lt;span lang=&quot;VI&quot;&gt;ang thi&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;t bị l&amp;agrave;m việc);&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc x&amp;aacute;c định gi&amp;aacute; trị vượt so với ti&amp;ecirc;u chuẩn, định mức được quy định như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp b&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;tr&amp;iacute;, sử dụng t&amp;agrave;i sản l&amp;agrave; phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc vượt về số lượng so với ti&amp;ecirc;u chu&lt;/span&gt;ẩ&lt;span lang=&quot;VI&quot;&gt;n, định mức: Gi&amp;aacute; trị vượt được x&amp;aacute;c định bằng số lượng t&amp;agrave;i sản vượt nh&amp;acirc;n với (x) nguy&amp;ecirc;n gi&amp;aacute; của t&amp;agrave;i sản&amp;nbsp;&lt;/span&gt;tr&lt;span lang=&quot;VI&quot;&gt;&amp;ecirc;n sổ kế to&amp;aacute;n; trường hợp tr&amp;ecirc;n sổ kế to&amp;aacute;n kh&amp;ocirc;ng theo d&amp;otilde;i nguy&amp;ecirc;n gi&amp;aacute; t&amp;agrave;i sản th&amp;igrave; l&lt;/span&gt;ấ&lt;span lang=&quot;VI&quot;&gt;y theo gi&amp;aacute; trị mua sắm mới t&amp;agrave;i sản c&amp;ugrave;ng&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;oại hoặc c&amp;oacute; ti&amp;ecirc;u chuẩn kỹ thuật, c&amp;ocirc;ng năng sử dụng tương đương tr&amp;ecirc;n thị trường tại thời điểm x&lt;/span&gt;ả&lt;span lang=&quot;VI&quot;&gt;y ra vi phạm;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản l&amp;agrave; phương tiện đi lại, m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc đ&amp;uacute;ng về số lượng nhưng vượt về mức gi&amp;aacute; so với ti&amp;ecirc;u chuẩn, định mức: Gi&amp;aacute; trị vượt được x&amp;aacute;c định bằng số lượng vượt nh&amp;acirc;n với (x) (ch&amp;ecirc;nh lệch giữa nguy&amp;ecirc;n gi&amp;aacute; của t&amp;agrave;i sản tr&amp;ecirc;n sổ kế to&amp;aacute;n trừ đi (-) gi&amp;aacute; trị t&amp;agrave;i sản theo ti&amp;ecirc;u chuẩn, định mức do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền quy định);&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Trường hợp t&amp;agrave;i sản l&amp;agrave; trụ sở l&amp;agrave;m việc: Gi&amp;aacute; trị vượt được x&amp;aacute;c định bằng ch&amp;ecirc;nh lệch giữa diện t&amp;iacute;ch thực tế trừ đi (-) diện t&amp;iacute;ch được&amp;nbsp;&lt;/span&gt;sử dụng&lt;span lang=&quot;VI&quot;&gt;theo ti&amp;ecirc;u chuẩn, định mức nh&amp;acirc;n với (x) đơn gi&amp;aacute; b&amp;igrave;nh qu&amp;acirc;n theo sổ kế to&amp;aacute;n; trường hợp tr&amp;ecirc;n sổ kế to&amp;aacute;n kh&amp;ocirc;ng theo d&amp;otilde;i th&amp;igrave; đơn gi&amp;aacute; t&amp;iacute;nh theo suất đầu tư của nh&amp;agrave;, c&amp;ocirc;ng tr&amp;igrave;nh c&amp;oacute; ti&amp;ecirc;u chuẩn kỹ thuật tương đương do Bộ X&amp;acirc;y dựng ban h&amp;agrave;nh &amp;aacute;p dụng tại thời điểm h&amp;agrave;nh vi vi phạm xảy ra.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 8. H&amp;agrave;nh&amp;nbsp;&lt;/span&gt;vi&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;bố tr&amp;iacute;, sử dụng t&amp;agrave;&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng mục đ&amp;iacute;ch&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng mục đ&amp;iacute;ch quy định tại khoản 2 Điều 7 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi bố tr&amp;iacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng với mục đ&amp;iacute;ch, c&amp;ocirc;ng năng sử dụng của t&amp;agrave;i sản được đầu tư, trang bị, mua sắm. Một số trường hợp cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Bố tr&amp;iacute;, sử dụng trụ s&lt;/span&gt;ở l&lt;span lang=&quot;VI&quot;&gt;&amp;agrave;m việc, cơ sở hoạt động sự nghiệp l&amp;agrave;m nh&amp;agrave; ở hoặc sử dụng v&amp;agrave;o mục đ&amp;iacute;ch c&amp;aacute; nh&amp;acirc;n;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Bố tr&amp;iacute;, sử dụng xe &amp;ocirc; t&amp;ocirc; đưa đ&amp;oacute;n từ nơi ở đến nơi&amp;nbsp;&lt;/span&gt;l&lt;span lang=&quot;VI&quot;&gt;&amp;agrave;m việc đối với chức danh kh&amp;ocirc;ng c&amp;oacute; ti&amp;ecirc;u chu&lt;/span&gt;ẩ&lt;span lang=&quot;VI&quot;&gt;n; bố tr&amp;iacute;, sử dụng xe &amp;ocirc; t&amp;ocirc; phục vụ c&amp;ocirc;ng t&amp;aacute;c cho c&amp;aacute;c chức danh kh&amp;ocirc;ng c&amp;oacute; ti&amp;ecirc;u chuẩn sử dụng xe m&amp;agrave; kh&amp;ocirc;ng được cấp c&amp;oacute; thẩm quyền ph&amp;ecirc; duyệt; bố tr&amp;iacute;, sử dụng xe &amp;ocirc; t&amp;ocirc; v&amp;agrave;o mục đ&amp;iacute;ch c&amp;aacute; nh&amp;acirc;n;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Bố tr&amp;iacute;, sử dụng m&amp;aacute;y m&amp;oacute;c, trang thiết bị l&amp;agrave;m việc, t&amp;agrave;i sản kh&amp;aacute;c v&amp;agrave;o mục đ&amp;iacute;ch c&amp;aacute; nh&amp;acirc;n.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với cơ quan nh&amp;agrave; nước, đơn vị sự nghiệp c&amp;ocirc;ng lập chưa tự chủ t&amp;agrave;i ch&amp;iacute;nh sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết th&amp;igrave; xử phạt theo quy định tại khoản 2 Điều 7 Nghị định số 192/2013/NĐ-CP v&amp;agrave; khoản 1 Điều n&amp;agrave;y; kh&amp;ocirc;ng xử phạt theo quy định tại Điều 12 Nghị định số 192/2013/NĐ-CP v&amp;agrave; Điều 13 Th&amp;ocirc;ng tư n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	c)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với đơn vị sự nghiệp c&amp;ocirc;ng lập tự chủ t&amp;agrave;i ch&amp;iacute;nh sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết kh&amp;ocirc;n&lt;/span&gt;g&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;đ&amp;uacute;ng quy định th&amp;igrave; bị xử phạt theo quy định tại Điều 12 Nghị định số 192/2013/NĐ-CP v&amp;agrave; Điều 13 Th&lt;/span&gt;&amp;ocirc;&lt;span lang=&quot;VI&quot;&gt;ng tư n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 9. H&amp;agrave;nh v&lt;/span&gt;i&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;cho mượn t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&lt;/span&gt;&amp;uacute;&lt;span lang=&quot;VI&quot;&gt;ng quy định&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi cho mượn t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng quy định được thực hiện theo quy định tại Điều 8 Nghị định số 192/2013/NĐ-CP.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi cho mượn t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng quy định l&amp;agrave; h&amp;agrave;nh vi cho tổ chức, c&amp;aacute; nh&amp;acirc;n kh&amp;aacute;c sử dụng t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng ph&amp;acirc;n biệt c&amp;oacute; hợp đồng cho mượn hay kh&amp;ocirc;ng c&amp;oacute; hợp đồng cho mượn, kh&amp;ocirc;ng ph&amp;acirc;n biệt thời hạn cho mượn.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Đ&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ều 10. H&amp;agrave;nh vi trao đ&lt;/span&gt;ổ&lt;span lang=&quot;VI&quot;&gt;i, biếu, tặng cho t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng quy định&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi trao đổi, biếu, tặng cho t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng quy định được thực hiện theo quy định tại Điều 9 Nghị định số 192/2013/NĐ-CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi trao đổi t&amp;agrave;i sản nh&amp;agrave; nước quy định tại khoản 1 Điều 9 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi d&amp;ugrave;ng t&amp;agrave;i sản&amp;nbsp;&lt;/span&gt;nh&amp;agrave;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;nước của tổ chức đ&lt;/span&gt;ể&lt;span lang=&quot;VI&quot;&gt;đổi lấy t&amp;agrave;i sản của tổ chức, c&amp;aacute; nh&amp;acirc;n kh&amp;aacute;c m&amp;agrave; kh&amp;ocirc;ng được cấp c&amp;oacute; thẩm quyền cho ph&amp;eacute;p;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi biếu, tặng cho t&amp;agrave;i sản nh&amp;agrave; nước kh&amp;ocirc;ng đ&amp;uacute;ng quy định l&amp;agrave; h&amp;agrave;nh vi sử dụng t&amp;agrave;i sản nh&amp;agrave; nước để l&amp;agrave;m qu&amp;agrave; tặng vi phạm Quy chế tặng qu&amp;agrave; do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền ban h&amp;agrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 11. H&amp;agrave;nh vi lấn chiếm trụ s&lt;/span&gt;ở&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;l&amp;agrave;m việc, cơ sở hoạt động sự nghiệp&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi lấn chiếm trụ sở l&amp;agrave;m việc, cơ sở hoạt động sự nghiệp thực hiện theo quy định tại Điều 10 Nghị định 192/2013/NĐ-CP.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi lấn chiếm trụ sở l&amp;agrave;m việc, cơ sở hoạt động sự nghiệp quy định tại Điều 10 Nghị định số 192/2013/NĐ-CP bao gồm:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Tổ chức, c&amp;aacute; nh&amp;acirc;n đang sử dụng đất liền kề tự chuyển dịch mốc g&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ới thửa đất để mở rộng diện t&amp;iacute;ch đất hoặc x&amp;acirc;y dựng c&amp;ocirc;ng tr&amp;igrave;nh lấn chiếm sang kh&amp;ocirc;ng gian phần diện t&amp;iacute;ch đất thuộc khu&amp;ocirc;n vi&amp;ecirc;n trụ sở l&amp;agrave;m việc, cơ sở hoạt động sự nghiệp của cơ quan, tổ chức, đơn vị;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Tổ chức, c&amp;aacute; nh&amp;acirc;n tự&amp;nbsp;&lt;/span&gt;&amp;yacute;&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;sử dụng nh&amp;agrave;, đất thuộc trụ sở l&amp;agrave;m việc, cơ sở hoạt động sự nghiệp của cơ quan, tổ chức, đơn vị.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 12. H&amp;agrave;nh vi chiếm đoạt t&amp;agrave;i sản&amp;nbsp;&lt;/span&gt;nh&amp;agrave; nước&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi chiếm đoạt t&amp;agrave;i sản nh&amp;agrave; nước được thực hiện theo quy định tại Điều 11 Nghị định s&lt;/span&gt;ố&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;192/2013/NĐ-CP.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi chiếm đoạt t&amp;agrave;i sản nh&amp;agrave; nước bị xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh l&amp;agrave; việc nắm g&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ữ, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước m&amp;agrave; kh&amp;ocirc;ng được cơ quan nh&amp;agrave; nước c&amp;oacute;&amp;nbsp;&lt;/span&gt;thẩm quyền&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;cho ph&amp;eacute;p nhưng chưa đến mức bị truy cứu tr&amp;aacute;ch nhiệm h&amp;igrave;nh sự.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Đ&lt;/span&gt;i&lt;span lang=&quot;VI&quot;&gt;ều 13. H&amp;agrave;nh vi vi phạm quy định về sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết được thực hiện theo quy định tại Điều 12 Nghị định số 192/2013/NĐ-CP.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết khi chưa c&amp;oacute; quyết định của cấp c&amp;oacute; thẩm quyền quy định tại khoản 1 Điều 12 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết m&amp;agrave; tại thời điểm thực hiện h&amp;agrave;nh vi kh&amp;ocirc;ng c&amp;oacute; quyết định của cấp c&amp;oacute; thẩm quyền.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Thẩm quyền quyết định sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave;o mục đ&amp;iacute;ch sản xuất, kinh doanh dịch vụ, cho thu&amp;ecirc;, li&amp;ecirc;n doanh, li&amp;ecirc;n kết thực hiện theo quy định của Luật quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước v&amp;agrave; c&amp;aacute;c văn bản quy định chi tiết, hướng dẫn thi h&amp;agrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 14. H&amp;agrave;nh vi vi phạm quy định về xử l&amp;yacute; t&amp;agrave;i sản nh&amp;agrave; nước&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về xử l&amp;yacute; t&amp;agrave;i sản nh&amp;agrave; nước được thực hiện theo quy định tại Điều 14 Nghị định s&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;192/2013/NĐ-CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi để hư hỏng, thất tho&amp;aacute;t t&amp;agrave;i sản quy định tại khoản 2 Điều 14 Nghị đ&lt;/span&gt;ị&lt;span lang=&quot;VI&quot;&gt;nh số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi l&amp;agrave;m mất m&amp;aacute;t t&amp;agrave;i sản hoặc l&amp;agrave;m t&amp;agrave;i sản bị hư hỏng; l&amp;agrave;m mất m&amp;aacute;t, thay đổi hoặc biến dạng linh kiện, phụ t&amp;ugrave;ng của t&amp;agrave;i sản;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi k&amp;ecirc; khai kh&amp;ocirc;ng đ&amp;uacute;ng danh mục, hiện trạng t&amp;agrave;i sản đề nghị xử l&amp;yacute; quy định tại khoản 4 Điều 14 Nghị định số 192/2013/NĐ-CP l&amp;agrave; h&amp;agrave;nh vi k&amp;ecirc; khai kh&amp;ocirc;ng đ&amp;uacute;ng danh mục, hiện trạng t&amp;agrave;i sản đề nghị xử l&amp;yacute; d&lt;/span&gt;ẫ&lt;span lang=&quot;VI&quot;&gt;n đ&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;n việc quy&lt;/span&gt;ế&lt;span lang=&quot;VI&quot;&gt;t định xử l&amp;yacute; t&amp;agrave;i sản kh&amp;ocirc;ng đ&amp;uacute;ng quy định;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	3.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Việc x&amp;aacute;c định số tiền phải nộp lại tương ứng với gi&amp;aacute; trị t&amp;agrave;i sản bị thất tho&amp;aacute;t, hư hỏng quy định tại điểm b khoản 5 Điều 14 Nghị định số 192/2013/NĐ- CP được quy định như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với t&amp;agrave;i sản bị mất hoặc hư hỏng kh&amp;ocirc;ng thể khắc phục được, số tiền phải nộp lại được x&amp;aacute;c định tương ứng với gi&amp;aacute; mua mới t&amp;agrave;i sản c&amp;ugrave;ng loại hoặc c&amp;oacute; ti&amp;ecirc;u chuẩn, c&amp;ocirc;ng năng sử dụng tương đương tr&amp;ecirc;n thị trường;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	b)&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;Đối với t&amp;agrave;i sản bị hư hỏng c&amp;oacute; thể khắc phục được, số tiền phải nộp lại l&amp;agrave; chi ph&amp;iacute; để sửa chữa t&amp;agrave;i sản đ&amp;oacute;.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;strong&gt;&lt;span lang=&quot;VI&quot;&gt;Điều 15. H&amp;agrave;nh vi vi phạm quy định về tổ chức xử l&amp;yacute; t&amp;agrave;i sản nh&amp;agrave; nước&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&lt;span lang=&quot;VI&quot;&gt;Việc xử phạt h&amp;agrave;nh vi vi phạm quy định về tổ chức x&lt;/span&gt;ử&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;l&amp;yacute; t&amp;agrave;i sản nh&amp;agrave; nước được thực hiện theo quy định tại Điều 15 Nghị định số 192/2013/NĐ-CP. Một số nội dung được quy định cụ thể như sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	1.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi qu&amp;aacute; thời hạn quy định m&amp;agrave; vẫn chưa triển khai thực hiện phương &amp;aacute;n xử l&amp;yacute; đ&amp;atilde; được cấp c&amp;oacute; thẩm quyền ph&amp;ecirc; duyệt quy định tại điểm a khoản 1 Điều 15 Nghị định số 192/2013/NĐ-CP được x&amp;aacute;c định theo thời hạn xử l&amp;yacute; quy định tại quyết định xử l&amp;yacute; t&amp;agrave;i sản. Trường hợp tại quyết định xử l&amp;yacute; t&amp;agrave;i sản kh&amp;ocirc;ng quy định thời hạn cụ thể, th&amp;igrave; thực hiện theo quy định của ph&amp;aacute;p luật c&amp;oacute; li&amp;ecirc;n quan. Trường hợp được cấp c&amp;oacute; thẩm quyền ph&amp;ecirc; duyệt phương &amp;aacute;n xử l&amp;yacute; gia hạn việc xử l&amp;yacute; t&amp;agrave;i sản, th&amp;igrave; thời hạn quy định được x&amp;aacute;c định theo thời hạn sau khi đ&amp;atilde; được gia hạn;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	2.&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;H&amp;agrave;nh vi kh&amp;ocirc;ng thực hiện hoặc thực hiện kh&amp;ocirc;ng đầy đủ việc th&amp;ocirc;ng b&amp;aacute;o c&amp;ocirc;ng khai b&amp;aacute;n đấu gi&amp;aacute; t&amp;agrave;i sản nh&amp;agrave; nước theo quy định của ph&amp;aacute;p luật về quản l&amp;yacute;, sử dụng t&amp;agrave;i sản nh&amp;agrave; nước quy định tại&amp;nbsp;&lt;/span&gt;điểm&lt;span lang=&quot;VI&quot;&gt;&amp;nbsp;c khoản 2 Điều 15 Nghị định s&lt;/span&gt;ố&amp;nbsp;&lt;span lang=&quot;VI&quot;&gt;192/2013/NĐ-CP bao gồm:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	a)&amp;nbsp;&lt;span lan', NULL, NULL, 1, '2014-03-28 16:03:41', 1, NULL, 1, 0);
INSERT INTO `news` (`id`, `menu_id`, `title`, `caption`, `detail`, `title_eng`, `caption_eng`, `detail_eng`, `thumb_image_path`, `list_file_attach`, `create_user_id`, `create_date`, `feature_flag`, `update_date`, `is_public`, `del_flg`) VALUES
(18, 29, 'THÔNG TƯ 219/2013/TT- BTC', 'Hướng dẫn thi hành Luật Thuế giá trị gia tăng và Nghị định số 209/2013/NĐ-CP ngày 18/12/2013 của Chính phủ quy định chi tiết và hướng dẫn thi hành một số điều Luật Thuế giá trị gia tăng', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Hướng dẫn thi h&amp;agrave;nh Luật Thuế gi&amp;aacute; trị gia tăng v&amp;agrave; Nghị định số 209/2013/NĐ-CP ng&amp;agrave;y 18/12/2013 của Ch&amp;iacute;nh phủ quy định chi tiết v&amp;agrave; hướng dẫn thi h&amp;agrave;nh một số điều Luật Thuế gi&amp;aacute; trị gia tăng&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Căn cứ Luật Thuế gi&amp;aacute; trị gia tăng số 13/2008/QH12 ng&amp;agrave;y 03 th&amp;aacute;ng 6 năm 2008 v&amp;agrave; Luật sửa đổi, bổ sung một số điều của Luật Thuế gi&amp;aacute; trị gia tăng số 31/2013/QH13 ng&amp;agrave;y 19 th&amp;aacute;ng 6 năm 2013;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Căn cứ Luật Quản l&amp;yacute; thuế số 78/2006/QH11 ng&amp;agrave;y 29 th&amp;aacute;ng 11 năm 2006&amp;nbsp;v&amp;agrave;&amp;nbsp;Luật sửa đổi, bổ sung một số điều của Luật Quản l&amp;yacute; thuế số 21/2012/QH13 ng&amp;agrave;y 20 th&amp;aacute;ng 11 năm 2012;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Căn cứ Nghị định số 209/2013/NĐ-CP ng&amp;agrave;y 18 th&amp;aacute;ng 12 năm 2013 của Ch&amp;iacute;nh phủ quy định chi tiết v&amp;agrave; hướng dẫn thi h&amp;agrave;nh một số điều Luật Thuế gi&amp;aacute; trị gia tăng;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Căn cứ Nghị định số 118/2008/NĐ-CP ng&amp;agrave;y 27 th&amp;aacute;ng 11 năm 2008 của Ch&amp;iacute;nh phủ quy định chức năng, nhiệm vụ, quyền hạn v&amp;agrave; cơ cấu tổ chức của Bộ T&amp;agrave;i ch&amp;iacute;nh;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Theo đề nghị của Tổng cục trưởng Tổng cục Thuế,&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Bộ trưởng Bộ T&amp;agrave;i ch&amp;iacute;nh hướng dẫn thi h&amp;agrave;nh về thuế gi&amp;aacute; trị gia tăng như sau:&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Chương I&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px; text-align: center;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-family: Arial, sans-serif;&quot;&gt;QUY ĐỊNH CHUNG&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Điều 1. Phạm vi điều chỉnh&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Th&amp;ocirc;ng tư n&amp;agrave;y hướng dẫn về đối tượng chịu thuế, đối tượng kh&amp;ocirc;ng chịu thuế, người nộp thuế, căn cứ v&amp;agrave; phương ph&amp;aacute;p t&amp;iacute;nh thuế, khấu trừ, ho&amp;agrave;n thuế v&amp;agrave; nơi nộp thuế gi&amp;aacute; trị gia tăng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Điều 2. Đối tượng chịu thuế&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Đối tượng chịu thuế gi&amp;aacute; trị gia tăng (GTGT) l&amp;agrave; h&amp;agrave;ng h&amp;oacute;a, dịch vụ d&amp;ugrave;ng cho sản xuất, kinh doanh v&amp;agrave; ti&amp;ecirc;u d&amp;ugrave;ng ở Việt Nam (bao gồm cả h&amp;agrave;ng h&amp;oacute;a, dịch vụ mua của tổ chức, c&amp;aacute; nh&amp;acirc;n ở nước ngo&amp;agrave;i), trừ c&amp;aacute;c đối tượng kh&amp;ocirc;ng chịu thuế GTGT hướng dẫn tại Điều 4 Th&amp;ocirc;ng tư n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Điều 3. Người nộp thuế&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Người nộp thuế GTGT l&amp;agrave; tổ chức, c&amp;aacute; nh&amp;acirc;n sản xuất, kinh doanh h&amp;agrave;ng h&amp;oacute;a, dịch vụ chịu thuế GTGT ở Việt Nam, kh&amp;ocirc;ng ph&amp;acirc;n biệt ng&amp;agrave;nh nghề, h&amp;igrave;nh thức, tổ chức kinh doanh (sau đ&amp;acirc;y gọi l&amp;agrave; cơ sở kinh doanh) v&amp;agrave; tổ chức, c&amp;aacute; nh&amp;acirc;n nhập khẩu h&amp;agrave;ng h&amp;oacute;a, mua dịch vụ từ nước ngo&amp;agrave;i chịu thuế GTGT (sau đ&amp;acirc;y gọi l&amp;agrave; người nhập khẩu) bao gồm:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;1. C&amp;aacute;c tổ chức kinh doanh được th&amp;agrave;nh lập v&amp;agrave; đăng k&amp;yacute; kinh doanh theo Luật Doanh nghiệp, Luật Doanh nghiệp Nh&amp;agrave; nước (nay l&amp;agrave; Luật Doanh nghiệp), Luật Hợp t&amp;aacute;c x&amp;atilde; v&amp;agrave; ph&amp;aacute;p luật kinh doanh chuy&amp;ecirc;n ng&amp;agrave;nh kh&amp;aacute;c;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;2. C&amp;aacute;c tổ chức kinh tế của tổ chức ch&amp;iacute;nh trị, tổ chức ch&amp;iacute;nh trị - x&amp;atilde; hội, tổ chức x&amp;atilde; hội, tổ chức x&amp;atilde; hội - nghề nghiệp, đơn vị vũ trang nh&amp;acirc;n d&amp;acirc;n, tổ chức sự nghiệp v&amp;agrave; c&amp;aacute;c tổ chức kh&amp;aacute;c;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;3. C&amp;aacute;c doanh nghiệp c&amp;oacute; vốn đầu tư nước ngo&amp;agrave;i v&amp;agrave; b&amp;ecirc;n nước ngo&amp;agrave;i tham gia hợp t&amp;aacute;c kinh doanh theo Luật đầu tư nước ngo&amp;agrave;i tại Việt Nam (nay l&amp;agrave; Luật đầu tư); c&amp;aacute;c tổ chức, c&amp;aacute; nh&amp;acirc;n nước ngo&amp;agrave;i hoạt động kinh doanh ở Việt Nam nhưng kh&amp;ocirc;ng th&amp;agrave;nh lập ph&amp;aacute;p nh&amp;acirc;n tại Việt Nam;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;4. C&amp;aacute; nh&amp;acirc;n, hộ gia đ&amp;igrave;nh, nh&amp;oacute;m người kinh doanh độc lập v&amp;agrave; c&amp;aacute;c đối tượng kh&amp;aacute;c c&amp;oacute; hoạt động sản xuất, kinh doanh, nhập khẩu;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;5. Tổ chức, c&amp;aacute; nh&amp;acirc;n sản xuất kinh doanh tại Việt Nam mua dịch vụ (kể cả trường hợp mua dịch vụ gắn với h&amp;agrave;ng h&amp;oacute;a) của tổ chức nước ngo&amp;agrave;i kh&amp;ocirc;ng c&amp;oacute; cơ sở thường tr&amp;uacute; tại Việt Nam, c&amp;aacute; nh&amp;acirc;n ở nước ngo&amp;agrave;i l&amp;agrave; đối tượng kh&amp;ocirc;ng cư tr&amp;uacute; tại Việt Nam th&amp;igrave; tổ chức, c&amp;aacute; nh&amp;acirc;n mua dịch vụ l&amp;agrave; người nộp thuế, trừ trường hợp kh&amp;ocirc;ng phải k&amp;ecirc; khai, t&amp;iacute;nh nộp thuế GTGT hướng dẫn tại khoản 2 Điều 5 Th&amp;ocirc;ng tư n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Quy định về cơ sở thường tr&amp;uacute; v&amp;agrave; đối tượng kh&amp;ocirc;ng cư tr&amp;uacute; thực hiện theo ph&amp;aacute;p luật về thuế thu nhập doanh nghiệp v&amp;agrave; ph&amp;aacute;p luật về thuế thu nhập c&amp;aacute; nh&amp;acirc;n.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;6. Chi nh&amp;aacute;nh của doanh nghiệp chế xuất được th&amp;agrave;nh lập để hoạt động mua b&amp;aacute;n h&amp;agrave;ng h&amp;oacute;a v&amp;agrave; c&amp;aacute;c hoạt động li&amp;ecirc;n quan trực tiếp đến mua b&amp;aacute;n h&amp;agrave;ng h&amp;oacute;a tại Việt Nam theo quy định của ph&amp;aacute;p luật về khu c&amp;ocirc;ng nghiệp, khu chế xuất v&amp;agrave; khu kinh tế.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 1: C&amp;ocirc;ng ty TNHH Sanko l&amp;agrave; doanh nghiệp chế xuất. Ngo&amp;agrave;i hoạt động sản xuất để xuất khẩu C&amp;ocirc;ng ty TNHH Sanko c&amp;ograve;n được cấp ph&amp;eacute;p thực hiện quyền nhập khẩu để b&amp;aacute;n ra hoặc để xuất khẩu, C&amp;ocirc;ng ty TNHH Sanko phải th&amp;agrave;nh lập chi nh&amp;aacute;nh để thực hiện hoạt động n&amp;agrave;y theo quy định của ph&amp;aacute;p luật th&amp;igrave; Chi nh&amp;aacute;nh hạch to&amp;aacute;n ri&amp;ecirc;ng v&amp;agrave; k&amp;ecirc; khai, nộp thuế GTGT ri&amp;ecirc;ng đối với hoạt động n&amp;agrave;y, kh&amp;ocirc;ng hạch to&amp;aacute;n chung v&amp;agrave;o hoạt động sản xuất để xuất khẩu.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Khi nhập khẩu h&amp;agrave;ng h&amp;oacute;a để thực hiện ph&amp;acirc;n phối (b&amp;aacute;n ra), Chi nh&amp;aacute;nh C&amp;ocirc;ng ty TNHH Sanko thực hiện k&amp;ecirc; khai, nộp thuế GTGT ở kh&amp;acirc;u nhập khẩu v&amp;agrave; khi b&amp;aacute;n ra (bao gồm cả xuất khẩu), C&amp;ocirc;ng ty TNHH Sanko sử dụng h&amp;oacute;a đơn, k&amp;ecirc; khai, nộp thuế GTGT theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Điều 4. Đối tượng kh&amp;ocirc;ng chịu thuế GTGT&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;1. Sản phẩm trồng trọt (bao gồm cả sản phẩm rừng trồng), chăn nu&amp;ocirc;i, thủy sản, hải sản nu&amp;ocirc;i trồng, đ&amp;aacute;nh bắt chưa chế biến th&amp;agrave;nh c&amp;aacute;c sản phẩm kh&amp;aacute;c hoặc chỉ qua sơ chế th&amp;ocirc;ng thường của tổ chức, c&amp;aacute; nh&amp;acirc;n tự sản xuất, đ&amp;aacute;nh bắt b&amp;aacute;n ra v&amp;agrave; ở kh&amp;acirc;u nhập khẩu.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;C&amp;aacute;c sản phẩm mới qua sơ chế th&amp;ocirc;ng thường l&amp;agrave; sản phẩm mới được l&amp;agrave;m sạch, phơi, sấy kh&amp;ocirc;, b&amp;oacute;c vỏ, xay, xay bỏ vỏ, x&amp;aacute;t bỏ vỏ, t&amp;aacute;ch hạt, t&amp;aacute;ch cọng, cắt, ướp muối, bảo quản lạnh (ướp lạnh, đ&amp;ocirc;ng lạnh), bảo quản bằng kh&amp;iacute; sunfuro, bảo quản theo phương thức cho h&amp;oacute;a chất để tr&amp;aacute;nh thối rữa, ng&amp;acirc;m trong dung dịch lưu huỳnh hoặc ng&amp;acirc;m trong dung dịch bảo quản kh&amp;aacute;c v&amp;agrave; c&amp;aacute;c h&amp;igrave;nh thức bảo quản th&amp;ocirc;ng thường kh&amp;aacute;c.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 2: C&amp;ocirc;ng ty A k&amp;yacute; hợp đồng nu&amp;ocirc;i heo với C&amp;ocirc;ng ty B theo h&amp;igrave;nh thức nhận tiền c&amp;ocirc;ng từ C&amp;ocirc;ng ty B hoặc b&amp;aacute;n lại sản phẩm cho C&amp;ocirc;ng ty B th&amp;igrave; tiền c&amp;ocirc;ng nu&amp;ocirc;i heo nhận từ C&amp;ocirc;ng ty B v&amp;agrave; sản phẩm heo b&amp;aacute;n cho C&amp;ocirc;ng ty B thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp C&amp;ocirc;ng ty B b&amp;aacute;n ra hoặc đưa v&amp;agrave;o chế biến để b&amp;aacute;n ra th&amp;igrave; sản phẩm b&amp;aacute;n ra thuộc đối tượng chịu thuế GTGT theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;2. Sản phẩm l&amp;agrave; giống vật nu&amp;ocirc;i, giống c&amp;acirc;y trồng, bao gồm trứng giống, con giống, c&amp;acirc;y giống, hạt giống, c&amp;agrave;nh giống, củ giống, tinh dịch, ph&amp;ocirc;i, vật liệu di truyền ở c&amp;aacute;c kh&amp;acirc;u nu&amp;ocirc;i trồng, nhập khẩu v&amp;agrave; kinh doanh thương mại. Sản phẩm giống vật nu&amp;ocirc;i, giống c&amp;acirc;y trồng thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT l&amp;agrave; sản phẩm do c&amp;aacute;c cơ sở nhập khẩu, kinh doanh thương mại c&amp;oacute; giấy đăng k&amp;yacute; kinh doanh giống vật nu&amp;ocirc;i, giống c&amp;acirc;y trồng do cơ quan quản l&amp;yacute; nh&amp;agrave; nước cấp. Đối với sản phẩm l&amp;agrave; giống vật nu&amp;ocirc;i, giống c&amp;acirc;y trồng thuộc loại Nh&amp;agrave; nước ban h&amp;agrave;nh ti&amp;ecirc;u chuẩn, chất lượng phải đ&amp;aacute;p ứng c&amp;aacute;c điều kiện do nh&amp;agrave; nước quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;3. Tưới, ti&amp;ecirc;u nước; c&amp;agrave;y, bừa đất; nạo v&amp;eacute;t k&amp;ecirc;nh, mương nội đồng phục vụ sản xuất n&amp;ocirc;ng nghiệp; dịch vụ thu hoạch sản phẩm n&amp;ocirc;ng nghiệp.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;4. Sản phẩm muối được sản xuất từ nước biển, muối mỏ tự nhi&amp;ecirc;n, muối tinh, muối i-ốt m&amp;agrave; th&amp;agrave;nh phần ch&amp;iacute;nh l&amp;agrave; Na-tri-clo-rua (NaCl).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;5. Nh&amp;agrave; ở thuộc sở hữu nh&amp;agrave; nước do Nh&amp;agrave; nước b&amp;aacute;n cho người đang thu&amp;ecirc;.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;6. Chuyển quyền sử dụng đất.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;7. Bảo hiểm nh&amp;acirc;n thọ, bảo hiểm sức khoẻ, bảo hiểm người học, c&amp;aacute;c dịch vụ bảo hiểm kh&amp;aacute;c li&amp;ecirc;n quan đến con người; bảo hiểm vật nu&amp;ocirc;i, bảo hiểm c&amp;acirc;y trồng, c&amp;aacute;c dịch vụ bảo hiểm n&amp;ocirc;ng nghiệp kh&amp;aacute;c; bảo hiểm t&amp;agrave;u, thuyền, trang thiết bị v&amp;agrave; c&amp;aacute;c dụng cụ cần thiết kh&amp;aacute;c phục vụ trực tiếp đ&amp;aacute;nh bắt thủy sản; t&amp;aacute;i bảo hiểm.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;8. C&amp;aacute;c dịch vụ t&amp;agrave;i ch&amp;iacute;nh, ng&amp;acirc;n h&amp;agrave;ng, kinh doanh chứng kho&amp;aacute;n sau đ&amp;acirc;y:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;a) Dịch vụ cấp t&amp;iacute;n dụng gồm c&amp;aacute;c h&amp;igrave;nh thức:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Cho vay;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Chiết khấu, t&amp;aacute;i chiết khấu c&amp;ocirc;ng cụ chuyển nhượng v&amp;agrave; c&amp;aacute;c giấy tờ c&amp;oacute; gi&amp;aacute; kh&amp;aacute;c;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Bảo l&amp;atilde;nh ng&amp;acirc;n h&amp;agrave;ng;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Cho thu&amp;ecirc; t&amp;agrave;i ch&amp;iacute;nh;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Ph&amp;aacute;t h&amp;agrave;nh thẻ t&amp;iacute;n dụng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp tổ chức t&amp;iacute;n dụng thu c&amp;aacute;c loại ph&amp;iacute; li&amp;ecirc;n quan đến ph&amp;aacute;t h&amp;agrave;nh thẻ t&amp;iacute;n dụng th&amp;igrave; c&amp;aacute;c khoản ph&amp;iacute; thu từ kh&amp;aacute;ch h&amp;agrave;ng thuộc quy tr&amp;igrave;nh dịch vụ cấp t&amp;iacute;n dụng (ph&amp;iacute; ph&amp;aacute;t h&amp;agrave;nh thẻ) theo quy chế cho vay của tổ chức t&amp;iacute;n dụng đối với kh&amp;aacute;ch h&amp;agrave;ng như ph&amp;iacute; trả nợ trước hạn, phạt chậm trả nợ, cơ cấu lại nợ, quản l&amp;yacute; khoản vay v&amp;agrave; c&amp;aacute;c khoản ph&amp;iacute; kh&amp;aacute;c thuộc quy tr&amp;igrave;nh cấp t&amp;iacute;n dụng thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;C&amp;aacute;c khoản ph&amp;iacute; giao dịch thẻ th&amp;ocirc;ng thường kh&amp;ocirc;ng thuộc quy tr&amp;igrave;nh cấp t&amp;iacute;n dụng như ph&amp;iacute; cấp lại m&amp;atilde; pin cho thẻ t&amp;iacute;n dụng, ph&amp;iacute; cung cấp bản sao h&amp;oacute;a đơn giao dịch, ph&amp;iacute; đ&amp;ograve;i bồi ho&amp;agrave;n khi sử dụng thẻ, ph&amp;iacute; th&amp;ocirc;ng b&amp;aacute;o mất cắp, thất lạc thẻ t&amp;iacute;n dụng, ph&amp;iacute; hủy thẻ t&amp;iacute;n dụng, ph&amp;iacute; chuyển đổi loại thẻ t&amp;iacute;n dụng v&amp;agrave; c&amp;aacute;c khoản ph&amp;iacute; kh&amp;aacute;c thuộc đối tượng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Bao thanh to&amp;aacute;n trong nước; bao thanh to&amp;aacute;n quốc tế đối với c&amp;aacute;c ng&amp;acirc;n h&amp;agrave;ng được ph&amp;eacute;p thực hiện thanh to&amp;aacute;n quốc tế;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- B&amp;aacute;n t&amp;agrave;i sản bảo đảm tiền vay do tổ chức t&amp;iacute;n dụng hoặc do cơ quan thi h&amp;agrave;nh &amp;aacute;n thực hiện theo quy định của ph&amp;aacute;p luật về xử l&amp;yacute; t&amp;agrave;i sản bảo đảm tiền vay.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp hết thời gian trả nợ, người c&amp;oacute; t&amp;agrave;i sản bảo đảm kh&amp;ocirc;ng c&amp;oacute; khả năng trả nợ v&amp;agrave; phải b&amp;agrave;n giao t&amp;agrave;i sản cho tổ chức t&amp;iacute;n dụng để tổ chức t&amp;iacute;n dụng xử l&amp;yacute; t&amp;agrave;i sản bảo đảm tiền vay theo quy định của ph&amp;aacute;p luật th&amp;igrave; c&amp;aacute;c b&amp;ecirc;n thực hiện thủ tục b&amp;agrave;n giao t&amp;agrave;i sản bảo đảm theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp c&amp;aacute;c b&amp;ecirc;n thỏa thuận người c&amp;oacute; t&amp;agrave;i sản bảo đảm tự b&amp;aacute;n t&amp;agrave;i sản bảo đảm để trả nợ, nếu người c&amp;oacute; t&amp;agrave;i sản bảo đảm l&amp;agrave; người nộp thuế GTGT v&amp;agrave; t&amp;agrave;i sản đem b&amp;aacute;n thuộc đối tượng chịu thuế GTGT th&amp;igrave; phải k&amp;ecirc; khai, nộp thuế GTGT theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp tổ chức t&amp;iacute;n dụng nhận t&amp;agrave;i sản bảo đảm để thay thế cho việc thực hiện nghĩa vụ trả nợ th&amp;igrave; tổ chức t&amp;iacute;n dụng thực hiện hạch to&amp;aacute;n tăng gi&amp;aacute; trị t&amp;agrave;i sản phục vụ sản xuất kinh doanh theo quy định. Khi tổ chức t&amp;iacute;n dụng b&amp;aacute;n t&amp;agrave;i sản phục vụ hoạt động kinh doanh nếu t&amp;agrave;i sản thuộc đối tượng chịu thuế GTGT th&amp;igrave; tổ chức t&amp;iacute;n dụng phải k&amp;ecirc; khai, nộp thuế GTGT theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 3: Th&amp;aacute;ng 3/2014 C&amp;ocirc;ng ty TNHH A l&amp;agrave; cơ sở kinh doanh nộp thuế GTGT theo phương ph&amp;aacute;p khấu trừ thế chấp d&amp;acirc;y chuyền, m&amp;aacute;y m&amp;oacute;c thiết bị để vay vốn tại Ng&amp;acirc;n h&amp;agrave;ng B, thời gian vay l&amp;agrave; 1 năm (hạn trả nợ l&amp;agrave; ng&amp;agrave;y 31/3/2015). Đến ng&amp;agrave;y 31/3/2015, C&amp;ocirc;ng ty TNHH A kh&amp;ocirc;ng c&amp;oacute; khả năng trả nợ v&amp;agrave; phải b&amp;agrave;n giao t&amp;agrave;i sản cho Ng&amp;acirc;n h&amp;agrave;ng B th&amp;igrave; khi b&amp;agrave;n giao t&amp;agrave;i sản, C&amp;ocirc;ng ty TNHH A thực hiện thủ tục b&amp;agrave;n giao t&amp;agrave;i sản bảo đảm theo quy định của ph&amp;aacute;p luật về việc xử l&amp;yacute; t&amp;agrave;i sản bảo đảm. Ng&amp;acirc;n h&amp;agrave;ng B b&amp;aacute;n t&amp;agrave;i sản bảo đảm tiền vay để thu hồi nợ th&amp;igrave; t&amp;agrave;i sản b&amp;aacute;n ra thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Dịch vụ cung cấp th&amp;ocirc;ng tin t&amp;iacute;n dụng do đơn vị, tổ chức thuộc Ng&amp;acirc;n h&amp;agrave;ng nh&amp;agrave; nước cung cấp cho c&amp;aacute;c tổ chức t&amp;iacute;n dụng để sử dụng trong hoạt động cấp t&amp;iacute;n dụng theo quy định của Luật Ng&amp;acirc;n h&amp;agrave;ng nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 4: Tổ chức X l&amp;agrave; đơn vị thuộc Ng&amp;acirc;n h&amp;agrave;ng Nh&amp;agrave; nước được Ng&amp;acirc;n h&amp;agrave;ng Nh&amp;agrave; nước cho ph&amp;eacute;p thực hiện dịch vụ cung cấp th&amp;ocirc;ng tin t&amp;iacute;n dụng. Năm 2014, tổ chức X k&amp;yacute; hợp đồng cung cấp th&amp;ocirc;ng tin t&amp;iacute;n dụng cho một số ng&amp;acirc;n h&amp;agrave;ng thương mại phục vụ hoạt động cấp t&amp;iacute;n dụng v&amp;agrave; phục vụ hoạt động kh&amp;aacute;c của ng&amp;acirc;n h&amp;agrave;ng thương mại th&amp;igrave; doanh thu từ dịch vụ cung cấp th&amp;ocirc;ng tin t&amp;iacute;n dụng phục vụ hoạt động cấp t&amp;iacute;n dụng thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT, doanh thu từ dịch vụ cung cấp th&amp;ocirc;ng tin t&amp;iacute;n dụng phục vụ hoạt động kh&amp;aacute;c của ng&amp;acirc;n h&amp;agrave;ng thương mại kh&amp;ocirc;ng theo quy định của Luật Ng&amp;acirc;n h&amp;agrave;ng Nh&amp;agrave; nước thuộc đối tượng chịu thuế GTGT với thuế suất 10%.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- H&amp;igrave;nh thức cấp t&amp;iacute;n dụng kh&amp;aacute;c theo quy định của ph&amp;aacute;p luật.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;b) Hoạt động cho vay ri&amp;ecirc;ng lẻ, kh&amp;ocirc;ng phải hoạt động kinh doanh, cung ứng thường xuy&amp;ecirc;n của người nộp thuế kh&amp;ocirc;ng phải l&amp;agrave; tổ chức t&amp;iacute;n dụng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 5: C&amp;ocirc;ng ty cổ phần VC c&amp;oacute; tiền nh&amp;agrave;n rỗi tạm thời chưa phục vụ hoạt động kinh doanh, C&amp;ocirc;ng ty cổ phần VC k&amp;yacute; hợp đồng cho C&amp;ocirc;ng ty T vay tiền trong thời hạn 6 th&amp;aacute;ng v&amp;agrave; được nhận khoản tiền l&amp;atilde;i th&amp;igrave; khoản tiền l&amp;atilde;i C&amp;ocirc;ng ty cổ phần VC nhận được thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;c) Kinh doanh chứng kho&amp;aacute;n bao gồm: M&amp;ocirc;i giới chứng kho&amp;aacute;n, tự doanh chứng kho&amp;aacute;n, bảo l&amp;atilde;nh ph&amp;aacute;t h&amp;agrave;nh chứng kho&amp;aacute;n, tư vấn đầu tư chứng kho&amp;aacute;n, lưu k&amp;yacute; chứng kho&amp;aacute;n, quản l&amp;yacute; quỹ đầu tư chứng kho&amp;aacute;n, quản l&amp;yacute; c&amp;ocirc;ng ty đầu tư chứng kho&amp;aacute;n, quản l&amp;yacute; danh mục đầu tư chứng kho&amp;aacute;n, dịch vụ tổ chức thị trường của sở giao dịch chứng kho&amp;aacute;n hoặc trung t&amp;acirc;m giao dịch chứng kho&amp;aacute;n, dịch vụ li&amp;ecirc;n quan đến chứng kho&amp;aacute;n đăng k&amp;yacute;, lưu k&amp;yacute; tại Trung t&amp;acirc;m Lưu k&amp;yacute; Chứng kho&amp;aacute;n Việt Nam, cho kh&amp;aacute;ch h&amp;agrave;ng vay tiền để thực hiện giao dịch k&amp;yacute; quỹ, ứng trước tiền b&amp;aacute;n chứng kho&amp;aacute;n v&amp;agrave; hoạt động kinh doanh chứng kho&amp;aacute;n kh&amp;aacute;c theo quy định của ph&amp;aacute;p luật về chứng kho&amp;aacute;n.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Hoạt động cung cấp th&amp;ocirc;ng tin, tổ chức b&amp;aacute;n đấu gi&amp;aacute; cổ phần của c&amp;aacute;c tổ chức ph&amp;aacute;t h&amp;agrave;nh, hỗ trợ kỹ thuật phục vụ giao dịch chứng kho&amp;aacute;n trực tuyến của Sở Giao dịch chứng kho&amp;aacute;n.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;d) Chuyển nhượng vốn bao gồm việc chuyển nhượng một phần hoặc to&amp;agrave;n bộ số vốn đ&amp;atilde; đầu tư v&amp;agrave;o tổ chức kinh tế kh&amp;aacute;c (kh&amp;ocirc;ng ph&amp;acirc;n biệt c&amp;oacute; th&amp;agrave;nh lập hay kh&amp;ocirc;ng th&amp;agrave;nh lập ph&amp;aacute;p nh&amp;acirc;n mới), chuyển nhượng chứng kho&amp;aacute;n, chuyển nhượng quyền g&amp;oacute;p vốn v&amp;agrave; c&amp;aacute;c h&amp;igrave;nh thức chuyển nhượng vốn kh&amp;aacute;c theo quy định của ph&amp;aacute;p luật, kể cả trường hợp b&amp;aacute;n doanh nghiệp cho doanh nghiệp kh&amp;aacute;c để sản xuất kinh doanh v&amp;agrave; doanh nghiệp mua kế thừa to&amp;agrave;n bộ quyền v&amp;agrave; nghĩa vụ của doanh nghiệp b&amp;aacute;n theo quy định của ph&amp;aacute;p luật.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 6: Th&amp;aacute;ng 4/2014, C&amp;ocirc;ng ty TNHH A g&amp;oacute;p vốn bằng m&amp;aacute;y m&amp;oacute;c, thiết bị để th&amp;agrave;nh lập C&amp;ocirc;ng ty cổ phần B, gi&amp;aacute; trị vốn g&amp;oacute;p của C&amp;ocirc;ng ty TNHH A được Hội đồng giao nhận vốn g&amp;oacute;p của c&amp;aacute;c b&amp;ecirc;n g&amp;oacute;p vốn định gi&amp;aacute; l&amp;agrave; 2,5 tỷ đồng, bằng 25% số vốn của C&amp;ocirc;ng ty cổ phần B. Th&amp;aacute;ng 11/2014, C&amp;ocirc;ng ty TNHH A b&amp;aacute;n phần vốn g&amp;oacute;p tại C&amp;ocirc;ng ty cổ phần B cho Quỹ Đầu tư ABB với gi&amp;aacute; 4 tỷ đồng th&amp;igrave; số tiền 4 tỷ đồng C&amp;ocirc;ng ty TNHH A thu được l&amp;agrave; doanh thu chuyển nhượng vốn thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;đ) B&amp;aacute;n nợ;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;e) Kinh doanh ngoại tệ;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;g) Dịch vụ t&amp;agrave;i ch&amp;iacute;nh ph&amp;aacute;i sinh bao gồm: h&amp;oacute;an đổi l&amp;atilde;i suất; hợp đồng kỳ hạn; hợp đồng tương lai; quyền chọn mua, b&amp;aacute;n ngoại tệ; c&amp;aacute;c dịch vụ t&amp;agrave;i ch&amp;iacute;nh ph&amp;aacute;i sinh kh&amp;aacute;c theo quy định của ph&amp;aacute;p luật;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;h) Bán t&amp;agrave;i sản bảo đảm của khoản nợ của tổ chức m&amp;agrave; Nh&amp;agrave; nước sở hữu 100% vốn điều lệ do Ch&amp;iacute;nh phủ th&amp;agrave;nh lập để xử l&amp;yacute; nợ xấu của c&amp;aacute;c tổ chức t&amp;iacute;n dụng Việt Nam.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;9. Dịch vụ y tế, dịch vụ th&amp;uacute; y, bao gồm dịch vụ kh&amp;aacute;m bệnh, chữa bệnh, ph&amp;ograve;ng bệnh cho người v&amp;agrave; vật nu&amp;ocirc;i, dịch vụ sinh đẻ c&amp;oacute; kế hoạch, dịch vụ điều dưỡng sức khoẻ, phục hồi chức năng cho người bệnh, vận chuyển người bệnh, dịch vụ cho thu&amp;ecirc; ph&amp;ograve;ng bệnh, giường bệnh của c&amp;aacute;c cơ sở y tế; x&amp;eacute;t nghiệm, chiếu, chụp, m&amp;aacute;u v&amp;agrave; chế phẩm m&amp;aacute;u d&amp;ugrave;ng cho người bệnh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp trong g&amp;oacute;i dịch vụ chữa bệnh (theo quy định của Bộ Y tế) bao gồm cả sử dụng thuốc chữa bệnh th&amp;igrave; khoản thu từ tiền thuốc chữa bệnh nằm trong g&amp;oacute;i dịch vụ chữa bệnh cũng thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;10. Dịch vụ bưu ch&amp;iacute;nh, viễn th&amp;ocirc;ng c&amp;ocirc;ng &amp;iacute;ch v&amp;agrave; Internet phổ cập theo chương tr&amp;igrave;nh của Ch&amp;iacute;nh phủ; Dịch vụ bưu ch&amp;iacute;nh, viễn th&amp;ocirc;ng từ nước ngo&amp;agrave;i v&amp;agrave;o Việt Nam (chiều đến).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;11. Dịch vụ duy tr&amp;igrave; vườn th&amp;uacute;, vườn hoa, c&amp;ocirc;ng vi&amp;ecirc;n, c&amp;acirc;y xanh đường phố, chiếu s&amp;aacute;ng c&amp;ocirc;ng cộng; dịch vụ tang lễ. C&amp;aacute;c dịch vụ n&amp;ecirc;u tại khoản n&amp;agrave;y kh&amp;ocirc;ng ph&amp;acirc;n biệt nguồn kinh ph&amp;iacute; chi trả. Cụ thể:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;a) Dịch vụ duy tr&amp;igrave; vườn th&amp;uacute;, vườn hoa, c&amp;ocirc;ng vi&amp;ecirc;n, c&amp;acirc;y xanh đường phố, bảo vệ rừng do Nh&amp;agrave; nước l&amp;agrave;m chủ rừng, bao gồm hoạt động quản l&amp;yacute;, trồng c&amp;acirc;y, chăm s&amp;oacute;c c&amp;acirc;y, bảo vệ chim, th&amp;uacute; ở c&amp;aacute;c c&amp;ocirc;ng vi&amp;ecirc;n, vườn th&amp;uacute;, khu vực c&amp;ocirc;ng cộng, rừng quốc gia, vườn quốc gia;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;b) Chiếu s&amp;aacute;ng c&amp;ocirc;ng cộng bao gồm chiếu s&amp;aacute;ng đường phố, ng&amp;otilde;, x&amp;oacute;m trong khu d&amp;acirc;n cư, vườn hoa, c&amp;ocirc;ng vi&amp;ecirc;n. Doanh thu kh&amp;ocirc;ng chịu thuế l&amp;agrave; doanh thu từ hoạt động chiếu s&amp;aacute;ng c&amp;ocirc;ng cộng;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;c) Dịch vụ tang lễ của c&amp;aacute;c cơ sở c&amp;oacute; chức năng kinh doanh dịch vụ tang lễ bao gồm c&amp;aacute;c hoạt động cho thu&amp;ecirc; nh&amp;agrave; tang lễ, xe &amp;ocirc; t&amp;ocirc; phục vụ tang lễ, mai t&amp;aacute;ng, hỏa t&amp;aacute;ng, cải t&amp;aacute;ng, di chuyển mộ, chăm s&amp;oacute;c mộ.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;12. Duy tu, sửa chữa, x&amp;acirc;y dựng bằng nguồn vốn đ&amp;oacute;ng g&amp;oacute;p của nh&amp;acirc;n d&amp;acirc;n (bao gồm cả vốn đ&amp;oacute;ng g&amp;oacute;p, t&amp;agrave;i trợ của tổ chức, c&amp;aacute; nh&amp;acirc;n), vốn viện trợ nh&amp;acirc;n đạo đối với c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh văn h&amp;oacute;a, nghệ thuật, c&amp;ocirc;ng tr&amp;igrave;nh phục vụ c&amp;ocirc;ng cộng, cơ sở hạ tầng v&amp;agrave; nh&amp;agrave; ở cho đối tượng ch&amp;iacute;nh s&amp;aacute;ch x&amp;atilde; hội.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp c&amp;oacute; sử dụng nguồn vốn kh&amp;aacute;c ngo&amp;agrave;i vốn đ&amp;oacute;ng g&amp;oacute;p của nh&amp;acirc;n d&amp;acirc;n, vốn viện trợ nh&amp;acirc;n đạo m&amp;agrave; nguồn vốn kh&amp;aacute;c kh&amp;ocirc;ng vượt qu&amp;aacute; 50% tổng số nguồn vốn sử dụng cho c&amp;ocirc;ng tr&amp;igrave;nh th&amp;igrave; đối tượng kh&amp;ocirc;ng chịu thuế l&amp;agrave; to&amp;agrave;n bộ gi&amp;aacute; trị c&amp;ocirc;ng tr&amp;igrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp c&amp;oacute; sử dụng nguồn vốn kh&amp;aacute;c ngo&amp;agrave;i vốn đ&amp;oacute;ng g&amp;oacute;p của nh&amp;acirc;n d&amp;acirc;n, vốn viện trợ nh&amp;acirc;n đạo m&amp;agrave; nguồn vốn kh&amp;aacute;c vượt qu&amp;aacute; 50% tổng số nguồn vốn sử dụng cho c&amp;ocirc;ng tr&amp;igrave;nh th&amp;igrave; to&amp;agrave;n bộ gi&amp;aacute; trị c&amp;ocirc;ng tr&amp;igrave;nh thuộc đối tượng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Đối tượng ch&amp;iacute;nh s&amp;aacute;ch x&amp;atilde; hội bao gồm: người c&amp;oacute; c&amp;ocirc;ng theo quy định của ph&amp;aacute;p luật về người c&amp;oacute; c&amp;ocirc;ng; đối tượng bảo trợ x&amp;atilde; hội hưởng trợ cấp từ ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước; người thuộc hộ ngh&amp;egrave;o, cận ngh&amp;egrave;o v&amp;agrave; c&amp;aacute;c trường hợp kh&amp;aacute;c theo quy định của ph&amp;aacute;p luật.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;13. Dạy học, dạy nghề theo quy định của ph&amp;aacute;p luật bao gồm cả dạy ngoại ngữ, tin học; dạy m&amp;uacute;a, h&amp;aacute;t, hội họa, nhạc, kịch, xiếc, thể dục, thể thao; nu&amp;ocirc;i dạy trẻ v&amp;agrave; dạy c&amp;aacute;c nghề kh&amp;aacute;c nhằm đ&amp;agrave;o tạo, bồi dưỡng n&amp;acirc;ng cao tr&amp;igrave;nh độ văn h&amp;oacute;a, kiến thức chuy&amp;ecirc;n m&amp;ocirc;n nghề nghiệp.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp c&amp;aacute;c cơ sở dạy học c&amp;aacute;c cấp từ mầm non đến trung học phổ th&amp;ocirc;ng c&amp;oacute; thu tiền ăn, tiền vận chuyển đưa đ&amp;oacute;n học sinh v&amp;agrave; c&amp;aacute;c khoản thu kh&amp;aacute;c dưới h&amp;igrave;nh thức thu hộ, chi hộ th&amp;igrave; tiền ăn, tiền vận chuyển đưa đ&amp;oacute;n học sinh v&amp;agrave; c&amp;aacute;c khoản thu hộ, chi hộ n&amp;agrave;y cũng thuộc đối tượng kh&amp;ocirc;ng chịu thuế.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Khoản thu về ở nội tr&amp;uacute; của học sinh, sinh vi&amp;ecirc;n, học vi&amp;ecirc;n; hoạt động đ&amp;agrave;o tạo (bao gồm cả việc tổ chức thi v&amp;agrave; cấp chứng chỉ trong quy tr&amp;igrave;nh đ&amp;agrave;o tạo) do cơ sở đ&amp;agrave;o tạo cung cấp thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT. Trường hợp cơ sở đ&amp;agrave;o tạo kh&amp;ocirc;ng trực tiếp tổ chức đ&amp;agrave;o tạo m&amp;agrave; chỉ tổ chức thi, cấp chứng chỉ trong quy tr&amp;igrave;nh đ&amp;agrave;o tạo th&amp;igrave; hoạt động tổ chức thi v&amp;agrave; cấp chứng chỉ cũng thuộc đối tượng kh&amp;ocirc;ng chịu thuế. Trường hợp cung cấp dịch vụ thi v&amp;agrave; cấp chứng chỉ kh&amp;ocirc;ng thuộc quy tr&amp;igrave;nh đ&amp;agrave;o tạo th&amp;igrave; thuộc đối tượng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 7: Trung t&amp;acirc;m đ&amp;agrave;o tạo X được cấp c&amp;oacute; thẩm quyền giao nhiệm vụ đ&amp;agrave;o tạo để cấp chứng chỉ h&amp;agrave;nh nghề về đại l&amp;yacute; bảo hiểm. Trung t&amp;acirc;m đ&amp;agrave;o tạo X giao nhiệm vụ đ&amp;agrave;o tạo cho đơn vị Y thực hiện, Trung t&amp;acirc;m đ&amp;agrave;o tạo X tổ chức thi v&amp;agrave; cấp chứng chỉ h&amp;agrave;nh nghề đại l&amp;yacute; bảo hiểm th&amp;igrave; hoạt động tổ chức thi v&amp;agrave; cấp chứng chỉ của Trung t&amp;acirc;m đ&amp;agrave;o tạo X thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;14. Ph&amp;aacute;t s&amp;oacute;ng truyền thanh, truyền h&amp;igrave;nh bằng nguồn vốn ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;15. Xuất bản, nhập khẩu, ph&amp;aacute;t h&amp;agrave;nh b&amp;aacute;o, tạp ch&amp;iacute;, bản tin chuy&amp;ecirc;n ng&amp;agrave;nh, s&amp;aacute;ch ch&amp;iacute;nh trị, s&amp;aacute;ch gi&amp;aacute;o khoa, gi&amp;aacute;o tr&amp;igrave;nh, s&amp;aacute;ch văn bản ph&amp;aacute;p luật, s&amp;aacute;ch khoa học-kỹ thuật, s&amp;aacute;ch in bằng chữ d&amp;acirc;n tộc thiểu số v&amp;agrave; tranh, ảnh, &amp;aacute;p ph&amp;iacute;ch tuy&amp;ecirc;n truyền cổ động, kể cả dưới dạng băng hoặc đĩa ghi tiếng, ghi h&amp;igrave;nh, dữ liệu điện tử; tiền, in tiền.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;B&amp;aacute;o, tạp ch&amp;iacute;, bản tin chuy&amp;ecirc;n ng&amp;agrave;nh, bao gồm cả hoạt động truyền trang b&amp;aacute;o, tạp ch&amp;iacute;, bản tin chuy&amp;ecirc;n ng&amp;agrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;S&amp;aacute;ch ch&amp;iacute;nh trị l&amp;agrave; s&amp;aacute;ch tuy&amp;ecirc;n truyền đường lối ch&amp;iacute;nh trị của Đảng v&amp;agrave; Nh&amp;agrave; nước phục vụ nhiệm vụ ch&amp;iacute;nh trị theo chuy&amp;ecirc;n đề, chủ đề, phục vụ c&amp;aacute;c ng&amp;agrave;y kỷ niệm, ng&amp;agrave;y truyền thống của c&amp;aacute;c tổ chức, c&amp;aacute;c cấp, c&amp;aacute;c ng&amp;agrave;nh, địa phương; c&amp;aacute;c loại s&amp;aacute;ch thống k&amp;ecirc;, tuy&amp;ecirc;n truyền phong tr&amp;agrave;o người tốt việc tốt; s&amp;aacute;ch in c&amp;aacute;c b&amp;agrave;i ph&amp;aacute;t biểu, nghi&amp;ecirc;n cứu l&amp;yacute; luận của l&amp;atilde;nh đạo Đảng v&amp;agrave; Nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;S&amp;aacute;ch gi&amp;aacute;o khoa l&amp;agrave; s&amp;aacute;ch d&amp;ugrave;ng để giảng dạy v&amp;agrave; học tập trong tất cả c&amp;aacute;c cấp từ mầm non đến trung học phổ th&amp;ocirc;ng (bao gồm cả s&amp;aacute;ch tham khảo d&amp;ugrave;ng cho gi&amp;aacute;o vi&amp;ecirc;n v&amp;agrave; học sinh ph&amp;ugrave; hợp với nội dung chương tr&amp;igrave;nh gi&amp;aacute;o dục).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Gi&amp;aacute;o tr&amp;igrave;nh l&amp;agrave; s&amp;aacute;ch d&amp;ugrave;ng để giảng dạy v&amp;agrave; học tập trong c&amp;aacute;c trường đại học, cao đẳng, trung học chuy&amp;ecirc;n nghiệp v&amp;agrave; dạy nghề.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;S&amp;aacute;ch văn bản ph&amp;aacute;p luật l&amp;agrave; s&amp;aacute;ch in c&amp;aacute;c văn bản quy phạm ph&amp;aacute;p luật của Nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;S&amp;aacute;ch khoa học-kỹ thuật l&amp;agrave; s&amp;aacute;ch d&amp;ugrave;ng để giới thiệu, hướng dẫn những kiến thức khoa học, kỹ thuật c&amp;oacute; quan hệ trực tiếp đến sản xuất v&amp;agrave; c&amp;aacute;c ng&amp;agrave;nh khoa học, kỹ thuật.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;S&amp;aacute;ch in bằng chữ d&amp;acirc;n tộc thiểu số bao gồm cả s&amp;aacute;ch in song ngữ chữ phổ th&amp;ocirc;ng v&amp;agrave; chữ d&amp;acirc;n tộc thiểu số.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Tranh, ảnh, &amp;aacute;p ph&amp;iacute;ch tuy&amp;ecirc;n truyền cổ động l&amp;agrave; tranh, ảnh, &amp;aacute;p ph&amp;iacute;ch, c&amp;aacute;c loại tờ rơi, tờ gấp phục vụ cho mục đ&amp;iacute;ch tuy&amp;ecirc;n truyền, cổ động, khẩu hiệu, ảnh l&amp;atilde;nh tụ, Đảng kỳ, Quốc kỳ, Đo&amp;agrave;n kỳ, Đội kỳ.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;16. Vận chuyển h&amp;agrave;nh kh&amp;aacute;ch c&amp;ocirc;ng cộng bằng xe bu&amp;yacute;t, xe điện l&amp;agrave; vận chuyển h&amp;agrave;nh kh&amp;aacute;ch c&amp;ocirc;ng cộng bằng xe bu&amp;yacute;t, xe điện theo c&amp;aacute;c tuyến trong nội tỉnh, trong đ&amp;ocirc; thị v&amp;agrave; c&amp;aacute;c tuyến l&amp;acirc;n cận ngoại tỉnh theo quy định của Bộ Giao th&amp;ocirc;ng vận tải.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;17. H&amp;agrave;ng h&amp;oacute;a thuộc loại trong nước chưa sản xuất được nhập khẩu trong c&amp;aacute;c trường hợp sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;a) M&amp;aacute;y m&amp;oacute;c, thiết bị, phụ t&amp;ugrave;ng, vật tư nhập khẩu để sử dụng trực tiếp cho hoạt động nghi&amp;ecirc;n cứu khoa học, ph&amp;aacute;t triển c&amp;ocirc;ng nghệ;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;b) M&amp;aacute;y m&amp;oacute;c, thiết bị, phụ t&amp;ugrave;ng thay thế, phương tiện vận tải chuy&amp;ecirc;n d&amp;ugrave;ng v&amp;agrave; vật tư cần nhập khẩu để tiến h&amp;agrave;nh hoạt động t&amp;igrave;m kiếm thăm d&amp;ograve;, ph&amp;aacute;t triển mỏ dầu, kh&amp;iacute; đốt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;c) T&amp;agrave;u bay (bao gồm cả động cơ t&amp;agrave;u bay), d&amp;agrave;n khoan, t&amp;agrave;u thủy thuộc loại trong nước chưa sản xuất được nhập khẩu để&lt;em&gt;&amp;nbsp;&lt;/em&gt;tạo t&amp;agrave;i sản cố định của doanh nghiệp hoặc thu&amp;ecirc; của nước ngo&amp;agrave;i để sử dụng cho sản xuất, kinh doanh, cho thu&amp;ecirc;, cho thu&amp;ecirc; lại.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Để x&amp;aacute;c định h&amp;agrave;ng h&amp;oacute;a thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT ở kh&amp;acirc;u nhập khẩu quy định tại khoản&lt;strong&gt;&lt;em&gt;&amp;nbsp;&lt;/em&gt;&lt;/strong&gt;n&amp;agrave;y, người nhập khẩu phải xuất tr&amp;igrave;nh cho cơ quan hải quan c&amp;aacute;c hồ sơ theo hướng dẫn của Bộ T&amp;agrave;i ch&amp;iacute;nh về thủ tục hải quan; kiểm tra, gi&amp;aacute;m s&amp;aacute;t hải quan; thuế xuất khẩu, thuế nhập khẩu v&amp;agrave; quản l&amp;yacute; thuế đối với h&amp;agrave;ng h&amp;oacute;a xuất khẩu, nhập khẩu.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Danh mục m&amp;aacute;y m&amp;oacute;c, thiết bị, phụ t&amp;ugrave;ng, vật tư thuộc loại trong nước đ&amp;atilde; sản xuất được để l&amp;agrave;m cơ sở ph&amp;acirc;n biệt với loại trong nước chưa sản xuất được cần nhập khẩu sử dụng trực tiếp cho hoạt động nghi&amp;ecirc;n cứu khoa học v&amp;agrave; ph&amp;aacute;t triển c&amp;ocirc;ng nghệ; Danh mục m&amp;aacute;y m&amp;oacute;c, thiết bị, phụ t&amp;ugrave;ng thay thế, phương tiện vận tải chuy&amp;ecirc;n d&amp;ugrave;ng v&amp;agrave; vật tư thuộc loại trong nước đ&amp;atilde; sản xuất được l&amp;agrave;m cơ sở ph&amp;acirc;n biệt với loại trong nước chưa sản xuất được cần nhập khẩu để tiến h&amp;agrave;nh hoạt động t&amp;igrave;m kiếm thăm d&amp;ograve;, ph&amp;aacute;t triển mỏ dầu, kh&amp;iacute; đốt; Danh mục t&amp;agrave;u bay, d&amp;agrave;n khoan, t&amp;agrave;u thủy thuộc loại trong nước đ&amp;atilde; sản xuất được l&amp;agrave;m cơ sở ph&amp;acirc;n biệt với loại trong nước chưa sản xuất được cần nhập khẩu để tạo t&amp;agrave;i sản cố định của doanh nghiệp hoặc thu&amp;ecirc; của nước ngo&amp;agrave;i để sử dụng cho sản xuất, kinh doanh, cho thu&amp;ecirc;, cho thu&amp;ecirc; lại do Bộ Kế hoạch v&amp;agrave; Đầu tư ban h&amp;agrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot', 'THÔNG TƯ 219/2013/TT- BTC', 'Hướng dẫn thi hành Luật Thuế giá trị gia tăng và Nghị định số 209/2013/NĐ-CP ngày 18/12/2013 của Chính phủ quy định chi tiết và hướng dẫn thi hành một số điều Luật Thuế giá trị gia tăng', '&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Hướng dẫn thi h&amp;agrave;nh Luật Thuế gi&amp;aacute; trị gia tăng v&amp;agrave; Nghị định số 209/2013/NĐ-CP ng&amp;agrave;y 18/12/2013 của Ch&amp;iacute;nh phủ quy định chi tiết v&amp;agrave; hướng dẫn thi h&amp;agrave;nh một số điều Luật Thuế gi&amp;aacute; trị gia tăng&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Căn cứ Luật Thuế gi&amp;aacute; trị gia tăng số 13/2008/QH12 ng&amp;agrave;y 03 th&amp;aacute;ng 6 năm 2008 v&amp;agrave; Luật sửa đổi, bổ sung một số điều của Luật Thuế gi&amp;aacute; trị gia tăng số 31/2013/QH13 ng&amp;agrave;y 19 th&amp;aacute;ng 6 năm 2013;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Căn cứ Luật Quản l&amp;yacute; thuế số 78/2006/QH11 ng&amp;agrave;y 29 th&amp;aacute;ng 11 năm 2006&amp;nbsp;v&amp;agrave;&amp;nbsp;Luật sửa đổi, bổ sung một số điều của Luật Quản l&amp;yacute; thuế số 21/2012/QH13 ng&amp;agrave;y 20 th&amp;aacute;ng 11 năm 2012;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Căn cứ Nghị định số 209/2013/NĐ-CP ng&amp;agrave;y 18 th&amp;aacute;ng 12 năm 2013 của Ch&amp;iacute;nh phủ quy định chi tiết v&amp;agrave; hướng dẫn thi h&amp;agrave;nh một số điều Luật Thuế gi&amp;aacute; trị gia tăng;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Căn cứ Nghị định số 118/2008/NĐ-CP ng&amp;agrave;y 27 th&amp;aacute;ng 11 năm 2008 của Ch&amp;iacute;nh phủ quy định chức năng, nhiệm vụ, quyền hạn v&amp;agrave; cơ cấu tổ chức của Bộ T&amp;agrave;i ch&amp;iacute;nh;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Theo đề nghị của Tổng cục trưởng Tổng cục Thuế,&lt;/p&gt;\r\n&lt;p style=&quot;margin: 8px 0px 15px; color: rgb(0, 0, 0); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px; line-height: 17.600000381469727px; text-align: justify;&quot;&gt;\r\n	Bộ trưởng Bộ T&amp;agrave;i ch&amp;iacute;nh hướng dẫn thi h&amp;agrave;nh về thuế gi&amp;aacute; trị gia tăng như sau:&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Chương I&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px; text-align: center;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-family: Arial, sans-serif;&quot;&gt;QUY ĐỊNH CHUNG&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Điều 1. Phạm vi điều chỉnh&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Th&amp;ocirc;ng tư n&amp;agrave;y hướng dẫn về đối tượng chịu thuế, đối tượng kh&amp;ocirc;ng chịu thuế, người nộp thuế, căn cứ v&amp;agrave; phương ph&amp;aacute;p t&amp;iacute;nh thuế, khấu trừ, ho&amp;agrave;n thuế v&amp;agrave; nơi nộp thuế gi&amp;aacute; trị gia tăng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Điều 2. Đối tượng chịu thuế&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Đối tượng chịu thuế gi&amp;aacute; trị gia tăng (GTGT) l&amp;agrave; h&amp;agrave;ng h&amp;oacute;a, dịch vụ d&amp;ugrave;ng cho sản xuất, kinh doanh v&amp;agrave; ti&amp;ecirc;u d&amp;ugrave;ng ở Việt Nam (bao gồm cả h&amp;agrave;ng h&amp;oacute;a, dịch vụ mua của tổ chức, c&amp;aacute; nh&amp;acirc;n ở nước ngo&amp;agrave;i), trừ c&amp;aacute;c đối tượng kh&amp;ocirc;ng chịu thuế GTGT hướng dẫn tại Điều 4 Th&amp;ocirc;ng tư n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Điều 3. Người nộp thuế&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Người nộp thuế GTGT l&amp;agrave; tổ chức, c&amp;aacute; nh&amp;acirc;n sản xuất, kinh doanh h&amp;agrave;ng h&amp;oacute;a, dịch vụ chịu thuế GTGT ở Việt Nam, kh&amp;ocirc;ng ph&amp;acirc;n biệt ng&amp;agrave;nh nghề, h&amp;igrave;nh thức, tổ chức kinh doanh (sau đ&amp;acirc;y gọi l&amp;agrave; cơ sở kinh doanh) v&amp;agrave; tổ chức, c&amp;aacute; nh&amp;acirc;n nhập khẩu h&amp;agrave;ng h&amp;oacute;a, mua dịch vụ từ nước ngo&amp;agrave;i chịu thuế GTGT (sau đ&amp;acirc;y gọi l&amp;agrave; người nhập khẩu) bao gồm:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;1. C&amp;aacute;c tổ chức kinh doanh được th&amp;agrave;nh lập v&amp;agrave; đăng k&amp;yacute; kinh doanh theo Luật Doanh nghiệp, Luật Doanh nghiệp Nh&amp;agrave; nước (nay l&amp;agrave; Luật Doanh nghiệp), Luật Hợp t&amp;aacute;c x&amp;atilde; v&amp;agrave; ph&amp;aacute;p luật kinh doanh chuy&amp;ecirc;n ng&amp;agrave;nh kh&amp;aacute;c;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;2. C&amp;aacute;c tổ chức kinh tế của tổ chức ch&amp;iacute;nh trị, tổ chức ch&amp;iacute;nh trị - x&amp;atilde; hội, tổ chức x&amp;atilde; hội, tổ chức x&amp;atilde; hội - nghề nghiệp, đơn vị vũ trang nh&amp;acirc;n d&amp;acirc;n, tổ chức sự nghiệp v&amp;agrave; c&amp;aacute;c tổ chức kh&amp;aacute;c;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;3. C&amp;aacute;c doanh nghiệp c&amp;oacute; vốn đầu tư nước ngo&amp;agrave;i v&amp;agrave; b&amp;ecirc;n nước ngo&amp;agrave;i tham gia hợp t&amp;aacute;c kinh doanh theo Luật đầu tư nước ngo&amp;agrave;i tại Việt Nam (nay l&amp;agrave; Luật đầu tư); c&amp;aacute;c tổ chức, c&amp;aacute; nh&amp;acirc;n nước ngo&amp;agrave;i hoạt động kinh doanh ở Việt Nam nhưng kh&amp;ocirc;ng th&amp;agrave;nh lập ph&amp;aacute;p nh&amp;acirc;n tại Việt Nam;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;4. C&amp;aacute; nh&amp;acirc;n, hộ gia đ&amp;igrave;nh, nh&amp;oacute;m người kinh doanh độc lập v&amp;agrave; c&amp;aacute;c đối tượng kh&amp;aacute;c c&amp;oacute; hoạt động sản xuất, kinh doanh, nhập khẩu;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;5. Tổ chức, c&amp;aacute; nh&amp;acirc;n sản xuất kinh doanh tại Việt Nam mua dịch vụ (kể cả trường hợp mua dịch vụ gắn với h&amp;agrave;ng h&amp;oacute;a) của tổ chức nước ngo&amp;agrave;i kh&amp;ocirc;ng c&amp;oacute; cơ sở thường tr&amp;uacute; tại Việt Nam, c&amp;aacute; nh&amp;acirc;n ở nước ngo&amp;agrave;i l&amp;agrave; đối tượng kh&amp;ocirc;ng cư tr&amp;uacute; tại Việt Nam th&amp;igrave; tổ chức, c&amp;aacute; nh&amp;acirc;n mua dịch vụ l&amp;agrave; người nộp thuế, trừ trường hợp kh&amp;ocirc;ng phải k&amp;ecirc; khai, t&amp;iacute;nh nộp thuế GTGT hướng dẫn tại khoản 2 Điều 5 Th&amp;ocirc;ng tư n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Quy định về cơ sở thường tr&amp;uacute; v&amp;agrave; đối tượng kh&amp;ocirc;ng cư tr&amp;uacute; thực hiện theo ph&amp;aacute;p luật về thuế thu nhập doanh nghiệp v&amp;agrave; ph&amp;aacute;p luật về thuế thu nhập c&amp;aacute; nh&amp;acirc;n.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;6. Chi nh&amp;aacute;nh của doanh nghiệp chế xuất được th&amp;agrave;nh lập để hoạt động mua b&amp;aacute;n h&amp;agrave;ng h&amp;oacute;a v&amp;agrave; c&amp;aacute;c hoạt động li&amp;ecirc;n quan trực tiếp đến mua b&amp;aacute;n h&amp;agrave;ng h&amp;oacute;a tại Việt Nam theo quy định của ph&amp;aacute;p luật về khu c&amp;ocirc;ng nghiệp, khu chế xuất v&amp;agrave; khu kinh tế.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 1: C&amp;ocirc;ng ty TNHH Sanko l&amp;agrave; doanh nghiệp chế xuất. Ngo&amp;agrave;i hoạt động sản xuất để xuất khẩu C&amp;ocirc;ng ty TNHH Sanko c&amp;ograve;n được cấp ph&amp;eacute;p thực hiện quyền nhập khẩu để b&amp;aacute;n ra hoặc để xuất khẩu, C&amp;ocirc;ng ty TNHH Sanko phải th&amp;agrave;nh lập chi nh&amp;aacute;nh để thực hiện hoạt động n&amp;agrave;y theo quy định của ph&amp;aacute;p luật th&amp;igrave; Chi nh&amp;aacute;nh hạch to&amp;aacute;n ri&amp;ecirc;ng v&amp;agrave; k&amp;ecirc; khai, nộp thuế GTGT ri&amp;ecirc;ng đối với hoạt động n&amp;agrave;y, kh&amp;ocirc;ng hạch to&amp;aacute;n chung v&amp;agrave;o hoạt động sản xuất để xuất khẩu.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Khi nhập khẩu h&amp;agrave;ng h&amp;oacute;a để thực hiện ph&amp;acirc;n phối (b&amp;aacute;n ra), Chi nh&amp;aacute;nh C&amp;ocirc;ng ty TNHH Sanko thực hiện k&amp;ecirc; khai, nộp thuế GTGT ở kh&amp;acirc;u nhập khẩu v&amp;agrave; khi b&amp;aacute;n ra (bao gồm cả xuất khẩu), C&amp;ocirc;ng ty TNHH Sanko sử dụng h&amp;oacute;a đơn, k&amp;ecirc; khai, nộp thuế GTGT theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Điều 4. Đối tượng kh&amp;ocirc;ng chịu thuế GTGT&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;1. Sản phẩm trồng trọt (bao gồm cả sản phẩm rừng trồng), chăn nu&amp;ocirc;i, thủy sản, hải sản nu&amp;ocirc;i trồng, đ&amp;aacute;nh bắt chưa chế biến th&amp;agrave;nh c&amp;aacute;c sản phẩm kh&amp;aacute;c hoặc chỉ qua sơ chế th&amp;ocirc;ng thường của tổ chức, c&amp;aacute; nh&amp;acirc;n tự sản xuất, đ&amp;aacute;nh bắt b&amp;aacute;n ra v&amp;agrave; ở kh&amp;acirc;u nhập khẩu.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;C&amp;aacute;c sản phẩm mới qua sơ chế th&amp;ocirc;ng thường l&amp;agrave; sản phẩm mới được l&amp;agrave;m sạch, phơi, sấy kh&amp;ocirc;, b&amp;oacute;c vỏ, xay, xay bỏ vỏ, x&amp;aacute;t bỏ vỏ, t&amp;aacute;ch hạt, t&amp;aacute;ch cọng, cắt, ướp muối, bảo quản lạnh (ướp lạnh, đ&amp;ocirc;ng lạnh), bảo quản bằng kh&amp;iacute; sunfuro, bảo quản theo phương thức cho h&amp;oacute;a chất để tr&amp;aacute;nh thối rữa, ng&amp;acirc;m trong dung dịch lưu huỳnh hoặc ng&amp;acirc;m trong dung dịch bảo quản kh&amp;aacute;c v&amp;agrave; c&amp;aacute;c h&amp;igrave;nh thức bảo quản th&amp;ocirc;ng thường kh&amp;aacute;c.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 2: C&amp;ocirc;ng ty A k&amp;yacute; hợp đồng nu&amp;ocirc;i heo với C&amp;ocirc;ng ty B theo h&amp;igrave;nh thức nhận tiền c&amp;ocirc;ng từ C&amp;ocirc;ng ty B hoặc b&amp;aacute;n lại sản phẩm cho C&amp;ocirc;ng ty B th&amp;igrave; tiền c&amp;ocirc;ng nu&amp;ocirc;i heo nhận từ C&amp;ocirc;ng ty B v&amp;agrave; sản phẩm heo b&amp;aacute;n cho C&amp;ocirc;ng ty B thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp C&amp;ocirc;ng ty B b&amp;aacute;n ra hoặc đưa v&amp;agrave;o chế biến để b&amp;aacute;n ra th&amp;igrave; sản phẩm b&amp;aacute;n ra thuộc đối tượng chịu thuế GTGT theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;2. Sản phẩm l&amp;agrave; giống vật nu&amp;ocirc;i, giống c&amp;acirc;y trồng, bao gồm trứng giống, con giống, c&amp;acirc;y giống, hạt giống, c&amp;agrave;nh giống, củ giống, tinh dịch, ph&amp;ocirc;i, vật liệu di truyền ở c&amp;aacute;c kh&amp;acirc;u nu&amp;ocirc;i trồng, nhập khẩu v&amp;agrave; kinh doanh thương mại. Sản phẩm giống vật nu&amp;ocirc;i, giống c&amp;acirc;y trồng thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT l&amp;agrave; sản phẩm do c&amp;aacute;c cơ sở nhập khẩu, kinh doanh thương mại c&amp;oacute; giấy đăng k&amp;yacute; kinh doanh giống vật nu&amp;ocirc;i, giống c&amp;acirc;y trồng do cơ quan quản l&amp;yacute; nh&amp;agrave; nước cấp. Đối với sản phẩm l&amp;agrave; giống vật nu&amp;ocirc;i, giống c&amp;acirc;y trồng thuộc loại Nh&amp;agrave; nước ban h&amp;agrave;nh ti&amp;ecirc;u chuẩn, chất lượng phải đ&amp;aacute;p ứng c&amp;aacute;c điều kiện do nh&amp;agrave; nước quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;3. Tưới, ti&amp;ecirc;u nước; c&amp;agrave;y, bừa đất; nạo v&amp;eacute;t k&amp;ecirc;nh, mương nội đồng phục vụ sản xuất n&amp;ocirc;ng nghiệp; dịch vụ thu hoạch sản phẩm n&amp;ocirc;ng nghiệp.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;4. Sản phẩm muối được sản xuất từ nước biển, muối mỏ tự nhi&amp;ecirc;n, muối tinh, muối i-ốt m&amp;agrave; th&amp;agrave;nh phần ch&amp;iacute;nh l&amp;agrave; Na-tri-clo-rua (NaCl).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;5. Nh&amp;agrave; ở thuộc sở hữu nh&amp;agrave; nước do Nh&amp;agrave; nước b&amp;aacute;n cho người đang thu&amp;ecirc;.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;6. Chuyển quyền sử dụng đất.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;7. Bảo hiểm nh&amp;acirc;n thọ, bảo hiểm sức khoẻ, bảo hiểm người học, c&amp;aacute;c dịch vụ bảo hiểm kh&amp;aacute;c li&amp;ecirc;n quan đến con người; bảo hiểm vật nu&amp;ocirc;i, bảo hiểm c&amp;acirc;y trồng, c&amp;aacute;c dịch vụ bảo hiểm n&amp;ocirc;ng nghiệp kh&amp;aacute;c; bảo hiểm t&amp;agrave;u, thuyền, trang thiết bị v&amp;agrave; c&amp;aacute;c dụng cụ cần thiết kh&amp;aacute;c phục vụ trực tiếp đ&amp;aacute;nh bắt thủy sản; t&amp;aacute;i bảo hiểm.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;8. C&amp;aacute;c dịch vụ t&amp;agrave;i ch&amp;iacute;nh, ng&amp;acirc;n h&amp;agrave;ng, kinh doanh chứng kho&amp;aacute;n sau đ&amp;acirc;y:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;a) Dịch vụ cấp t&amp;iacute;n dụng gồm c&amp;aacute;c h&amp;igrave;nh thức:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Cho vay;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Chiết khấu, t&amp;aacute;i chiết khấu c&amp;ocirc;ng cụ chuyển nhượng v&amp;agrave; c&amp;aacute;c giấy tờ c&amp;oacute; gi&amp;aacute; kh&amp;aacute;c;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Bảo l&amp;atilde;nh ng&amp;acirc;n h&amp;agrave;ng;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Cho thu&amp;ecirc; t&amp;agrave;i ch&amp;iacute;nh;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Ph&amp;aacute;t h&amp;agrave;nh thẻ t&amp;iacute;n dụng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp tổ chức t&amp;iacute;n dụng thu c&amp;aacute;c loại ph&amp;iacute; li&amp;ecirc;n quan đến ph&amp;aacute;t h&amp;agrave;nh thẻ t&amp;iacute;n dụng th&amp;igrave; c&amp;aacute;c khoản ph&amp;iacute; thu từ kh&amp;aacute;ch h&amp;agrave;ng thuộc quy tr&amp;igrave;nh dịch vụ cấp t&amp;iacute;n dụng (ph&amp;iacute; ph&amp;aacute;t h&amp;agrave;nh thẻ) theo quy chế cho vay của tổ chức t&amp;iacute;n dụng đối với kh&amp;aacute;ch h&amp;agrave;ng như ph&amp;iacute; trả nợ trước hạn, phạt chậm trả nợ, cơ cấu lại nợ, quản l&amp;yacute; khoản vay v&amp;agrave; c&amp;aacute;c khoản ph&amp;iacute; kh&amp;aacute;c thuộc quy tr&amp;igrave;nh cấp t&amp;iacute;n dụng thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;C&amp;aacute;c khoản ph&amp;iacute; giao dịch thẻ th&amp;ocirc;ng thường kh&amp;ocirc;ng thuộc quy tr&amp;igrave;nh cấp t&amp;iacute;n dụng như ph&amp;iacute; cấp lại m&amp;atilde; pin cho thẻ t&amp;iacute;n dụng, ph&amp;iacute; cung cấp bản sao h&amp;oacute;a đơn giao dịch, ph&amp;iacute; đ&amp;ograve;i bồi ho&amp;agrave;n khi sử dụng thẻ, ph&amp;iacute; th&amp;ocirc;ng b&amp;aacute;o mất cắp, thất lạc thẻ t&amp;iacute;n dụng, ph&amp;iacute; hủy thẻ t&amp;iacute;n dụng, ph&amp;iacute; chuyển đổi loại thẻ t&amp;iacute;n dụng v&amp;agrave; c&amp;aacute;c khoản ph&amp;iacute; kh&amp;aacute;c thuộc đối tượng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Bao thanh to&amp;aacute;n trong nước; bao thanh to&amp;aacute;n quốc tế đối với c&amp;aacute;c ng&amp;acirc;n h&amp;agrave;ng được ph&amp;eacute;p thực hiện thanh to&amp;aacute;n quốc tế;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- B&amp;aacute;n t&amp;agrave;i sản bảo đảm tiền vay do tổ chức t&amp;iacute;n dụng hoặc do cơ quan thi h&amp;agrave;nh &amp;aacute;n thực hiện theo quy định của ph&amp;aacute;p luật về xử l&amp;yacute; t&amp;agrave;i sản bảo đảm tiền vay.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp hết thời gian trả nợ, người c&amp;oacute; t&amp;agrave;i sản bảo đảm kh&amp;ocirc;ng c&amp;oacute; khả năng trả nợ v&amp;agrave; phải b&amp;agrave;n giao t&amp;agrave;i sản cho tổ chức t&amp;iacute;n dụng để tổ chức t&amp;iacute;n dụng xử l&amp;yacute; t&amp;agrave;i sản bảo đảm tiền vay theo quy định của ph&amp;aacute;p luật th&amp;igrave; c&amp;aacute;c b&amp;ecirc;n thực hiện thủ tục b&amp;agrave;n giao t&amp;agrave;i sản bảo đảm theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp c&amp;aacute;c b&amp;ecirc;n thỏa thuận người c&amp;oacute; t&amp;agrave;i sản bảo đảm tự b&amp;aacute;n t&amp;agrave;i sản bảo đảm để trả nợ, nếu người c&amp;oacute; t&amp;agrave;i sản bảo đảm l&amp;agrave; người nộp thuế GTGT v&amp;agrave; t&amp;agrave;i sản đem b&amp;aacute;n thuộc đối tượng chịu thuế GTGT th&amp;igrave; phải k&amp;ecirc; khai, nộp thuế GTGT theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp tổ chức t&amp;iacute;n dụng nhận t&amp;agrave;i sản bảo đảm để thay thế cho việc thực hiện nghĩa vụ trả nợ th&amp;igrave; tổ chức t&amp;iacute;n dụng thực hiện hạch to&amp;aacute;n tăng gi&amp;aacute; trị t&amp;agrave;i sản phục vụ sản xuất kinh doanh theo quy định. Khi tổ chức t&amp;iacute;n dụng b&amp;aacute;n t&amp;agrave;i sản phục vụ hoạt động kinh doanh nếu t&amp;agrave;i sản thuộc đối tượng chịu thuế GTGT th&amp;igrave; tổ chức t&amp;iacute;n dụng phải k&amp;ecirc; khai, nộp thuế GTGT theo quy định.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 3: Th&amp;aacute;ng 3/2014 C&amp;ocirc;ng ty TNHH A l&amp;agrave; cơ sở kinh doanh nộp thuế GTGT theo phương ph&amp;aacute;p khấu trừ thế chấp d&amp;acirc;y chuyền, m&amp;aacute;y m&amp;oacute;c thiết bị để vay vốn tại Ng&amp;acirc;n h&amp;agrave;ng B, thời gian vay l&amp;agrave; 1 năm (hạn trả nợ l&amp;agrave; ng&amp;agrave;y 31/3/2015). Đến ng&amp;agrave;y 31/3/2015, C&amp;ocirc;ng ty TNHH A kh&amp;ocirc;ng c&amp;oacute; khả năng trả nợ v&amp;agrave; phải b&amp;agrave;n giao t&amp;agrave;i sản cho Ng&amp;acirc;n h&amp;agrave;ng B th&amp;igrave; khi b&amp;agrave;n giao t&amp;agrave;i sản, C&amp;ocirc;ng ty TNHH A thực hiện thủ tục b&amp;agrave;n giao t&amp;agrave;i sản bảo đảm theo quy định của ph&amp;aacute;p luật về việc xử l&amp;yacute; t&amp;agrave;i sản bảo đảm. Ng&amp;acirc;n h&amp;agrave;ng B b&amp;aacute;n t&amp;agrave;i sản bảo đảm tiền vay để thu hồi nợ th&amp;igrave; t&amp;agrave;i sản b&amp;aacute;n ra thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- Dịch vụ cung cấp th&amp;ocirc;ng tin t&amp;iacute;n dụng do đơn vị, tổ chức thuộc Ng&amp;acirc;n h&amp;agrave;ng nh&amp;agrave; nước cung cấp cho c&amp;aacute;c tổ chức t&amp;iacute;n dụng để sử dụng trong hoạt động cấp t&amp;iacute;n dụng theo quy định của Luật Ng&amp;acirc;n h&amp;agrave;ng nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 4: Tổ chức X l&amp;agrave; đơn vị thuộc Ng&amp;acirc;n h&amp;agrave;ng Nh&amp;agrave; nước được Ng&amp;acirc;n h&amp;agrave;ng Nh&amp;agrave; nước cho ph&amp;eacute;p thực hiện dịch vụ cung cấp th&amp;ocirc;ng tin t&amp;iacute;n dụng. Năm 2014, tổ chức X k&amp;yacute; hợp đồng cung cấp th&amp;ocirc;ng tin t&amp;iacute;n dụng cho một số ng&amp;acirc;n h&amp;agrave;ng thương mại phục vụ hoạt động cấp t&amp;iacute;n dụng v&amp;agrave; phục vụ hoạt động kh&amp;aacute;c của ng&amp;acirc;n h&amp;agrave;ng thương mại th&amp;igrave; doanh thu từ dịch vụ cung cấp th&amp;ocirc;ng tin t&amp;iacute;n dụng phục vụ hoạt động cấp t&amp;iacute;n dụng thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT, doanh thu từ dịch vụ cung cấp th&amp;ocirc;ng tin t&amp;iacute;n dụng phục vụ hoạt động kh&amp;aacute;c của ng&amp;acirc;n h&amp;agrave;ng thương mại kh&amp;ocirc;ng theo quy định của Luật Ng&amp;acirc;n h&amp;agrave;ng Nh&amp;agrave; nước thuộc đối tượng chịu thuế GTGT với thuế suất 10%.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;- H&amp;igrave;nh thức cấp t&amp;iacute;n dụng kh&amp;aacute;c theo quy định của ph&amp;aacute;p luật.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;b) Hoạt động cho vay ri&amp;ecirc;ng lẻ, kh&amp;ocirc;ng phải hoạt động kinh doanh, cung ứng thường xuy&amp;ecirc;n của người nộp thuế kh&amp;ocirc;ng phải l&amp;agrave; tổ chức t&amp;iacute;n dụng.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 5: C&amp;ocirc;ng ty cổ phần VC c&amp;oacute; tiền nh&amp;agrave;n rỗi tạm thời chưa phục vụ hoạt động kinh doanh, C&amp;ocirc;ng ty cổ phần VC k&amp;yacute; hợp đồng cho C&amp;ocirc;ng ty T vay tiền trong thời hạn 6 th&amp;aacute;ng v&amp;agrave; được nhận khoản tiền l&amp;atilde;i th&amp;igrave; khoản tiền l&amp;atilde;i C&amp;ocirc;ng ty cổ phần VC nhận được thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;c) Kinh doanh chứng kho&amp;aacute;n bao gồm: M&amp;ocirc;i giới chứng kho&amp;aacute;n, tự doanh chứng kho&amp;aacute;n, bảo l&amp;atilde;nh ph&amp;aacute;t h&amp;agrave;nh chứng kho&amp;aacute;n, tư vấn đầu tư chứng kho&amp;aacute;n, lưu k&amp;yacute; chứng kho&amp;aacute;n, quản l&amp;yacute; quỹ đầu tư chứng kho&amp;aacute;n, quản l&amp;yacute; c&amp;ocirc;ng ty đầu tư chứng kho&amp;aacute;n, quản l&amp;yacute; danh mục đầu tư chứng kho&amp;aacute;n, dịch vụ tổ chức thị trường của sở giao dịch chứng kho&amp;aacute;n hoặc trung t&amp;acirc;m giao dịch chứng kho&amp;aacute;n, dịch vụ li&amp;ecirc;n quan đến chứng kho&amp;aacute;n đăng k&amp;yacute;, lưu k&amp;yacute; tại Trung t&amp;acirc;m Lưu k&amp;yacute; Chứng kho&amp;aacute;n Việt Nam, cho kh&amp;aacute;ch h&amp;agrave;ng vay tiền để thực hiện giao dịch k&amp;yacute; quỹ, ứng trước tiền b&amp;aacute;n chứng kho&amp;aacute;n v&amp;agrave; hoạt động kinh doanh chứng kho&amp;aacute;n kh&amp;aacute;c theo quy định của ph&amp;aacute;p luật về chứng kho&amp;aacute;n.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Hoạt động cung cấp th&amp;ocirc;ng tin, tổ chức b&amp;aacute;n đấu gi&amp;aacute; cổ phần của c&amp;aacute;c tổ chức ph&amp;aacute;t h&amp;agrave;nh, hỗ trợ kỹ thuật phục vụ giao dịch chứng kho&amp;aacute;n trực tuyến của Sở Giao dịch chứng kho&amp;aacute;n.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;d) Chuyển nhượng vốn bao gồm việc chuyển nhượng một phần hoặc to&amp;agrave;n bộ số vốn đ&amp;atilde; đầu tư v&amp;agrave;o tổ chức kinh tế kh&amp;aacute;c (kh&amp;ocirc;ng ph&amp;acirc;n biệt c&amp;oacute; th&amp;agrave;nh lập hay kh&amp;ocirc;ng th&amp;agrave;nh lập ph&amp;aacute;p nh&amp;acirc;n mới), chuyển nhượng chứng kho&amp;aacute;n, chuyển nhượng quyền g&amp;oacute;p vốn v&amp;agrave; c&amp;aacute;c h&amp;igrave;nh thức chuyển nhượng vốn kh&amp;aacute;c theo quy định của ph&amp;aacute;p luật, kể cả trường hợp b&amp;aacute;n doanh nghiệp cho doanh nghiệp kh&amp;aacute;c để sản xuất kinh doanh v&amp;agrave; doanh nghiệp mua kế thừa to&amp;agrave;n bộ quyền v&amp;agrave; nghĩa vụ của doanh nghiệp b&amp;aacute;n theo quy định của ph&amp;aacute;p luật.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 6: Th&amp;aacute;ng 4/2014, C&amp;ocirc;ng ty TNHH A g&amp;oacute;p vốn bằng m&amp;aacute;y m&amp;oacute;c, thiết bị để th&amp;agrave;nh lập C&amp;ocirc;ng ty cổ phần B, gi&amp;aacute; trị vốn g&amp;oacute;p của C&amp;ocirc;ng ty TNHH A được Hội đồng giao nhận vốn g&amp;oacute;p của c&amp;aacute;c b&amp;ecirc;n g&amp;oacute;p vốn định gi&amp;aacute; l&amp;agrave; 2,5 tỷ đồng, bằng 25% số vốn của C&amp;ocirc;ng ty cổ phần B. Th&amp;aacute;ng 11/2014, C&amp;ocirc;ng ty TNHH A b&amp;aacute;n phần vốn g&amp;oacute;p tại C&amp;ocirc;ng ty cổ phần B cho Quỹ Đầu tư ABB với gi&amp;aacute; 4 tỷ đồng th&amp;igrave; số tiền 4 tỷ đồng C&amp;ocirc;ng ty TNHH A thu được l&amp;agrave; doanh thu chuyển nhượng vốn thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;đ) B&amp;aacute;n nợ;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;e) Kinh doanh ngoại tệ;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;g) Dịch vụ t&amp;agrave;i ch&amp;iacute;nh ph&amp;aacute;i sinh bao gồm: h&amp;oacute;an đổi l&amp;atilde;i suất; hợp đồng kỳ hạn; hợp đồng tương lai; quyền chọn mua, b&amp;aacute;n ngoại tệ; c&amp;aacute;c dịch vụ t&amp;agrave;i ch&amp;iacute;nh ph&amp;aacute;i sinh kh&amp;aacute;c theo quy định của ph&amp;aacute;p luật;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;h) Bán t&amp;agrave;i sản bảo đảm của khoản nợ của tổ chức m&amp;agrave; Nh&amp;agrave; nước sở hữu 100% vốn điều lệ do Ch&amp;iacute;nh phủ th&amp;agrave;nh lập để xử l&amp;yacute; nợ xấu của c&amp;aacute;c tổ chức t&amp;iacute;n dụng Việt Nam.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;9. Dịch vụ y tế, dịch vụ th&amp;uacute; y, bao gồm dịch vụ kh&amp;aacute;m bệnh, chữa bệnh, ph&amp;ograve;ng bệnh cho người v&amp;agrave; vật nu&amp;ocirc;i, dịch vụ sinh đẻ c&amp;oacute; kế hoạch, dịch vụ điều dưỡng sức khoẻ, phục hồi chức năng cho người bệnh, vận chuyển người bệnh, dịch vụ cho thu&amp;ecirc; ph&amp;ograve;ng bệnh, giường bệnh của c&amp;aacute;c cơ sở y tế; x&amp;eacute;t nghiệm, chiếu, chụp, m&amp;aacute;u v&amp;agrave; chế phẩm m&amp;aacute;u d&amp;ugrave;ng cho người bệnh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp trong g&amp;oacute;i dịch vụ chữa bệnh (theo quy định của Bộ Y tế) bao gồm cả sử dụng thuốc chữa bệnh th&amp;igrave; khoản thu từ tiền thuốc chữa bệnh nằm trong g&amp;oacute;i dịch vụ chữa bệnh cũng thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;10. Dịch vụ bưu ch&amp;iacute;nh, viễn th&amp;ocirc;ng c&amp;ocirc;ng &amp;iacute;ch v&amp;agrave; Internet phổ cập theo chương tr&amp;igrave;nh của Ch&amp;iacute;nh phủ; Dịch vụ bưu ch&amp;iacute;nh, viễn th&amp;ocirc;ng từ nước ngo&amp;agrave;i v&amp;agrave;o Việt Nam (chiều đến).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;11. Dịch vụ duy tr&amp;igrave; vườn th&amp;uacute;, vườn hoa, c&amp;ocirc;ng vi&amp;ecirc;n, c&amp;acirc;y xanh đường phố, chiếu s&amp;aacute;ng c&amp;ocirc;ng cộng; dịch vụ tang lễ. C&amp;aacute;c dịch vụ n&amp;ecirc;u tại khoản n&amp;agrave;y kh&amp;ocirc;ng ph&amp;acirc;n biệt nguồn kinh ph&amp;iacute; chi trả. Cụ thể:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;a) Dịch vụ duy tr&amp;igrave; vườn th&amp;uacute;, vườn hoa, c&amp;ocirc;ng vi&amp;ecirc;n, c&amp;acirc;y xanh đường phố, bảo vệ rừng do Nh&amp;agrave; nước l&amp;agrave;m chủ rừng, bao gồm hoạt động quản l&amp;yacute;, trồng c&amp;acirc;y, chăm s&amp;oacute;c c&amp;acirc;y, bảo vệ chim, th&amp;uacute; ở c&amp;aacute;c c&amp;ocirc;ng vi&amp;ecirc;n, vườn th&amp;uacute;, khu vực c&amp;ocirc;ng cộng, rừng quốc gia, vườn quốc gia;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;b) Chiếu s&amp;aacute;ng c&amp;ocirc;ng cộng bao gồm chiếu s&amp;aacute;ng đường phố, ng&amp;otilde;, x&amp;oacute;m trong khu d&amp;acirc;n cư, vườn hoa, c&amp;ocirc;ng vi&amp;ecirc;n. Doanh thu kh&amp;ocirc;ng chịu thuế l&amp;agrave; doanh thu từ hoạt động chiếu s&amp;aacute;ng c&amp;ocirc;ng cộng;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;c) Dịch vụ tang lễ của c&amp;aacute;c cơ sở c&amp;oacute; chức năng kinh doanh dịch vụ tang lễ bao gồm c&amp;aacute;c hoạt động cho thu&amp;ecirc; nh&amp;agrave; tang lễ, xe &amp;ocirc; t&amp;ocirc; phục vụ tang lễ, mai t&amp;aacute;ng, hỏa t&amp;aacute;ng, cải t&amp;aacute;ng, di chuyển mộ, chăm s&amp;oacute;c mộ.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;12. Duy tu, sửa chữa, x&amp;acirc;y dựng bằng nguồn vốn đ&amp;oacute;ng g&amp;oacute;p của nh&amp;acirc;n d&amp;acirc;n (bao gồm cả vốn đ&amp;oacute;ng g&amp;oacute;p, t&amp;agrave;i trợ của tổ chức, c&amp;aacute; nh&amp;acirc;n), vốn viện trợ nh&amp;acirc;n đạo đối với c&amp;aacute;c c&amp;ocirc;ng tr&amp;igrave;nh văn h&amp;oacute;a, nghệ thuật, c&amp;ocirc;ng tr&amp;igrave;nh phục vụ c&amp;ocirc;ng cộng, cơ sở hạ tầng v&amp;agrave; nh&amp;agrave; ở cho đối tượng ch&amp;iacute;nh s&amp;aacute;ch x&amp;atilde; hội.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp c&amp;oacute; sử dụng nguồn vốn kh&amp;aacute;c ngo&amp;agrave;i vốn đ&amp;oacute;ng g&amp;oacute;p của nh&amp;acirc;n d&amp;acirc;n, vốn viện trợ nh&amp;acirc;n đạo m&amp;agrave; nguồn vốn kh&amp;aacute;c kh&amp;ocirc;ng vượt qu&amp;aacute; 50% tổng số nguồn vốn sử dụng cho c&amp;ocirc;ng tr&amp;igrave;nh th&amp;igrave; đối tượng kh&amp;ocirc;ng chịu thuế l&amp;agrave; to&amp;agrave;n bộ gi&amp;aacute; trị c&amp;ocirc;ng tr&amp;igrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp c&amp;oacute; sử dụng nguồn vốn kh&amp;aacute;c ngo&amp;agrave;i vốn đ&amp;oacute;ng g&amp;oacute;p của nh&amp;acirc;n d&amp;acirc;n, vốn viện trợ nh&amp;acirc;n đạo m&amp;agrave; nguồn vốn kh&amp;aacute;c vượt qu&amp;aacute; 50% tổng số nguồn vốn sử dụng cho c&amp;ocirc;ng tr&amp;igrave;nh th&amp;igrave; to&amp;agrave;n bộ gi&amp;aacute; trị c&amp;ocirc;ng tr&amp;igrave;nh thuộc đối tượng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Đối tượng ch&amp;iacute;nh s&amp;aacute;ch x&amp;atilde; hội bao gồm: người c&amp;oacute; c&amp;ocirc;ng theo quy định của ph&amp;aacute;p luật về người c&amp;oacute; c&amp;ocirc;ng; đối tượng bảo trợ x&amp;atilde; hội hưởng trợ cấp từ ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước; người thuộc hộ ngh&amp;egrave;o, cận ngh&amp;egrave;o v&amp;agrave; c&amp;aacute;c trường hợp kh&amp;aacute;c theo quy định của ph&amp;aacute;p luật.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;13. Dạy học, dạy nghề theo quy định của ph&amp;aacute;p luật bao gồm cả dạy ngoại ngữ, tin học; dạy m&amp;uacute;a, h&amp;aacute;t, hội họa, nhạc, kịch, xiếc, thể dục, thể thao; nu&amp;ocirc;i dạy trẻ v&amp;agrave; dạy c&amp;aacute;c nghề kh&amp;aacute;c nhằm đ&amp;agrave;o tạo, bồi dưỡng n&amp;acirc;ng cao tr&amp;igrave;nh độ văn h&amp;oacute;a, kiến thức chuy&amp;ecirc;n m&amp;ocirc;n nghề nghiệp.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Trường hợp c&amp;aacute;c cơ sở dạy học c&amp;aacute;c cấp từ mầm non đến trung học phổ th&amp;ocirc;ng c&amp;oacute; thu tiền ăn, tiền vận chuyển đưa đ&amp;oacute;n học sinh v&amp;agrave; c&amp;aacute;c khoản thu kh&amp;aacute;c dưới h&amp;igrave;nh thức thu hộ, chi hộ th&amp;igrave; tiền ăn, tiền vận chuyển đưa đ&amp;oacute;n học sinh v&amp;agrave; c&amp;aacute;c khoản thu hộ, chi hộ n&amp;agrave;y cũng thuộc đối tượng kh&amp;ocirc;ng chịu thuế.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Khoản thu về ở nội tr&amp;uacute; của học sinh, sinh vi&amp;ecirc;n, học vi&amp;ecirc;n; hoạt động đ&amp;agrave;o tạo (bao gồm cả việc tổ chức thi v&amp;agrave; cấp chứng chỉ trong quy tr&amp;igrave;nh đ&amp;agrave;o tạo) do cơ sở đ&amp;agrave;o tạo cung cấp thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT. Trường hợp cơ sở đ&amp;agrave;o tạo kh&amp;ocirc;ng trực tiếp tổ chức đ&amp;agrave;o tạo m&amp;agrave; chỉ tổ chức thi, cấp chứng chỉ trong quy tr&amp;igrave;nh đ&amp;agrave;o tạo th&amp;igrave; hoạt động tổ chức thi v&amp;agrave; cấp chứng chỉ cũng thuộc đối tượng kh&amp;ocirc;ng chịu thuế. Trường hợp cung cấp dịch vụ thi v&amp;agrave; cấp chứng chỉ kh&amp;ocirc;ng thuộc quy tr&amp;igrave;nh đ&amp;agrave;o tạo th&amp;igrave; thuộc đối tượng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;V&amp;iacute; dụ 7: Trung t&amp;acirc;m đ&amp;agrave;o tạo X được cấp c&amp;oacute; thẩm quyền giao nhiệm vụ đ&amp;agrave;o tạo để cấp chứng chỉ h&amp;agrave;nh nghề về đại l&amp;yacute; bảo hiểm. Trung t&amp;acirc;m đ&amp;agrave;o tạo X giao nhiệm vụ đ&amp;agrave;o tạo cho đơn vị Y thực hiện, Trung t&amp;acirc;m đ&amp;agrave;o tạo X tổ chức thi v&amp;agrave; cấp chứng chỉ h&amp;agrave;nh nghề đại l&amp;yacute; bảo hiểm th&amp;igrave; hoạt động tổ chức thi v&amp;agrave; cấp chứng chỉ của Trung t&amp;acirc;m đ&amp;agrave;o tạo X thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;14. Ph&amp;aacute;t s&amp;oacute;ng truyền thanh, truyền h&amp;igrave;nh bằng nguồn vốn ng&amp;acirc;n s&amp;aacute;ch nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;15. Xuất bản, nhập khẩu, ph&amp;aacute;t h&amp;agrave;nh b&amp;aacute;o, tạp ch&amp;iacute;, bản tin chuy&amp;ecirc;n ng&amp;agrave;nh, s&amp;aacute;ch ch&amp;iacute;nh trị, s&amp;aacute;ch gi&amp;aacute;o khoa, gi&amp;aacute;o tr&amp;igrave;nh, s&amp;aacute;ch văn bản ph&amp;aacute;p luật, s&amp;aacute;ch khoa học-kỹ thuật, s&amp;aacute;ch in bằng chữ d&amp;acirc;n tộc thiểu số v&amp;agrave; tranh, ảnh, &amp;aacute;p ph&amp;iacute;ch tuy&amp;ecirc;n truyền cổ động, kể cả dưới dạng băng hoặc đĩa ghi tiếng, ghi h&amp;igrave;nh, dữ liệu điện tử; tiền, in tiền.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;B&amp;aacute;o, tạp ch&amp;iacute;, bản tin chuy&amp;ecirc;n ng&amp;agrave;nh, bao gồm cả hoạt động truyền trang b&amp;aacute;o, tạp ch&amp;iacute;, bản tin chuy&amp;ecirc;n ng&amp;agrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;S&amp;aacute;ch ch&amp;iacute;nh trị l&amp;agrave; s&amp;aacute;ch tuy&amp;ecirc;n truyền đường lối ch&amp;iacute;nh trị của Đảng v&amp;agrave; Nh&amp;agrave; nước phục vụ nhiệm vụ ch&amp;iacute;nh trị theo chuy&amp;ecirc;n đề, chủ đề, phục vụ c&amp;aacute;c ng&amp;agrave;y kỷ niệm, ng&amp;agrave;y truyền thống của c&amp;aacute;c tổ chức, c&amp;aacute;c cấp, c&amp;aacute;c ng&amp;agrave;nh, địa phương; c&amp;aacute;c loại s&amp;aacute;ch thống k&amp;ecirc;, tuy&amp;ecirc;n truyền phong tr&amp;agrave;o người tốt việc tốt; s&amp;aacute;ch in c&amp;aacute;c b&amp;agrave;i ph&amp;aacute;t biểu, nghi&amp;ecirc;n cứu l&amp;yacute; luận của l&amp;atilde;nh đạo Đảng v&amp;agrave; Nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;S&amp;aacute;ch gi&amp;aacute;o khoa l&amp;agrave; s&amp;aacute;ch d&amp;ugrave;ng để giảng dạy v&amp;agrave; học tập trong tất cả c&amp;aacute;c cấp từ mầm non đến trung học phổ th&amp;ocirc;ng (bao gồm cả s&amp;aacute;ch tham khảo d&amp;ugrave;ng cho gi&amp;aacute;o vi&amp;ecirc;n v&amp;agrave; học sinh ph&amp;ugrave; hợp với nội dung chương tr&amp;igrave;nh gi&amp;aacute;o dục).&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Gi&amp;aacute;o tr&amp;igrave;nh l&amp;agrave; s&amp;aacute;ch d&amp;ugrave;ng để giảng dạy v&amp;agrave; học tập trong c&amp;aacute;c trường đại học, cao đẳng, trung học chuy&amp;ecirc;n nghiệp v&amp;agrave; dạy nghề.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;S&amp;aacute;ch văn bản ph&amp;aacute;p luật l&amp;agrave; s&amp;aacute;ch in c&amp;aacute;c văn bản quy phạm ph&amp;aacute;p luật của Nh&amp;agrave; nước.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;S&amp;aacute;ch khoa học-kỹ thuật l&amp;agrave; s&amp;aacute;ch d&amp;ugrave;ng để giới thiệu, hướng dẫn những kiến thức khoa học, kỹ thuật c&amp;oacute; quan hệ trực tiếp đến sản xuất v&amp;agrave; c&amp;aacute;c ng&amp;agrave;nh khoa học, kỹ thuật.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;S&amp;aacute;ch in bằng chữ d&amp;acirc;n tộc thiểu số bao gồm cả s&amp;aacute;ch in song ngữ chữ phổ th&amp;ocirc;ng v&amp;agrave; chữ d&amp;acirc;n tộc thiểu số.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p align=&quot;left&quot; style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Tranh, ảnh, &amp;aacute;p ph&amp;iacute;ch tuy&amp;ecirc;n truyền cổ động l&amp;agrave; tranh, ảnh, &amp;aacute;p ph&amp;iacute;ch, c&amp;aacute;c loại tờ rơi, tờ gấp phục vụ cho mục đ&amp;iacute;ch tuy&amp;ecirc;n truyền, cổ động, khẩu hiệu, ảnh l&amp;atilde;nh tụ, Đảng kỳ, Quốc kỳ, Đo&amp;agrave;n kỳ, Đội kỳ.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;16. Vận chuyển h&amp;agrave;nh kh&amp;aacute;ch c&amp;ocirc;ng cộng bằng xe bu&amp;yacute;t, xe điện l&amp;agrave; vận chuyển h&amp;agrave;nh kh&amp;aacute;ch c&amp;ocirc;ng cộng bằng xe bu&amp;yacute;t, xe điện theo c&amp;aacute;c tuyến trong nội tỉnh, trong đ&amp;ocirc; thị v&amp;agrave; c&amp;aacute;c tuyến l&amp;acirc;n cận ngoại tỉnh theo quy định của Bộ Giao th&amp;ocirc;ng vận tải.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;17. H&amp;agrave;ng h&amp;oacute;a thuộc loại trong nước chưa sản xuất được nhập khẩu trong c&amp;aacute;c trường hợp sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;a) M&amp;aacute;y m&amp;oacute;c, thiết bị, phụ t&amp;ugrave;ng, vật tư nhập khẩu để sử dụng trực tiếp cho hoạt động nghi&amp;ecirc;n cứu khoa học, ph&amp;aacute;t triển c&amp;ocirc;ng nghệ;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;b) M&amp;aacute;y m&amp;oacute;c, thiết bị, phụ t&amp;ugrave;ng thay thế, phương tiện vận tải chuy&amp;ecirc;n d&amp;ugrave;ng v&amp;agrave; vật tư cần nhập khẩu để tiến h&amp;agrave;nh hoạt động t&amp;igrave;m kiếm thăm d&amp;ograve;, ph&amp;aacute;t triển mỏ dầu, kh&amp;iacute; đốt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;c) T&amp;agrave;u bay (bao gồm cả động cơ t&amp;agrave;u bay), d&amp;agrave;n khoan, t&amp;agrave;u thủy thuộc loại trong nước chưa sản xuất được nhập khẩu để&lt;em&gt;&amp;nbsp;&lt;/em&gt;tạo t&amp;agrave;i sản cố định của doanh nghiệp hoặc thu&amp;ecirc; của nước ngo&amp;agrave;i để sử dụng cho sản xuất, kinh doanh, cho thu&amp;ecirc;, cho thu&amp;ecirc; lại.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Để x&amp;aacute;c định h&amp;agrave;ng h&amp;oacute;a thuộc đối tượng kh&amp;ocirc;ng chịu thuế GTGT ở kh&amp;acirc;u nhập khẩu quy định tại khoản&lt;strong&gt;&lt;em&gt;&amp;nbsp;&lt;/em&gt;&lt;/strong&gt;n&amp;agrave;y, người nhập khẩu phải xuất tr&amp;igrave;nh cho cơ quan hải quan c&amp;aacute;c hồ sơ theo hướng dẫn của Bộ T&amp;agrave;i ch&amp;iacute;nh về thủ tục hải quan; kiểm tra, gi&amp;aacute;m s&amp;aacute;t hải quan; thuế xuất khẩu, thuế nhập khẩu v&amp;agrave; quản l&amp;yacute; thuế đối với h&amp;agrave;ng h&amp;oacute;a xuất khẩu, nhập khẩu.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0px 0px 6pt; color: rgb(0, 0, 0); text-align: justify; line-height: 17.333332061767578px; font-family: verdana, arial, sans-serif; font-size: 13px;&quot;&gt;\r\n	&lt;span style=&quot;font-size: 10pt; font-family: Arial, sans-serif;&quot;&gt;Danh mục m&amp;aacute;y m&amp;oacute;c, thiết bị, phụ t&amp;ugrave;ng, vật tư thuộc loại trong nước đ&amp;atilde; sản xuất được để l&amp;agrave;m cơ sở ph&amp;acirc;n biệt với loại trong nước chưa sản xuất được cần nhập khẩu sử dụng trực tiếp cho hoạt động nghi&amp;ecirc;n cứu khoa học v&amp;agrave; ph&amp;aacute;t triển c&amp;ocirc;ng nghệ; Danh mục m&amp;aacute;y m&amp;oacute;c, thiết bị, phụ t&amp;ugrave;ng thay thế, phương tiện vận tải chuy&amp;ecirc;n d&amp;ugrave;ng v&amp;agrave; vật tư thuộc loại trong nước đ&amp;atilde; sản xuất được l&amp;agrave;m cơ sở ph&amp;acirc;n biệt với loại trong nước chưa sản xuất được cần nhập khẩu để tiến h&amp;agrave;nh hoạt động t&amp;igrave;m kiếm thăm d&amp;ograve;, ph&amp;aacute;t triển mỏ dầu, kh&amp;iacute; đốt; Danh mục t&amp;agrave;u bay, d&amp;agrave;n khoan, t&amp;agrave;u thủy thuộc loại trong nước đ&amp;atilde; sản xuất được l&amp;agrave;m cơ sở ph&amp;acirc;n biệt với loại trong nước chưa sản xuất được cần nhập khẩu để tạo t&amp;agrave;i sản cố định của doanh nghiệp hoặc thu&amp;ecirc; của nước ngo&amp;agrave;i để sử dụng cho sản xuất, kinh doanh, cho thu&amp;ecirc;, cho thu&amp;ecirc; lại do Bộ Kế hoạch v&amp;agrave; Đầu tư ban h&amp;agrave;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot', NULL, NULL, 1, '2014-03-28 16:05:37', 1, NULL, 1, 0);
